func Test_issue21(t *testing.T) {
	tt(t, func() {
		vm1 := New()
		_, err := vm1.Run(`
            abc = {}
            abc.ghi = "Nothing happens.";
            var jkl = 0;
            abc.def = function() {
                jkl += 1;
                return 1;
            }
        `)
		require.NoError(t, err)
		abc, err := vm1.Get("abc")
		require.NoError(t, err)

		vm2 := New()
		err = vm2.Set("cba", abc)
		require.NoError(t, err)
		_, err = vm2.Run(`
            var pqr = 0;
            cba.mno = function() {
                pqr -= 1;
                return 1;
            }
            cba.def();
            cba.def();
            cba.def();
        `)
		require.NoError(t, err)

		jkl, err := vm1.Get("jkl")
		require.NoError(t, err)
		is(jkl, 3)

		_, err = vm1.Run(`
            abc.mno();
            abc.mno();
            abc.mno();
        `)
		require.NoError(t, err)

		pqr, err := vm2.Get("pqr")
		require.NoError(t, err)
		is(pqr, -3)
	})
}

func Test_issue24(t *testing.T) {
	tt(t, func() {
		_, vm := test()

		{
			vm.Set("abc", []string{"abc", "def", "ghi"})
			value, err := vm.Get("abc")
			require.NoError(t, err)
			export, _ := value.Export()
			{
				value, valid := export.([]string)
				is(valid, true)

				is(value[0], "abc")
				is(value[2], "ghi")
			}
		}

		{
			vm.Set("abc", [...]string{"abc", "def", "ghi"})
			value, err := vm.Get("abc")
			require.NoError(t, err)
			export, _ := value.Export()
			{
				value, valid := export.([3]string)
				is(valid, true)

				is(value[0], "abc")
				is(value[2], "ghi")
			}
		}

		{
			vm.Set("abc", &[...]string{"abc", "def", "ghi"})
			value, err := vm.Get("abc")
			require.NoError(t, err)
			export, _ := value.Export()
			{
				value, valid := export.(*[3]string)
				is(valid, true)

				is(value[0], "abc")
				is(value[2], "ghi")
			}
		}

		{
			vm.Set("abc", map[int]string{0: "abc", 1: "def", 2: "ghi"})
			value, err := vm.Get("abc")
			require.NoError(t, err)
			export, _ := value.Export()
			{
				value, valid := export.(map[int]string)
				is(valid, true)

				is(value[0], "abc")
				is(value[2], "ghi")
			}
		}

		{
			vm.Set("abc", abcStruct{Abc: true, Ghi: "Nothing happens."})
			value, err := vm.Get("abc")
			require.NoError(t, err)
			export, _ := value.Export()
			{
				value, valid := export.(abcStruct)
				is(valid, true)

				is(value.Abc, true)
				is(value.Ghi, "Nothing happens.")
			}
		}

		{
			vm.Set("abc", &abcStruct{Abc: true, Ghi: "Nothing happens."})
			value, err := vm.Get("abc")
			require.NoError(t, err)
			export, _ := value.Export()
			{
				value, valid := export.(*abcStruct)
				is(valid, true)

				is(value.Abc, true)
				is(value.Ghi, "Nothing happens.")
			}
		}
	})
}

