func TestOttoCopy(t *testing.T) {
	tt(t, func() {
		vm0 := New()
		_, err := vm0.Run(`
            var abc = function() {
                return "Xyzzy";
            };

            function def() {
                return abc() + (0 + {});
            }
        `)
		require.NoError(t, err)

		value, err := vm0.Run(`
            def();
        `)
		require.NoError(t, err)
		is(value, "Xyzzy0[object Object]")

		vm1 := vm0.Copy()
		value, err = vm1.Run(`
            def();
        `)
		require.NoError(t, err)
		is(value, "Xyzzy0[object Object]")

		_, err = vm1.Run(`
            abc = function() {
                return 3.14159;
            };
        `)
		require.NoError(t, err)
		value, err = vm1.Run(`
            def();
        `)
		require.NoError(t, err)
		is(value, "3.141590[object Object]")

		value, err = vm0.Run(`
            def();
        `)
		require.NoError(t, err)
		is(value, "Xyzzy0[object Object]")

		{
			vm01 := New()
			_, err2 := vm01.Run(`
                var global = (function () {return this;}())
                var abc = 0;
                var vm = "vm0";

                var def = (function(){
                    var jkl = 0;
                    var abc = function() {
                        global.abc += 1;
                        jkl += 1;
                        return 1;
                    };

                    return function() {
                        return [ vm, global.abc, jkl, abc() ];
                    };
                })();
            `)
			require.NoError(t, err2)

			value2, err2 := vm01.Run(`
                def();
            `)
			require.NoError(t, err2)
			is(value2, "vm0,0,0,1")

			vm11 := vm01.Copy()
			err2 = vm11.Set("vm", "vm1")
			require.NoError(t, err2)
			value2, err2 = vm11.Run(`
                def();
            `)
			require.NoError(t, err2)
			is(value2, "vm1,1,1,1")

			value2, err2 = vm01.Run(`
                def();
            `)
			require.NoError(t, err2)
			is(value2, "vm0,1,1,1")

			value2, err2 = vm11.Run(`
                def();
            `)
			require.NoError(t, err2)
			is(value2, "vm1,2,2,1")
		}
	})
}

func TestOttoCall_clone(t *testing.T) {
