		}
		return true
	case string:
		return len(value) != 0
	case []uint16:
		return len(utf16.Decode(value)) != 0
	}
	if v.IsObject() {
		return true
	}
	panic(fmt.Sprintf("unexpected boolean type %T", v.value))
}

