			value, _ = vm.ToValue(&tmp2)
			is(value, 1)

			tmp0 = nil

			value, _ = vm.ToValue(&tmp2)
			is(value, "undefined")
		}

		{
			m := map[int64]string{0: "foo", 1: "bar"}
			val, err := vm.ToValue(m)
			is(err, nil)
			v0, err := val.Object().Get("0")
			is(err, nil)
			is(v0, m[0])
