			is(err, nil)
			is(v1, m[1])
			missing, err := val.Object().Get("2")
			is(err, nil)
			is(missing, UndefinedValue())
			invalid, err := val.Object().Get("xxx")
			is(err, nil)
			is(invalid, UndefinedValue())
		}

