package reviewdog

import (
	"bytes"
	"context"
	"strings"
	"testing"

	"github.com/reviewdog/reviewdog/filter"
	"github.com/reviewdog/reviewdog/proto/rdf"
)

func TestMultiCommentService_Post(t *testing.T) {
	buf1 := new(bytes.Buffer)
	buf2 := new(bytes.Buffer)
	w := MultiCommentService(NewRawCommentWriter(buf1), NewRawCommentWriter(buf2))

	const want = "line1\nline2"

	c := &Comment{Result: &filter.FilteredDiagnostic{Diagnostic: &rdf.Diagnostic{OriginalOutput: want}}}
	if err := w.Post(context.Background(), c); err != nil {
		t.Fatal(err)
	}

	if got := strings.Trim(buf1.String(), "\n"); got != want {
		t.Errorf("writer 1: got %v, want %v", got, want)
	}

	if got := strings.Trim(buf2.String(), "\n"); got != want {
		t.Errorf("writer 2: got %v, want %v", got, want)
	}

	if err := w.(BulkCommentService).Flush(context.Background()); err != nil {
		t.Errorf("MultiCommentService implements BulkCommentService and should not return error when any services implements it: %v", err)
	}
}

type fakeBulkCommentService struct {
	BulkCommentService
	calledFlush bool
}

func (f *fakeBulkCommentService) Flush(_ context.Context) error {
	f.calledFlush = true
	return nil
}

func TestMultiCommentService_Flush(t *testing.T) {
	f1 := &fakeBulkCommentService{}
	f2 := &fakeBulkCommentService{}
	w := MultiCommentService(f1, f2)
	if err := w.(BulkCommentService).Flush(context.Background()); err != nil {
		t.Fatal(err)
	}
	if !f1.calledFlush || !f2.calledFlush {
		t.Error("MultiCommentService_Flush should run Flush() for every services")
	}
}

package reviewdog

import (
	"context"
	"os/exec"
	"sync"
)

var _ DiffService = &DiffString{}

type DiffString struct {
	b     []byte
	strip int
}

func NewDiffString(diff string, strip int) DiffService {
	return &DiffString{b: []byte(diff), strip: strip}
}

func (d *DiffString) Diff(_ context.Context) ([]byte, error) {
	return d.b, nil
}

func (d *DiffString) Strip() int {
	return d.strip
}

var _ DiffService = &DiffCmd{}

type DiffCmd struct {
	cmd   *exec.Cmd
	strip int
	out   []byte
	done  bool
	mu    sync.RWMutex
}

func NewDiffCmd(cmd *exec.Cmd, strip int) *DiffCmd {
	return &DiffCmd{cmd: cmd, strip: strip}
}

// Diff returns diff. It caches the result and can be used more than once.
func (d *DiffCmd) Diff(_ context.Context) ([]byte, error) {
	d.mu.Lock()
	defer d.mu.Unlock()
	if d.done {
		return d.out, nil
	}
	out, err := d.cmd.Output()
	// Exit status of `git diff` is 1 if diff exists, so ignore the error if diff
	// presents.
	if err != nil && len(out) == 0 {
		return nil, err
	}
	d.out = out
	d.done = true
	return d.out, nil
}

func (d *DiffCmd) Strip() int {
	return d.strip
}

// EmptyDiff service return empty diff.
type EmptyDiff struct{}

func (*EmptyDiff) Diff(context.Context) ([]byte, error) {
	return []byte{}, nil
}

func (*EmptyDiff) Strip() int { return 0 }

package reviewdog

import (
	"context"
	"os"
	"os/exec"
	"strings"
	"sync"
	"testing"
)

func TestDiffString(t *testing.T) {
	difftext := `diff --git a/golint.old.go b/golint.new.go
index 34cacb9..a727dd3 100644
--- a/golint.old.go
+++ b/golint.new.go
@@ -2,6 +2,12 @@ package test

 var V int

+var NewError1 int
+
 // invalid func comment
 func F() {
 }
+
+// invalid func comment2
+func F2() {
+}
`
	d := NewDiffString(difftext, 1)
	b, err := d.Diff(context.Background())
	if err != nil {
		t.Error(err)
	}
	got := string(b)
	if got != difftext {
		t.Errorf("got:\n%v\nwant:\n%v", got, difftext)
	}
}

func TestDiffCmd(t *testing.T) {
	wantb, err := os.ReadFile("./diff/testdata/golint.diff")
	if err != nil {
		t.Fatal(err)
	}
	want := strings.SplitN(string(wantb), "\n", 5)[4] // strip extended header
	cmd := exec.Command("git", "diff", "--no-index", "./diff/testdata/golint.old.go", "./diff/testdata/golint.new.go")
	d := NewDiffCmd(cmd, 1)
	// ensure it supports multiple use
	var wg sync.WaitGroup
	for i := 0; i < 3; i++ {
		wg.Add(1)
		go func() {
			defer wg.Done()
			b, err := d.Diff(context.Background())
			if err != nil {
				t.Error(string(b), err)
			}
			got := strings.SplitN(string(b), "\n", 5)[4]
			if got != want {
				t.Errorf("got:\n%v\nwant:\n%v", got, want)
			}
		}()
	}
	wg.Wait()
}

package reviewdog

import (
	"fmt"

	"github.com/reviewdog/reviewdog/proto/rdf"
)

// FailLevel represents enumeration of available filter modes
type FailLevel int

const (
	// FailLevelDefault represents default mode, which means users doesn't specify
	// fail-level. Basically, it's same as FailLevelNone.
	FailLevelDefault FailLevel = iota
	// FailLevelNone - Do not fail.
	FailLevelNone
	// FailLevelAny - Fail with any severity.
	FailLevelAny
	// FailLevelInfo - Fail with info or above severity.
	FailLevelInfo
	// FailLevelWarning - Fail with warning or above severity.
	FailLevelWarning
	// FailLevelError - Fail with error or above severity.
	FailLevelError
)

// String implements the flag.Value interface
func (failLevel *FailLevel) String() string {
	names := [...]string{
		"default",
		"none",
		"any",
		"info",
		"warning",
		"error",
	}
	if *failLevel < FailLevelDefault || *failLevel > FailLevelError {
		return "Unknown failLevel"
	}

	return names[*failLevel]
}

// Set implements the flag.Value interface
func (failLevel *FailLevel) Set(value string) error {
	switch value {
	case "default", "":
		*failLevel = FailLevelDefault
	case "none":
		*failLevel = FailLevelNone
	case "any":
		*failLevel = FailLevelAny
	case "info":
		*failLevel = FailLevelInfo
	case "warning":
		*failLevel = FailLevelWarning
	case "error":
		*failLevel = FailLevelError
	default:
		return fmt.Errorf("invalid failLevel name: %s", value)
	}
	return nil
}

// ShouldFail returns true if reviewdog should exit with 1 with given rdf.Severity.
func (failLevel FailLevel) ShouldFail(severity rdf.Severity) bool {
	if failLevel == FailLevelDefault || failLevel == FailLevelNone {
		return false
	}
	minSeverity := failLevel.minSeverity()
	return minSeverity == rdf.Severity_UNKNOWN_SEVERITY || severity <= minSeverity
}

func (failLevel FailLevel) minSeverity() rdf.Severity {
	switch failLevel {
	case FailLevelDefault, FailLevelNone, FailLevelAny:
		return rdf.Severity_UNKNOWN_SEVERITY
	case FailLevelInfo:
		return rdf.Severity_INFO
	case FailLevelWarning:
		return rdf.Severity_WARNING
	case FailLevelError:
		return rdf.Severity_ERROR
	default:
		return rdf.Severity_UNKNOWN_SEVERITY
	}
}

package reviewdog

import (
	"testing"

	"github.com/reviewdog/reviewdog/proto/rdf"
)

func TestShouldFail(t *testing.T) {
	tests := []struct {
		failLevel FailLevel
		severity  rdf.Severity
		want      bool
	}{
		{
			failLevel: FailLevelDefault, severity: rdf.Severity_ERROR, want: false,
		},
		{
			failLevel: FailLevelDefault, severity: rdf.Severity_WARNING, want: false,
		},
		{
			failLevel: FailLevelDefault, severity: rdf.Severity_INFO, want: false,
		},
		{
			failLevel: FailLevelDefault, severity: rdf.Severity_UNKNOWN_SEVERITY, want: false,
		},
		{
			failLevel: FailLevelNone, severity: rdf.Severity_ERROR, want: false,
		},
		{
			failLevel: FailLevelError, severity: rdf.Severity_ERROR, want: true,
		},
		{
			failLevel: FailLevelError, severity: rdf.Severity_WARNING, want: false,
		},
		{
			failLevel: FailLevelError, severity: rdf.Severity_INFO, want: false,
		},
		{
			failLevel: FailLevelError, severity: rdf.Severity_UNKNOWN_SEVERITY, want: true,
		},
		{
			failLevel: FailLevelWarning, severity: rdf.Severity_ERROR, want: true,
		},
		{
			failLevel: FailLevelWarning, severity: rdf.Severity_WARNING, want: true,
		},
		{
			failLevel: FailLevelWarning, severity: rdf.Severity_INFO, want: false,
		},
		{
			failLevel: FailLevelWarning, severity: rdf.Severity_UNKNOWN_SEVERITY, want: true,
		},
		{
			failLevel: FailLevelInfo, severity: rdf.Severity_ERROR, want: true,
		},
		{
			failLevel: FailLevelInfo, severity: rdf.Severity_WARNING, want: true,
		},
		{
			failLevel: FailLevelInfo, severity: rdf.Severity_INFO, want: true,
		},
		{
			failLevel: FailLevelInfo, severity: rdf.Severity_UNKNOWN_SEVERITY, want: true,
		},
	}
	for _, tt := range tests {
		if got := tt.failLevel.ShouldFail(tt.severity); got != tt.want {
			t.Errorf("FailLevel(%s).ShouldFail(%s) = %v, want %v", tt.failLevel.String(),
				tt.severity, got, tt.want)
		}
	}
}

