server {
    listen 8080;
    root /usr/share/nginx/html/;
    index index.html;
    error_page 403 404 /404.html;
    location = /404.html {
        internal;
    }
    # redirects issued by nginx will be relative
    absolute_redirect off;
}

package main

import (
	"os"

	"github.com/stakater/Reloader/internal/pkg/app"
)

func main() {
	if err := app.Run(); err != nil {
		os.Exit(1)
	}
	os.Exit(0)
}

{
  "$schema": "https://docs.renovatebot.com/renovate-schema.json",
  "extends": [
    "config:recommended"
  ],
  "labels": [
    "dependencies"
  ],
  "rebaseWhen": "never",
  "vulnerabilityAlerts": {
    "enabled": true,
    "labels": ["security"]
  },

  "customManagers": [
    {
        "customType": "regex",
        "fileMatch": [
            ".vale.ini"
        ],
        "matchStrings": [
            "https:\/\/github\\.com\/(?<depName>.*)\/releases\/download\/(?<currentValue>.*)\/.*\\.zip"
        ],
        "datasourceTemplate": "github-releases"
    },
    {
      "customType": "regex",
      "description": "Update Helm Chart values file",
      "fileMatch": [
          "values\\.yaml$"
      ],
      "matchStrings": [
          "image:\\s*name: (?<depName>[a-zA-Z0-9\\.\\/]*)\\s*tag: (?<currentValue>[a-zA-Z0-9\\.\\/]*)"
      ],
      "datasourceTemplate": "docker"
    }
  ]
}
