	FileStorageModeFormatAuto  FileStorageMode = 0
	FileStorageModeFormatXml   FileStorageMode = (1 << 3)
	FileStorageModeFormatYaml  FileStorageMode = (2 << 3)
	FileStorageModeFormatJson  FileStorageMode = (3 << 3)
