	statement := createTestStatement()

	quotedCols := statement.col2NewColsWithQuote(cols...)
	assert.EqualValues(t, []string{statement.Engine.Quote("f1"), statement.Engine.Quote("f2"), statement.Engine.Quote("t3.f3")}, quotedCols)
}

