{
  "packages": {
    ".": {
      "changelog-path": "CHANGELOG.md",
      "release-type": "go",
      "prerelease": false,
      "bump-minor-pre-major": true,
      "bump-patch-for-minor-pre-major": true,
      "draft": false,
      "extra-files": [
        "README.md",
        "deploy/manifest.yaml",
        "chart/Chart.yaml",
        "chart/values.yaml",
        "container/manifests/deployment.yaml"
      ],
      "changelog-sections": [
        {
          "type": "feat",
          "section": "Features"
        },
        {
          "type": "fix",
          "section": "Bug Fixes"
        },
        {
          "type": "chore",
          "section": "Other"
        },
        {
          "type": "docs",
          "section": "Docs"
        },
        {
          "type": "perf",
          "section": "Performance"
        },
        {
          "type": "build",
          "hidden": true,
          "section": "Build"
        },
        {
          "type": "deps",
          "section": "Dependency Updates"
        },
        {
          "type": "ci",
          "hidden": true,
          "section": "CI"
        },
        {
          "type": "refactor",
          "section": "Refactoring"
        },
        {
          "type": "revert",
          "hidden": true,
          "section": "Reverts"
        },
        {
          "type": "style",
          "hidden": true,
          "section": "Styling"
        },
        {
          "type": "test",
          "hidden": true,
          "section": "Tests"
        }
      ]
    }
  },
  "$schema": "https://raw.githubusercontent.com/googleapis/release-please/main/schemas/config.json"
}

{
  "$schema": "https://docs.renovatebot.com/renovate-schema.json",
  "extends": [
    "config:base",
    "helpers:pinGitHubActionDigests",
    ":gitSignOff",
    "group:allNonMajor"
  ],
  "addLabels": ["dependencies"],
  "postUpdateOptions": [
    "gomodTidy",
    "gomodMassage"
  ],
  "automerge": true,
  "automergeType": "pr",
  "schedule": [
    "at any time"
  ],
  "platformAutomerge": true,
  "packageRules": [
    {
      "matchPackageNames": ["azure-sdk-for-go"],
      "enabled": true,
      "groupName": "azure-group"
    },
    {
      "matchPackageNames": ["prometheus"],
      "enabled": true,
      "groupName": "prometheus-group"
    },
    {
      "matchPackageNames": ["k8s.io", "sigs.k8s.io"],
      "enabled": true,
      "groupName": "kubernetes-group"
    },
    {
      "matchPackageNames": ["golang"],
      "enabled": true,
      "groupName": "golang-group"
    },
    {
      "matchUpdateTypes": ["minor", "patch"],
      "matchCurrentVersion": "!/^0/",
      "automerge": true
    },
    {
      "matchManagers": ["gomod"],
      "addLabels": ["go"]
    },
    {
      "matchManagers": ["github-actions"],
      "addLabels": ["github_actions"]
    },
    {
      "matchManagers": ["dockerfile"],
      "addLabels": ["docker"]
    }
  ],
  "regexManagers": [
    {
      "fileMatch": [
        "(^|\\/)Makefile$",
        "(^|\\/)Dockerfile",
        "(^|\\/).*\\.ya?ml$",
        "(^|\\/).*\\.toml$",
        "(^|\\/).*\\.sh$"
      ],
      "matchStrings": [
        "# renovate: datasource=(?<datasource>.+?) depName=(?<depName>.+?)\\s.*?_VERSION ?(\\??=|\\: ?) ?\\\"?(?<currentValue>.+?)?\\\"?\\s"
      ]
    }
  ]
}
