package govalidator

// Iterator is the function that accepts element of slice/array and its index
type Iterator func(interface{}, int)

// ResultIterator is the function that accepts element of slice/array and its index and returns any result
type ResultIterator func(interface{}, int) interface{}

// ConditionIterator is the function that accepts element of slice/array and its index and returns boolean
type ConditionIterator func(interface{}, int) bool

// ReduceIterator is the function that accepts two element of slice/array and returns result of merging those values
type ReduceIterator func(interface{}, interface{}) interface{}

// Some validates that any item of array corresponds to ConditionIterator. Returns boolean.
func Some(array []interface{}, iterator ConditionIterator) bool {
	res := false
	for index, data := range array {
		res = res || iterator(data, index)
	}
	return res
}

// Every validates that every item of array corresponds to ConditionIterator. Returns boolean.
func Every(array []interface{}, iterator ConditionIterator) bool {
	res := true
	for index, data := range array {
		res = res && iterator(data, index)
	}
	return res
}

// Reduce boils down a list of values into a single value by ReduceIterator
func Reduce(array []interface{}, iterator ReduceIterator, initialValue interface{}) interface{} {
	for _, data := range array {
		initialValue = iterator(initialValue, data)
	}
	return initialValue
}

// Each iterates over the slice and apply Iterator to every item
func Each(array []interface{}, iterator Iterator) {
	for index, data := range array {
		iterator(data, index)
	}
}

// Map iterates over the slice and apply ResultIterator to every item. Returns new slice as a result.
func Map(array []interface{}, iterator ResultIterator) []interface{} {
	var result = make([]interface{}, len(array))
	for index, data := range array {
		result[index] = iterator(data, index)
	}
	return result
}

// Find iterates over the slice and apply ConditionIterator to every item. Returns first item that meet ConditionIterator or nil otherwise.
func Find(array []interface{}, iterator ConditionIterator) interface{} {
	for index, data := range array {
		if iterator(data, index) {
			return data
		}
	}
	return nil
}

// Filter iterates over the slice and apply ConditionIterator to every item. Returns new slice.
func Filter(array []interface{}, iterator ConditionIterator) []interface{} {
	var result = make([]interface{}, 0)
	for index, data := range array {
		if iterator(data, index) {
			result = append(result, data)
		}
	}
	return result
}

// Count iterates over the slice and apply ConditionIterator to every item. Returns count of items that meets ConditionIterator.
func Count(array []interface{}, iterator ConditionIterator) int {
	count := 0
	for index, data := range array {
		if iterator(data, index) {
			count = count + 1
		}
	}
	return count
}

package govalidator

// Benchmark testing is produced with randomly filled array of 1 million elements

import (
	"math/rand"
	"testing"
)

func randomInt(min, max int) int {
	return min + rand.Intn(max-min)
}

func randomArray(n int) (res []interface{}) {
	res = make([]interface{}, n)

	for i := 0; i < n; i++ {
		res[i] = randomInt(-1000, 1000)
	}

	return
}

func BenchmarkEach(b *testing.B) {
	data := randomArray(1000000)
	b.ResetTimer()
	for n := 0; n < b.N; n++ {
		acc := 0
		var fn Iterator = func(value interface{}, index int) {
			acc = acc + value.(int)
		}
		Each(data, fn)
	}
}

func BenchmarkMap(b *testing.B) {
	data := randomArray(1000000)
	b.ResetTimer()
	for n := 0; n < b.N; n++ {
		var fn ResultIterator = func(value interface{}, index int) interface{} {
			return value.(int) * 3
		}
		_ = Map(data, fn)
	}
}

func BenchmarkFind(b *testing.B) {
	data := randomArray(1000000)
	b.ResetTimer()
	for n := 0; n < b.N; n++ {
		findElement := 96
		var fn1 ConditionIterator = func(value interface{}, index int) bool {
			return value.(int) == findElement
		}
		_ = Find(data, fn1)
	}
}

func BenchmarkFilter(b *testing.B) {
	data := randomArray(1000000)
	b.ResetTimer()
	for n := 0; n < b.N; n++ {
		var fn ConditionIterator = func(value interface{}, index int) bool {
			return value.(int)%2 == 0
		}
		_ = Filter(data, fn)
	}
}

func BenchmarkCount(b *testing.B) {
	data := randomArray(1000000)
	b.ResetTimer()
	for n := 0; n < b.N; n++ {
		var fn ConditionIterator = func(value interface{}, index int) bool {
			return value.(int)%2 == 0
		}
		_ = Count(data, fn)
	}
}

