package main

import (
	"os/signal"
	"syscall"
)

func init() {
	// workaround for https://github.com/golang/go/issues/37942
	signal.Ignore(syscall.SIGURG)
}

package main

import (
	"strings"

	"github.com/blang/semver/v4"
)

func getVersion() semver.Version {
	sv, err := semver.Parse(strings.TrimPrefix(version, "v"))
	if err == nil {
		return sv
	}

	return semver.Version{
		Major: 1,
		Minor: 15,
		Patch: 15,
		Pre: []semver.PRVersion{
			{VersionStr: "git"},
		},
		Build: []string{"HEAD"},
	}
}
