package xlsx

import (
	"bytes"
	"encoding/base64"
	"fmt"
	"math"
	"strconv"
	"strings"
	"time"
)

const (
	maxNonScientificNumber = 1e11
	minNonScientificNumber = 1e-9
)

// CellType is an int type for storing metadata about the data type in the cell.
type CellType int

// These are the cell types from the ST_CellType spec
const (
	CellTypeString CellType = iota
	// CellTypeStringFormula is a specific format for formulas that return string values. Formulas that return numbers
	// and booleans are stored as those types.
	CellTypeStringFormula
	CellTypeNumeric
	CellTypeBool
	// CellTypeInline is not respected on save, all inline string cells will be saved as SharedStrings
	// when saving to an XLSX file. This the same behavior as that found in Excel.
	CellTypeInline
	CellTypeError
	// d (Date): Cell contains a date in the ISO 8601 format.
	// That is the only mention of this format in the XLSX spec.
	// Date seems to be unused by the current version of Excel, it stores dates as Numeric cells with a date format string.
	// For now these cells will have their value output directly. It is unclear if the value is supposed to be parsed
	// into a number and then formatted using the formatting or not.
	CellTypeDate
)

func (ct CellType) Ptr() *CellType {
	return &ct
}

func (ct *CellType) fallbackTo(cellData string, fallback CellType) CellType {
	if ct != nil {
		switch *ct {
		case CellTypeNumeric:
			if _, err := strconv.ParseFloat(cellData, 64); err == nil {
				return *ct
			}
		default:
		}
	}
	return fallback
}

// Cell is a high level structure intended to provide user access to
// the contents of Cell within an xlsx.Row.
type Cell struct {
	Row            *Row
	Value          string
	RichText       []RichTextRun
	formula        string
	style          *Style
	NumFmt         string
	parsedNumFmt   *parsedNumberFormat
	date1904       bool
	Hidden         bool
	HMerge         int
	VMerge         int
	cellType       CellType
	DataValidation *xlsxDataValidation
	Hyperlink      Hyperlink
	num            int
	modified       bool
	origValue      string
	origNumFmt     string
	origRichText   []RichTextRun
}

// Return a representation of the Cell as a slice of bytes
func (c Cell) MarshalBinary() ([]byte, error) {

	// bs uses base64 to avoid directly encoding newlines and other bad values
	bs := func(s string) string {
		return base64.StdEncoding.EncodeToString([]byte(s))
	}

	var b bytes.Buffer
	// We can omit the Row pointer, because we know this information when we unmarshal.
	// We can omit the parsedNumFmt because this is created on demand anyway.
	// We can omit the DataValidation because we store this separately with a derived key
	// We can omit the Style because we store this separately with a derived key
	//
	// String values all contain fixed prefixes to avoid issues with empty strings.
	fmt.Fprintln(&b, bs("V"+c.Value), bs("F"+c.formula), bs("N"+c.NumFmt), c.date1904, c.Hidden, c.HMerge, c.VMerge, c.cellType, bs("HDS"+c.Hyperlink.DisplayString), bs("HL"+c.Hyperlink.Link), bs("HTT"+c.Hyperlink.Tooltip), c.num)
	return b.Bytes(), nil
}

// Read a slice of bytes, produced by MarshalBinary, into a Cell
func (c *Cell) UnmarshalBinary(data []byte) error {
	ubs := func(s string) string {
		decoded, err := base64.StdEncoding.DecodeString(s)
		if err != nil {
			panic(err)
		}
		return string(decoded)
	}

	b := bytes.NewBuffer(data)

	var value, formula, numfmt, hds, hl, htt string
	_, err := fmt.Fscanln(b, &value, &formula, &numfmt, &c.date1904, &c.Hidden, &c.HMerge, &c.VMerge, &c.cellType, &hds, &hl, &htt, &c.num)
	c.Value = strings.TrimPrefix(ubs(value), "V")
	c.formula = strings.TrimPrefix(ubs(formula), "F")
	c.NumFmt = strings.TrimPrefix(ubs(numfmt), "N")
	c.Hyperlink.DisplayString = strings.TrimPrefix(ubs(hds), "HDS")
	c.Hyperlink.Link = strings.TrimPrefix(ubs(hl), "HL")
	c.Hyperlink.Tooltip = strings.TrimPrefix(ubs(htt), "HTT")
	return err
}

// Modified returns True if a cell has been modified since it was last persisted.
func (c *Cell) Modified() bool {
	if c == nil {
		return false
	}
	rtEq := func(a, b []RichTextRun) bool {
		if len(a) != len(b) {
			return false
		}
		for i := 0; i < len(a); i++ {
			if a[i] != b[i] {
				return false
			}
		}
		return true
	}

	return c.modified || c.Value != c.origValue || c.NumFmt != c.origNumFmt || !rtEq(c.RichText, c.origRichText)
}

// Return a string repersenting a Cell in a way that can be used by the CellStore
func (c *Cell) key() string {
	return fmt.Sprintf("%s:%06d:%06d", c.Row.Sheet.Name, c.Row.num, c.num)
}

// Hyperlink is a structure to store link information
// in-workbook links to cells or defined names are stored in Location
// external links are stores in Link
type Hyperlink struct {
	DisplayString string
	Link          string
	Tooltip       string
	Location      string
}

// CellInterface defines the public API of the Cell.
type CellInterface interface {
	String() string
	FormattedValue() string
}

// NewCell creates a cell with a reference to its parent Row.  In most
// cases you shouldn't call this, but rather call Row.AddCell.
func newCell(r *Row, num int) *Cell {
	cell := &Cell{Row: r, num: num}
	return cell
}

func (c *Cell) updatable() {
	if c.Row != nil && c.Row.cellStoreRow != nil {
		c.Row.cellStoreRow.CellUpdatable(c)
	}

}

// Merge with other cells, horizontally and/or vertically.
func (c *Cell) Merge(hcells, vcells int) {
	c.updatable()
	c.HMerge = hcells
	c.VMerge = vcells
	c.modified = true
}

// Type returns the CellType of a cell. See CellType constants for more details.
func (c *Cell) Type() CellType {
	return c.cellType
}

// SetString sets the value of a cell to a string.
func (c *Cell) SetString(s string) {
	c.updatable()
	c.Value = s
	c.RichText = nil
	c.formula = ""
	c.cellType = CellTypeString
	c.modified = true
}

// SetRichText sets the value of a cell to a set of the rich text.
func (c *Cell) SetRichText(r []RichTextRun) {
	c.updatable()
	c.Value = ""
	c.RichText = append([]RichTextRun(nil), r...)
	c.formula = ""
	c.cellType = CellTypeString
	c.modified = true
}

// String returns the value of a Cell as a string.  If you'd like to
// see errors returned from formatting then please use
// Cell.FormattedValue() instead.
func (c *Cell) String() string {
	// To preserve the String() interface we'll throw away errors.
	// Note that using FormattedValue is therefore strongly
	// preferred.
	value, _ := c.FormattedValue()
	return value
}

// SetFloat sets the value of a cell to a float.
func (c *Cell) SetFloat(n float64) {
	c.updatable()
	c.SetValue(n)
}

// IsTime returns true if the cell stores a time value.
func (c *Cell) IsTime() bool {
	c.getNumberFormat()
	return c.parsedNumFmt.isTimeFormat
}

// GetTime returns the value of a Cell as a time.Time
func (c *Cell) GetTime(date1904 bool) (t time.Time, err error) {
	f, err := c.Float()
	if err != nil {
		return t, err
	}
	return TimeFromExcelTime(f, date1904), nil
}

/*
	The following are samples of format samples.

	* "0.00e+00"
	* "0", "#,##0"
	* "0.00", "#,##0.00", "@"
	* "#,##0 ;(#,##0)", "#,##0 ;[red](#,##0)"
	* "#,##0.00;(#,##0.00)", "#,##0.00;[red](#,##0.00)"
	* "0%", "0.00%"
	* "0.00e+00", "##0.0e+0"
*/

// SetFloatWithFormat sets the value of a cell to a float and applies
// formatting to the cell.
func (c *Cell) SetFloatWithFormat(n float64, format string) {
	c.updatable()
	c.SetValue(n)
	c.NumFmt = format
	c.formula = ""
}

// SetCellFormat set cell value  format
func (c *Cell) SetFormat(format string) {
	c.updatable()
	c.NumFmt = format
	c.modified = true
}

// DateTimeOptions are additional options for exporting times
type DateTimeOptions struct {
	// Location allows calculating times in other timezones/locations
	Location *time.Location
	// ExcelTimeFormat is the string you want excel to use to format the datetime
	ExcelTimeFormat string
}

var (
	DefaultDateFormat     = builtInNumFmt[14]
	DefaultDateTimeFormat = builtInNumFmt[22]

	DefaultDateOptions = DateTimeOptions{
		Location:        timeLocationUTC,
		ExcelTimeFormat: DefaultDateFormat,
	}

	DefaultDateTimeOptions = DateTimeOptions{
		Location:        timeLocationUTC,
		ExcelTimeFormat: DefaultDateTimeFormat,
	}
)

// SetDate sets the value of a cell to a float.
func (c *Cell) SetDate(t time.Time) {
	c.updatable()
	c.SetDateWithOptions(t, DefaultDateOptions)
}

func (c *Cell) SetDateTime(t time.Time) {
	c.updatable()
	c.SetDateWithOptions(t, DefaultDateTimeOptions)
}

// SetDateWithOptions allows for more granular control when exporting dates and times
func (c *Cell) SetDateWithOptions(t time.Time, options DateTimeOptions) {
	c.updatable()
	_, offset := t.In(options.Location).Zone()
	t = time.Unix(t.Unix()+int64(offset), int64(t.Nanosecond()))
	c.SetDateTimeWithFormat(TimeToExcelTime(t.In(timeLocationUTC), c.date1904), options.ExcelTimeFormat)
	c.modified = true
}

func (c *Cell) SetDateTimeWithFormat(n float64, format string) {
	c.updatable()
	c.Value = strconv.FormatFloat(n, 'f', -1, 64)
	c.NumFmt = format
	c.formula = ""
	c.cellType = CellTypeNumeric
	c.modified = true
}

// Float returns the value of cell as a number.
func (c *Cell) Float() (float64, error) {
	f, err := strconv.ParseFloat(c.Value, 64)
	if err != nil {
		return math.NaN(), err
	}
	return f, nil
}

// SetInt64 sets a cell's value to a 64-bit integer.
func (c *Cell) SetInt64(n int64) {
	c.updatable()
	c.SetValue(n)
}

// Int64 returns the value of cell as 64-bit integer.
func (c *Cell) Int64() (int64, error) {
	f, err := strconv.ParseInt(c.Value, 10, 64)
	if err != nil {
		return -1, err
	}
	return f, nil
}

// GeneralNumeric returns the value of the cell as a string. It is formatted very closely to the the XLSX spec for how
// to display values when the storage type is Number and the format type is General. It is not 100% identical to the
// spec but is as close as you can get using the built in Go formatting tools.
func (c *Cell) GeneralNumeric() (string, error) {
	return generalNumericScientific(c.Value, true)
}

// GeneralNumericWithoutScientific returns numbers that are always formatted as numbers, but it does not follow
// the rules for when XLSX should switch to scientific notation, since sometimes scientific notation is not desired,
// even if that is how the document is supposed to be formatted.
func (c *Cell) GeneralNumericWithoutScientific() (string, error) {
	return generalNumericScientific(c.Value, false)
}

// SetInt sets a cell's value to an integer.
func (c *Cell) SetInt(n int) {
	c.updatable()
	c.SetValue(n)
}

// SetHyperlink sets this cell to contain the given hyperlink, displayText and tooltip.
// If the displayText or tooltip are an empty string, they will not be set.
// The hyperlink provided must be a valid URL starting with http:// or https:// or
// excel will not recognize it as an external link. All other hyperlink formats will be
// treated as internal link between sheets. Official format in form of `#Sheet!A123`.
// Maximum number of hyperlinks per sheet is 65530, according to specification.
func (c *Cell) SetHyperlink(hyperlink string, displayText string, tooltip string) {
	c.updatable()
	h := strings.ToLower(hyperlink)
	if strings.HasPrefix(h, "http:") || strings.HasPrefix(h, "https://") {
		c.Hyperlink = Hyperlink{Link: hyperlink}
	} else {
		c.Hyperlink = Hyperlink{Link: hyperlink, Location: hyperlink}
	}
	c.SetString(hyperlink)
	c.Row.Sheet.addRelation(RelationshipTypeHyperlink, hyperlink, RelationshipTargetModeExternal)
	if displayText != "" {
		c.Hyperlink.DisplayString = displayText
		c.SetString(displayText)
	}
	if tooltip != "" {
		c.Hyperlink.Tooltip = tooltip
	}
}

// SetValue sets a cell's value to any type.
func (c *Cell) SetValue(n interface{}) {
	c.updatable()
	switch t := n.(type) {
	case time.Time:
		c.SetDateTime(t)
	case int, int8, int16, int32, int64:
		c.SetNumeric(fmt.Sprintf("%d", n))
	case float64:
		// When formatting floats, do not use fmt.Sprintf("%v", n), this will cause numbers below 1e-4 to be printed in
		// scientific notation. Scientific notation is not a valid way to store numbers in XML.
		// Also not not use fmt.Sprintf("%f", n), this will cause numbers to be stored as X.XXXXXX. Which means that
		// numbers will lose precision and numbers with fewer significant digits such as 0 will be stored as 0.000000
		// which causes tests to fail.
		c.SetNumeric(strconv.FormatFloat(t, 'f', -1, 64))
	case float32:
		c.SetNumeric(strconv.FormatFloat(float64(t), 'f', -1, 32))
	case string:
		c.SetString(t)
	case []byte:
		c.SetString(string(t))
	case bool:
		c.SetBool(t)
	case nil:
		c.SetString("")
	default:
		c.SetString(fmt.Sprintf("%v", n))
	}
}

// SetNumeric sets a cell's value to a number
func (c *Cell) SetNumeric(s string) {
	c.updatable()
	c.Value = s
	c.NumFmt = builtInNumFmt[builtInNumFmtIndex_GENERAL]
	c.formula = ""
	c.cellType = CellTypeNumeric
	c.modified = true
}

// Int returns the value of cell as integer.
// Has max 53 bits of precision
// See: float64(int64(math.MaxInt))
func (c *Cell) Int() (int, error) {
	f, err := strconv.ParseFloat(c.Value, 64)
	if err != nil {
		return -1, err
	}
	return int(f), nil
}

// SetBool sets a cell's value to a boolean.
func (c *Cell) SetBool(b bool) {
	c.updatable()
	if b {
		c.Value = "1"
	} else {
		c.Value = "0"
	}
	c.cellType = CellTypeBool
	c.modified = true
}

// Bool returns a boolean from a cell's value.
// TODO: Determine if the current return value is
// appropriate for types other than CellTypeBool.
func (c *Cell) Bool() bool {
	// If bool, just return the value.
	if c.cellType == CellTypeBool {
		return c.Value == "1"
	}
	// If numeric, base it on a non-zero.
	if c.cellType == CellTypeNumeric {
		return c.Value != "0"
	}
	// Return whether there's an empty string.
	return c.Value != ""
}

// SetFormula sets the format string for a cell.
func (c *Cell) SetFormula(formula string) {
	c.updatable()
	c.formula = formula
	c.cellType = CellTypeNumeric
	c.modified = true
}

func (c *Cell) SetStringFormula(formula string) {
	c.updatable()
	c.formula = formula
	c.cellType = CellTypeStringFormula
	c.modified = true
}

// Formula returns the formula string for the cell.
func (c *Cell) Formula() string {
	return c.formula
}

// GetStyle returns the Style associated with a Cell
func (c *Cell) GetStyle() *Style {
	if c.style == nil {
		c.style = NewStyle()
	}
	return c.style
}

// SetStyle sets the style of a cell.
func (c *Cell) SetStyle(style *Style) {
	c.updatable()
	c.style = style
	c.modified = true
}

// GetNumberFormat returns the number format string for a cell.
func (c *Cell) GetNumberFormat() string {
	return c.NumFmt
}

// getNumberFormat will update the parsedNumFmt struct if it has become out of date, since a cell's NumFmt string is a
// public field that could be edited by clients.
func (c *Cell) getNumberFormat() *parsedNumberFormat {
	if c.parsedNumFmt == nil || c.parsedNumFmt.numFmt != c.NumFmt {
		c.parsedNumFmt = parseFullNumberFormatString(c.NumFmt)
	}
	return c.parsedNumFmt
}

// FormattedValue returns a value, and possibly an error condition
// from a Cell.  If it is possible to apply a format to the cell
// value, it will do so, if not then an error will be returned, along
// with the raw value of the Cell.
func (c *Cell) FormattedValue() (string, error) {
	fullFormat := c.getNumberFormat()
	returnVal, err := fullFormat.FormatValue(c)
	if fullFormat.parseEncounteredError != nil {
		return returnVal, *fullFormat.parseEncounteredError
	}
	return returnVal, err
}

// SetDataValidation set data validation
func (c *Cell) SetDataValidation(dd *xlsxDataValidation) {
	c.updatable()
	c.DataValidation = dd
	c.modified = true
}

// GetCoordinates returns a pair of integers representing the
// cartesian coorindates of the Cell within the Sheet.  The
// coordinates are zero based and a returned in order x,y where x is
// the Column number and y is the Row number.  If you need to convert
// these numbers to a Excel cellID (i.e. B15) then please see the
// GetCellIDStringFromCoords function.
func (c *Cell) GetCoordinates() (int, int) {
	return c.num, c.Row.num
}

package xlsx

import "fmt"

// CellStore provides an interface for interacting with backend cell
// storage. For example, this allows us, as required, to persist cells
// to some store instead of holding them in memory.  This tactic
// allows us a degree of control around the characteristics of our
// programs when handling large spreadsheets - we can choose to run
// more slowly, but without exhausting system memory.
//
// If you wish to implement a custom CellStore you must not only
// support this interface, but also a CellStoreConstructor and a
// FileOption that set's the File's cellStoreConstructor to the right
// constructor.
type CellStore interface {
	MakeRow(sheet *Sheet) *Row
	MakeRowWithLen(sheet *Sheet, len int) *Row
	ReadRow(key string, sheet *Sheet) (*Row, error)
	WriteRow(r *Row) error
	MoveRow(r *Row, newIndex int) error
	RemoveRow(key string) error
	Close() error
}

// CellStoreConstructor defines the signature of a function that will
// be used to return a new instance of the CellStore implmentation,
// you must pass this into
type CellStoreConstructor func() (CellStore, error)

// CellStoreRow is the interface used to interact with the currently loaded Row from the CellStore.  Different backends can choose whether to hold the whole row in memory, or persist and load the cell
type CellStoreRow interface {
	AddCell() *Cell
	GetCell(colIdx int) *Cell
	PushCell(c *Cell)
	ForEachCell(cvf CellVisitorFunc, option ...CellVisitorOption) error
	MaxCol() int
	CellCount() int
	Updatable()
	CellUpdatable(c *Cell)
}

// CellVisitorFunc defines the signature of a function that will be
// called when visiting a Cell using CellStore.ForEachInRow.
type CellVisitorFunc func(c *Cell) error

// RowNotFoundError is an Error that should be returned by a
// RowStore implementation if a call to ReadRow is made with a key
// that doesn't correspond to any persisted Row.
type RowNotFoundError struct {
	key    string
	reason string
}

// NewRowNotFoundError creates a new RowNotFoundError, capturing the Row key and the reason this key could not be found.
func NewRowNotFoundError(key, reason string) *RowNotFoundError {
	return &RowNotFoundError{key, reason}
}

// Error returns a human-readable description of the failure to find a Row.  It makes RowNotFoundError comply with the Error interface.
func (cnfe RowNotFoundError) Error() string {
	return fmt.Sprintf("Row %q not found. %s", cnfe.key, cnfe.reason)
}

package xlsx

// Default column width in excel
const ColWidth = 9.5
const Excel2006MaxRowCount = 1048576
const Excel2006MaxRowIndex = Excel2006MaxRowCount - 1

type Col struct {
	Min          int
	Max          int
	Hidden       *bool
	Width        *float64
	Collapsed    *bool
	OutlineLevel *uint8
	BestFit      *bool
	CustomWidth  *bool
	Phonetic     *bool
	numFmt       string
	parsedNumFmt *parsedNumberFormat
	style        *Style
	outXfID      int
}

// NewColForRange return a pointer to a new Col, which will apply to
// columns in the range min to max (inclusive).  Note, in order for
// this Col to do anything useful you must set some of its parameters
// and then apply it to a Sheet by calling sheet.SetColParameters.
// Column numbers start from 1.
func NewColForRange(min, max int) *Col {
	if min < 1 {
		panic("min col must be >= 1")
	}
	if max < min {
		// Nice try ;-)
		return &Col{Min: max, Max: min}
	}

	return &Col{Min: min, Max: max}
}

// SetWidth sets the width of columns that have this Col applied to
// them.  The width is expressed as the number of characters of the
// maximum digit width of the numbers 0-9 as rendered in the normal
// style's font.
func (c *Col) SetWidth(width float64) {
	c.Width = &width
	custom := true
	c.CustomWidth = &custom
}

// SetType will set the format string of a column based on the type that you want to set it to.
// This function does not really make a lot of sense.
func (c *Col) SetType(cellType CellType) {
	switch cellType {
	case CellTypeString:
		c.numFmt = builtInNumFmt[builtInNumFmtIndex_STRING]
	case CellTypeNumeric:
		c.numFmt = builtInNumFmt[builtInNumFmtIndex_INT]
	case CellTypeBool:
		c.numFmt = builtInNumFmt[builtInNumFmtIndex_GENERAL] //TEMP
	case CellTypeInline:
		c.numFmt = builtInNumFmt[builtInNumFmtIndex_STRING]
	case CellTypeError:
		c.numFmt = builtInNumFmt[builtInNumFmtIndex_GENERAL] //TEMP
	case CellTypeDate:
		// Cells that are stored as dates are not properly supported in this library.
		// They should instead be stored as a Numeric with a date format.
		c.numFmt = builtInNumFmt[builtInNumFmtIndex_GENERAL]
	case CellTypeStringFormula:
		c.numFmt = builtInNumFmt[builtInNumFmtIndex_STRING]
	}
}

// GetStyle returns the Style associated with a Col
func (c *Col) GetStyle() *Style {
	return c.style
}

// SetStyle sets the style of a Col
func (c *Col) SetStyle(style *Style) {
	c.style = style
}

func (c *Col) SetOutlineLevel(outlineLevel uint8) {
	c.OutlineLevel = &outlineLevel
}

// copyToRange is an internal convenience function to make a copy of a
// Col with a different Min and Max value, it is not intended as a
// general purpose Col copying function as you must still insert the
// resulting Col into the Col Store.
func (c *Col) copyToRange(min, max int) *Col {
	return &Col{
		Min:          min,
		Max:          max,
		Hidden:       c.Hidden,
		Width:        c.Width,
		Collapsed:    c.Collapsed,
		OutlineLevel: c.OutlineLevel,
		BestFit:      c.BestFit,
		CustomWidth:  c.CustomWidth,
		Phonetic:     c.Phonetic,
		numFmt:       c.numFmt,
		parsedNumFmt: c.parsedNumFmt,
		style:        c.style,
	}
}

type ColStoreNode struct {
	Col  *Col
	Prev *ColStoreNode
	Next *ColStoreNode
}

func (csn *ColStoreNode) findNodeForColNum(num int) *ColStoreNode {
	switch {
	case num >= csn.Col.Min && num <= csn.Col.Max:
		return csn

	case num < csn.Col.Min:
		if csn.Prev == nil {
			return nil
		}
		if csn.Prev.Col.Max < num {
			return nil
		}
		return csn.Prev.findNodeForColNum(num)

	case num > csn.Col.Max:
		if csn.Next == nil {
			return nil
		}
		if csn.Next.Col.Min > num {
			return nil
		}
		return csn.Next.findNodeForColNum(num)
	}
	return nil
}

// ColStore is the working store of Col definitions, it will simplify all Cols added to it, to ensure there ar no overlapping definitions.
type ColStore struct {
	Root *ColStoreNode
	Len  int
}

// Add a Col to the ColStore. If it overwrites all, or part of some
// existing Col's range of columns the that Col will be adjusted
// and/or split to make room for the new Col.
func (cs *ColStore) Add(col *Col) *ColStoreNode {
	newNode := &ColStoreNode{Col: col}
	if cs.Root == nil {
		cs.Root = newNode
		cs.Len = 1
		return newNode
	}
	cs.makeWay(cs.Root, newNode)
	return newNode
}

func (cs *ColStore) FindColByIndex(index int) *Col {
	csn := cs.findNodeForColNum(index)
	if csn != nil {
		return csn.Col
	}
	return nil
}

func (cs *ColStore) findNodeForColNum(num int) *ColStoreNode {
	if cs.Root == nil {
		return nil
	}
	return cs.Root.findNodeForColNum(num)
}

func (cs *ColStore) removeNode(node *ColStoreNode) {
	if node.Prev != nil {
		if node.Next != nil {
			node.Prev.Next = node.Next
		} else {
			node.Prev.Next = nil
		}

	}
	if node.Next != nil {
		if node.Prev != nil {
			node.Next.Prev = node.Prev
		} else {
			node.Next.Prev = nil
		}
	}
	if cs.Root == node {
		switch {
		case node.Prev != nil:
			cs.Root = node.Prev
		case node.Next != nil:
			cs.Root = node.Next
		default:
			cs.Root = nil
		}
	}
	node.Next = nil
	node.Prev = nil
	cs.Len -= 1
}

// makeWay will adjust the Min and Max of this ColStoreNode's Col to
// make way for a new ColStoreNode's Col. If necessary it will
// generate an additional ColStoreNode with a new Col covering the
// "tail" portion of this ColStoreNode's Col should the new node lay
// completely within the range of this one, but without reaching its
// maximum extent.
func (cs *ColStore) makeWay(node1, node2 *ColStoreNode) {
	switch {
	case node1.Col.Max < node2.Col.Min:
		// The node2 starts after node1 ends, there's no overlap
		//
		// Node1 |----|
		// Node2        |----|
		if node1.Next != nil {
			if node1.Next.Col.Min <= node2.Col.Max {
				cs.makeWay(node1.Next, node2)
				return
			}
			cs.addNode(node1, node2, node1.Next)
			return
		}
		cs.addNode(node1, node2, nil)
		return

	case node1.Col.Min > node2.Col.Max:
		// Node2 ends before node1 begins, there's no overlap
		//
		// Node1         |-----|
		// Node2  |----|
		if node1.Prev != nil {
			if node1.Prev.Col.Max >= node2.Col.Min {
				cs.makeWay(node1.Prev, node2)
				return
			}
			cs.addNode(node1.Prev, node2, node1)
			return
		}
		cs.addNode(nil, node2, node1)
		return

	case node1.Col.Min == node2.Col.Min && node1.Col.Max == node2.Col.Max:
		// Exact match
		//
		// Node1 |xxx|
		// Node2 |---|

		prev := node1.Prev
		next := node1.Next
		cs.removeNode(node1)
		cs.addNode(prev, node2, next)
		// Remove node may have set the root to nil
		if cs.Root == nil {
			cs.Root = node2
		}
		return

	case node1.Col.Min > node2.Col.Min && node1.Col.Max < node2.Col.Max:
		// Node2 envelopes node1
		//
		// Node1  |xx|
		// Node2 |----|

		prev := node1.Prev
		next := node1.Next
		cs.removeNode(node1)
		switch {
		case prev == node2:
			node2.Next = next
		case next == node2:
			node2.Prev = prev
		default:
			cs.addNode(prev, node2, next)
		}

		if node2.Prev != nil && node2.Prev.Col.Max >= node2.Col.Min {
			cs.makeWay(prev, node2)
		}
		if node2.Next != nil && node2.Next.Col.Min <= node2.Col.Max {
			cs.makeWay(next, node2)
		}

		if cs.Root == nil {
			cs.Root = node2
		}

	case node1.Col.Min < node2.Col.Min && node1.Col.Max > node2.Col.Max:
		// Node2 bisects node1:
		//
		// Node1 |---xx---|
		// Node2    |--|
		newCol := node1.Col.copyToRange(node2.Col.Max+1, node1.Col.Max)
		newNode := &ColStoreNode{Col: newCol}
		cs.addNode(node1, newNode, node1.Next)
		node1.Col.Max = node2.Col.Min - 1
		cs.addNode(node1, node2, newNode)
		return

	case node1.Col.Max >= node2.Col.Min && node1.Col.Min < node2.Col.Min:
		// Node2 overlaps node1 at some point above it's minimum:
		//
		//  Node1  |----xx|
		//  Node2      |-------|
		next := node1.Next
		node1.Col.Max = node2.Col.Min - 1
		if next == node2 {
			return
		}
		cs.addNode(node1, node2, next)
		if next != nil && next.Col.Min <= node2.Col.Max {
			cs.makeWay(next, node2)
		}
		return

	case node1.Col.Min <= node2.Col.Max && node1.Col.Min > node2.Col.Min:
		// Node2 overlaps node1 at some point below it's maximum:
		//
		// Node1:     |------|
		// Node2: |----xx|
		prev := node1.Prev
		node1.Col.Min = node2.Col.Max + 1
		if prev == node2 {
			return
		}
		cs.addNode(prev, node2, node1)
		if prev != nil && prev.Col.Max >= node2.Col.Min {
			cs.makeWay(node1.Prev, node2)
		}
		return
	}
}

func (cs *ColStore) addNode(prev, this, next *ColStoreNode) {
	if prev != nil {
		prev.Next = this
	}
	this.Prev = prev
	this.Next = next
	if next != nil {
		next.Prev = this
	}
	cs.Len += 1
}

func (cs *ColStore) getOrMakeColsForRange(start *ColStoreNode, min, max int) []*Col {
	cols := []*Col{}
	var csn *ColStoreNode
	var newCol *Col
	switch {
	case start == nil:
		newCol = NewColForRange(min, max)
		csn = cs.Add(newCol)
	case start.Col.Min <= min && start.Col.Max >= min:
		csn = start
	case start.Col.Min < min && start.Col.Max < min:
		if start.Next != nil {
			return cs.getOrMakeColsForRange(start.Next, min, max)
		}
		newCol = NewColForRange(min, max)
		csn = cs.Add(newCol)
	case start.Col.Min > min:
		if start.Col.Min > max {
			newCol = NewColForRange(min, max)
		} else {
			newCol = NewColForRange(min, start.Col.Min-1)
		}
		csn = cs.Add(newCol)
	}

	cols = append(cols, csn.Col)
	if csn.Col.Max >= max {
		return cols
	}
	cols = append(cols, cs.getOrMakeColsForRange(csn.Next, csn.Col.Max+1, max)...)
	return cols
}

func chainOp(csn *ColStoreNode, fn func(idx int, col *Col)) {
	for csn.Prev != nil {
		csn = csn.Prev
	}

	var i int
	for i = 0; csn.Next != nil; i++ {
		fn(i, csn.Col)
		csn = csn.Next
	}
	fn(i+1, csn.Col)
}

// ForEach calls the function fn for each Col defined in the ColStore.
func (cs *ColStore) ForEach(fn func(idx int, col *Col)) {
	if cs.Root == nil {
		return
	}
	chainOp(cs.Root, fn)
}

package xlsx

import (
		"testing"

		qt "github.com/frankban/quicktest"
)

var notNil = qt.Not(qt.IsNil)

func TestNewColForRange(t *testing.T) {
		c := qt.New(t)
		col := NewColForRange(30, 45)
		c.Assert(col, notNil)
		c.Assert(col.Min, qt.Equals, 30)
		c.Assert(col.Max, qt.Equals, 45)

		// Auto fix the min/max
		col = NewColForRange(45, 30)
		c.Assert(col, notNil)
		c.Assert(col.Min, qt.Equals, 30)
		c.Assert(col.Max, qt.Equals, 45)

		// Min cannot be less than 1
		c.Assert(func() {NewColForRange(0, 1)}, qt.PanicMatches, "min col must be >= 1")
}

func TestCol(t *testing.T) {
		c := qt.New(t)
		c.Run("SetType", func(c *qt.C) {
				expectations := map[CellType]string{
						CellTypeString:        builtInNumFmt[builtInNumFmtIndex_STRING],
						CellTypeNumeric:       builtInNumFmt[builtInNumFmtIndex_INT],
						CellTypeBool:          builtInNumFmt[builtInNumFmtIndex_GENERAL],
						CellTypeInline:        builtInNumFmt[builtInNumFmtIndex_STRING],
						CellTypeError:         builtInNumFmt[builtInNumFmtIndex_GENERAL],
						CellTypeDate:          builtInNumFmt[builtInNumFmtIndex_GENERAL],
						CellTypeStringFormula: builtInNumFmt[builtInNumFmtIndex_STRING],
				}

				assertSetType := func(cellType CellType, expectation string) {
						col := &Col{}
						col.SetType(cellType)
						c.Assert(col.numFmt, qt.Equals, expectation)
				}
				for k, v := range expectations {
						assertSetType(k, v)
				}
		})
		c.Run("SetWidth", func(c *qt.C) {
				col := &Col{}
				col.SetWidth(20.2)
				c.Assert(*col.Width, qt.Equals, 20.2)
				c.Assert(*col.CustomWidth, qt.Equals, true)
		})

		c.Run("copyToRange", func(c *qt.C) {
				nf := &parsedNumberFormat{}
				s := &Style{}
				c1 := &Col{
						Min:          1,
						Max:          11,
						Hidden:       bPtr(true),
						Width:        fPtr(300.4),
						Collapsed:    bPtr(true),
						OutlineLevel: u8Ptr(2),
						numFmt:       "-0.00",
						parsedNumFmt: nf,
						style:        s,
				}

				c2 := c1.copyToRange(4, 10)
				c.Assert(c2.Min, qt.Equals, 4)
				c.Assert(c2.Max, qt.Equals, 10)
				c.Assert(c2.Hidden, qt.Equals, c1.Hidden)
				c.Assert(c2.Width, qt.Equals, c1.Width)
				c.Assert(c2.Collapsed, qt.Equals, c1.Collapsed)
				c.Assert(c2.OutlineLevel, qt.Equals, c1.OutlineLevel)
				c.Assert(c2.numFmt, qt.Equals, c1.numFmt)
				c.Assert(c2.parsedNumFmt, qt.Equals, c1.parsedNumFmt)
				c.Assert(c2.style, qt.Equals, c1.style)
		})

}

type ColStoreSuite struct{}

func TestAddRootNode(t *testing.T) {
		col := &Col{Min: 1, Max: 1}
		cs := ColStore{}
		cs.Add(col)
		c := qt.New(t)
		c.Assert(cs.Len, qt.Equals, 1)
		c.Assert(cs.Root.Col, qt.Equals, col)
}

func TestMakeWay(t *testing.T) {
		c := qt.New(t)
		assertWayMade := func(cols []*Col, chainFunc func(*ColStore)) {

				cs := &ColStore{}
				for _, col := range cols {
						_ = cs.Add(col)
				}
				chainFunc(cs)
		}

		// Col1: |--|
		// Col2:    |--|
		assertWayMade([]*Col{{Min: 1, Max: 2}, {Min: 3, Max: 4}},
				func(cs *ColStore) {
						c.Assert(cs.Len, qt.Equals, 2)
						root := cs.Root
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 2)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, notNil)
						node2 := root.Next
						c.Assert(node2.Prev, qt.Equals, root)
						c.Assert(node2.Next, qt.IsNil)
						c.Assert(node2.Col.Min, qt.Equals, 3)
						c.Assert(node2.Col.Max, qt.Equals, 4)
				})

		// Col1:    |--|
		// Col2: |--|
		assertWayMade([]*Col{{Min: 3, Max: 4}, {Min: 1, Max: 2}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 2)
						c.Assert(root.Col.Min, qt.Equals, 3)
						c.Assert(root.Col.Max, qt.Equals, 4)
						c.Assert(root.Prev, notNil)
						c.Assert(root.Next, qt.IsNil)
						node2 := root.Prev
						c.Assert(node2.Next, qt.Equals, root)
						c.Assert(node2.Prev, qt.IsNil)
						c.Assert(node2.Col.Min, qt.Equals, 1)
						c.Assert(node2.Col.Max, qt.Equals, 2)
				})

		// Col1: |--x|
		// Col2:   |--|
		assertWayMade([]*Col{{Min: 1, Max: 3}, {Min: 3, Max: 4}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 2)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 2)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, notNil)
						node2 := root.Next
						c.Assert(node2.Prev, qt.Equals, root)
						c.Assert(node2.Next, qt.IsNil)
						c.Assert(node2.Col.Min, qt.Equals, 3)
						c.Assert(node2.Col.Max, qt.Equals, 4)
				})

		// Col1:  |x-|
		// Col2: |--|
		assertWayMade([]*Col{{Min: 2, Max: 3}, {Min: 1, Max: 2}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 2)
						c.Assert(root.Col.Min, qt.Equals, 3)
						c.Assert(root.Col.Max, qt.Equals, 3)
						c.Assert(root.Prev, notNil)
						c.Assert(root.Next, qt.IsNil)
						node2 := root.Prev
						c.Assert(node2.Next, qt.Equals, root)
						c.Assert(node2.Prev, qt.IsNil)
						c.Assert(node2.Col.Min, qt.Equals, 1)
						c.Assert(node2.Col.Max, qt.Equals, 2)
				})

		// Col1: |---xx---|
		// Col2:    |--|
		assertWayMade([]*Col{{Min: 1, Max: 8}, {Min: 4, Max: 5}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 3)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, notNil)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 3)
						node2 := root.Next
						c.Assert(node2.Prev, qt.Equals, root)
						c.Assert(node2.Col.Min, qt.Equals, 4)
						c.Assert(node2.Col.Max, qt.Equals, 5)
						c.Assert(node2.Next, notNil)
						node3 := node2.Next
						c.Assert(node3.Prev, qt.Equals, node2)
						c.Assert(node3.Next, qt.IsNil)
						c.Assert(node3.Col.Min, qt.Equals, 6)
						c.Assert(node3.Col.Max, qt.Equals, 8)
				})

		// Col1: |xx|
		// Col2: |--|
		assertWayMade([]*Col{{Min: 1, Max: 2, Width: fPtr(40.1)}, {Min: 1, Max: 2, Width: fPtr(10.0)}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 1)
						c.Assert(root, notNil)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, qt.IsNil)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 2)
						// This is how we establish we have the new node, and not the old one
						c.Assert(*root.Col.Width, qt.Equals, 10.0)
				})

		// Col1:  |xx|
		// Col2: |----|
		assertWayMade([]*Col{{Min: 2, Max: 3, Width: fPtr(40.1)}, {Min: 1, Max: 4, Width: fPtr(10.0)}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 1)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, qt.IsNil)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 4)
						// This is how we establish we have the new node, and not the old one
						c.Assert(*root.Col.Width, qt.Equals, 10.0)
				})

		// Col1: |--|
		// Col2:    |--|
		// Col3:       |--|
		assertWayMade([]*Col{{Min: 1, Max: 2}, {Min: 3, Max: 4}, {Min: 5, Max: 6}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 3)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, notNil)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 2)
						node2 := root.Next
						c.Assert(node2.Prev, qt.Equals, root)
						c.Assert(node2.Col.Min, qt.Equals, 3)
						c.Assert(node2.Col.Max, qt.Equals, 4)
						c.Assert(node2.Next, notNil)
						node3 := node2.Next
						c.Assert(node3.Prev, qt.Equals, node2)
						c.Assert(node3.Next, qt.IsNil)
						c.Assert(node3.Col.Min, qt.Equals, 5)
						c.Assert(node3.Col.Max, qt.Equals, 6)
				})

		// Col1:       |--|
		// Col2:    |--|
		// Col3: |--|
		assertWayMade([]*Col{{Min: 5, Max: 6}, {Min: 3, Max: 4}, {Min: 1, Max: 2}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 3)
						c.Assert(root.Prev, notNil)
						c.Assert(root.Next, qt.IsNil)
						c.Assert(root.Col.Min, qt.Equals, 5)
						c.Assert(root.Col.Max, qt.Equals, 6)
						node2 := root.Prev
						c.Assert(node2.Next, qt.Equals, root)
						c.Assert(node2.Col.Min, qt.Equals, 3)
						c.Assert(node2.Col.Max, qt.Equals, 4)
						c.Assert(node2.Prev, notNil)
						node3 := node2.Prev
						c.Assert(node3.Next, qt.Equals, node2)
						c.Assert(node3.Prev, qt.IsNil)
						c.Assert(node3.Col.Min, qt.Equals, 1)
						c.Assert(node3.Col.Max, qt.Equals, 2)
				})

		// Col1: |--|
		// Col2:          |--|
		// Col3:     |--|
		assertWayMade([]*Col{{Min: 1, Max: 2}, {Min: 10, Max: 11}, {Min: 5, Max: 6}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 3)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, notNil)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 2)
						node2 := root.Next
						c.Assert(node2.Prev, qt.Equals, root)
						c.Assert(node2.Col.Min, qt.Equals, 5)
						c.Assert(node2.Col.Max, qt.Equals, 6)
						c.Assert(node2.Next, notNil)
						node3 := node2.Next
						c.Assert(node3.Prev, qt.Equals, node2)
						c.Assert(node3.Next, qt.IsNil)
						c.Assert(node3.Col.Min, qt.Equals, 10)
						c.Assert(node3.Col.Max, qt.Equals, 11)
				})

		// Col1: |-x|
		// Col2:        |x-|
		// Col3:  |-------|
		assertWayMade([]*Col{
				{Min: 1, Max: 2}, {Min: 8, Max: 9}, {Min: 2, Max: 8}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 3)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, notNil)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 1)
						node2 := root.Next
						c.Assert(node2.Prev, qt.Equals, root)
						c.Assert(node2.Next, notNil)
						c.Assert(node2.Col.Min, qt.Equals, 2)
						c.Assert(node2.Col.Max, qt.Equals, 8)
						node3 := node2.Next
						c.Assert(node3.Prev, qt.Equals, node2)
						c.Assert(node3.Next, qt.IsNil)
						c.Assert(node3.Col.Min, qt.Equals, 9)
						c.Assert(node3.Col.Max, qt.Equals, 9)
				})

		// Col1: |-x|
		// Col2:        |--|
		// Col3:  |-----|
		assertWayMade([]*Col{
				{Min: 1, Max: 2}, {Min: 8, Max: 9}, {Min: 2, Max: 7}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 3)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, notNil)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 1)
						node2 := root.Next
						c.Assert(node2.Prev, qt.Equals, root)
						c.Assert(node2.Next, notNil)
						c.Assert(node2.Col.Min, qt.Equals, 2)
						c.Assert(node2.Col.Max, qt.Equals, 7)
						node3 := node2.Next
						c.Assert(node3.Prev, qt.Equals, node2)
						c.Assert(node3.Next, qt.IsNil)
						c.Assert(node3.Col.Min, qt.Equals, 8)
						c.Assert(node3.Col.Max, qt.Equals, 9)
				})

		// Col1: |--|
		// Col2:        |x-|
		// Col3:    |-----|
		assertWayMade([]*Col{
				{Min: 1, Max: 2}, {Min: 8, Max: 9}, {Min: 3, Max: 8}},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 3)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, notNil)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 2)
						node2 := root.Next
						c.Assert(node2.Prev, qt.Equals, root)
						c.Assert(node2.Next, notNil)
						c.Assert(node2.Col.Min, qt.Equals, 3)
						c.Assert(node2.Col.Max, qt.Equals, 8)
						node3 := node2.Next
						c.Assert(node3.Prev, qt.Equals, node2)
						c.Assert(node3.Next, qt.IsNil)
						c.Assert(node3.Col.Min, qt.Equals, 9)
						c.Assert(node3.Col.Max, qt.Equals, 9)
				})

		// Col1: |--|
		// Col2:   |xx|
		// Col3:     |--|
		// Col4:   |--|
		assertWayMade(
				[]*Col{
						{Min: 1, Max: 2},
						{Min: 3, Max: 4, Width: fPtr(1.0)},
						{Min: 5, Max: 6},
						{Min: 3, Max: 4, Width: fPtr(2.0)},
				},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 3)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, notNil)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 2)
						node2 := root.Next
						c.Assert(node2.Prev, qt.Equals, root)
						c.Assert(node2.Next, notNil)
						c.Assert(node2.Col.Min, qt.Equals, 3)
						c.Assert(node2.Col.Max, qt.Equals, 4)
						c.Assert(*node2.Col.Width, qt.Equals, 2.0) // We have the later version
						node3 := node2.Next
						c.Assert(node3.Prev, qt.Equals, node2)
						c.Assert(node3.Next, qt.IsNil)
						c.Assert(node3.Col.Min, qt.Equals, 5)
						c.Assert(node3.Col.Max, qt.Equals, 6)
				})

		// Col1: |-x|
		// Col2:   |xx|
		// Col3:     |x-|
		// Col4:  |----|
		assertWayMade(
				[]*Col{
						{Min: 1, Max: 2, Width: fPtr(1.0)},
						{Min: 3, Max: 4, Width: fPtr(2.0)},
						{Min: 5, Max: 6, Width: fPtr(3.0)},
						{Min: 2, Max: 5, Width: fPtr(4.0)},
				},
				func(cs *ColStore) {
						root := cs.Root
						c.Assert(cs.Len, qt.Equals, 3)
						c.Assert(root.Prev, qt.IsNil)
						c.Assert(root.Next, notNil)
						c.Assert(root.Col.Min, qt.Equals, 1)
						c.Assert(root.Col.Max, qt.Equals, 1)
						c.Assert(*root.Col.Width, qt.Equals, 1.0)
						node2 := root.Next
						c.Assert(node2.Prev, qt.Equals, root)
						c.Assert(node2.Next, notNil)
						c.Assert(node2.Col.Min, qt.Equals, 2)
						c.Assert(node2.Col.Max, qt.Equals, 5)
						c.Assert(*node2.Col.Width, qt.Equals, 4.0)
						node3 := node2.Next
						c.Assert(node3.Prev, qt.Equals, node2)
						c.Assert(node3.Next, qt.IsNil)
						c.Assert(node3.Col.Min, qt.Equals, 6)
						c.Assert(node3.Col.Max, qt.Equals, 6)
						c.Assert(*node3.Col.Width, qt.Equals, 3.0)
				})

}

func TestFindNodeForCol(t *testing.T) {
