func TestGetOrMakeColsForRange(t *testing.T) {
		c := qt.New(t)
		assertCols := func(min, max int, initalCols, expectedCols []*Col) {
				cs := &ColStore{}
				for _, col := range initalCols {
						cs.Add(col)
				}
				result := cs.getOrMakeColsForRange(cs.Root, min, max)
				c.Assert(result, qt.HasLen, len(expectedCols))
				for i := 0; i < len(expectedCols); i++ {
						got := result[i]
						expected := expectedCols[i]
						c.Assert(got.Min, qt.Equals, expected.Min)
						c.Assert(got.Max, qt.Equals, expected.Max)
				}
		}

		// make everything
		assertCols(1, 11, nil, []*Col{{Min: 1, Max: 11}})

		// get everything, one col
		assertCols(1, 11, []*Col{{Min: 1, Max: 11}}, []*Col{{Min: 1, Max: 11}})

		// get everything, many cols
		assertCols(1, 11,
				[]*Col{
						{Min: 1, Max: 4},
						{Min: 5, Max: 8},
						{Min: 9, Max: 11},
				},
				[]*Col{
						{Min: 1, Max: 4},
						{Min: 5, Max: 8},
						{Min: 9, Max: 11},
				},
		)

		// make missing col
		assertCols(1, 11,
				[]*Col{
						{Min: 1, Max: 4},
						{Min: 9, Max: 11},
				},
				[]*Col{
						{Min: 1, Max: 4},
						{Min: 5, Max: 8},
						{Min: 9, Max: 11},
				},
		)

}

