	}
	if err := writeString(buf, b.TopColor); err != nil {
		return err
	}
	if err := writeString(buf, b.Bottom); err != nil {
		return err
	}
	if err := writeString(buf, b.BottomColor); err != nil {
		return err
	}
	return nil
}

