func writeCell(buf *bytes.Buffer, c *Cell) error {
	var err error
	if c == nil {
		if err := writeBool(buf, true); err != nil {

			return err
		}
		return writeEndOfRecord(buf)
	}
	if err := writeBool(buf, false); err != nil {
