func readRichTextColor(reader *bytes.Reader) (*RichTextColor, error) {
	var err error
	var hasIndexed bool
	var hasTheme bool

	c := &RichTextColor{}

	if c.coreColor.RGB, err = readString(reader); err != nil {
		return nil, err
	}
