# Build script for PowerShell
$ErrorActionPreference = "Stop"

$cmd = $args[0]
Write-Host "cmd: $cmd"


function quick() {
     try {
        go build
        if (-not $?) {
            Write-Host "build failed!" # WTH, error handling in powershell sucks
            exit
        }
    } catch {
        # This try/catch thing doesn't work, the above if statement does work though
        Write-Host "build failed 2!"
        exit
    }
    ./functions
}

function build () {
    docker run --rm -v ${pwd}:/go/src/github.com/fnproject/fn -w /go/src/github.com/fnproject/fn/cmd/fnserver golang:alpine go build -o fn-alpine
    docker build -t fnproject/fnserver:latest .
}

function run () {
    docker run --rm --name functions -it -v /var/run/docker.sock:/var/run/docker.sock -e FN_LOG_LEVEL=debug -e "FN_DB_URL=sqlite3:///app/data/fn.db" -v $PWD/data:/app/data -p 8080:8080 fnproject/fnserver
}

switch ($cmd)
{
    "quick" {quick}
    "build" { build }
    "run" {run}
    default {"Invalid command: $cmd"}
}

#!/bin/bash
# Top level test script to start all other tests
set -exuo pipefail

export CONTEXT="fn_basic_tests"
source ./helpers.sh
remove_containers ${CONTEXT}

export GOFLAGS=-mod=vendor
export POSTGRES_URL=$(spawn_postgres ${CONTEXT})
export MYSQL_URL=$(spawn_mysql ${CONTEXT})
export FN_DS_DB_PING_MAX_RETRIES=60

go test $(go list ./... | \
    grep -v vendor | \
    grep -v examples | \
    grep -v test/fn-api-tests | \
    grep -v test/fn-system-tests | \
    grep -v images/fn-test-utils\
)

go vet $(go list ./... | grep -v vendor)

remove_containers ${CONTEXT}

docker run -v `pwd`:/go/src/github.com/fnproject/fn --rm fnproject/swagger:0.0.1 /go/src/github.com/fnproject/fn/docs/swagger_v2.yml
