const placeholderData = ``

var placeholder, _ = ioutil.ReadAll(base64.NewDecoder(base64.StdEncoding, strings.NewReader(placeholderData)))

package main

import (
	"context"
	"log"
	"net/http"
	"net/url"
	"os"
