func writeTorrentData(ts *storage.Torrent, info metainfo.Info, b []byte) {
	for i := 0; i < info.NumPieces(); i += 1 {
		p := info.Piece(i)
		ts.Piece(p).WriteAt(b[p.Offset():p.Offset()+p.Length()], 0)
	}
}

