	in2 := inputs[1]

	diffOp := MyNewDiffOp{op}
	g := in1.Graph()
	in2Diff := NewUniqueNode(WithType(in2.Type()), WithShape(in2.Shape().Clone()...), WithChildren(Nodes{in2}), In(g), WithOp(Iop{}))

	var in1Diff *Node
	if in1Diff, err = ApplyOp(diffOp, in1, in2, in2Diff); err != nil {
