	correctGradA := []float64{0, 1, 1, 1}
	correctGradB := []float64{1, 0, 0, 0}

	assert.Equal(correctGradA, grads[0].Value().Data())
	assert.Equal(correctGradB, grads[1].Value().Data())
}

