	div := newElemBinOp(divOpType, y, x)
	if d, err = div.Do(ydv.d, d); err != nil {
		return errors.Wrapf(err, doFail, div)
	}

	sub := newElemBinOp(subOpType, x, y)
	if _, err = sub.Do(xdv.d, d); err != nil {
		return errors.Wrapf(err, doFail, sub)
	}
	return nil
}

