func TestCubeDiff(t *testing.T) {
	assert := assert.New(t)
	v, x, _, xT, _, err := unaryOpDiffTest(cubeOpType)
	if err != nil {
		t.Error(err)
	}

	correct := 3 * v * v
	xG, err := x.Grad()
	if err != nil {
		t.Error(err)
	}

	assert.True(dawson.CloseF64(correct, extractF64(xG)), "%v != %v", xG, correct)

	// Tensor edition
	xdvd := xT.boundTo.(*dualValue).d
	correctT := []float64{correct, correct}
	assert.True(floatsEqual64(correctT, extractF64s(xdvd)))
}

func TestTanhDiff(t *testing.T) {
	assert := assert.New(t)
	v, x, _, xT, _, err := unaryOpDiffTest(tanhOpType)
	if err != nil {
		t.Error(err)
	}

	// NOTE: there are not guarantees of identical behaviours across architectures,
	// in this case arm64 gives different results than amd64 for Tanh.
	// See https://github.com/golang/go/issues/18354#issuecomment-267705645
	correct := 1.0 - (float64(math.Tanh(v)) * float64(math.Tanh(v))) // I'm surprised Golang doesn't have a secant function!
	assert.InDeltaf(correct, x.boundTo.(*dualValue).d.Data(), 1e-14, "")

	// Tensor edition
	xdvd := xT.boundTo.(*dualValue).d.(*tensor.Dense)
	assert.InDeltaSlicef([]float64{correct, correct}, xdvd.Data(), 1e-14, "")
}

func TestSigmoidDiff(t *testing.T) {
	assert := assert.New(t)
	v, x, _, xT, _, err := unaryOpDiffTest(sigmoidOpType)
	if err != nil {
		t.Error(err)
	}

	correct := math.Exp(-v) / ((1 + math.Exp(-v)) * (1 + math.Exp(-v)))
	xG := x.boundTo.(*dualValue).d
	assert.True(dawson.CloseF64(correct, extractF64(xG)))

	// Tensor edition
	xdvd := xT.boundTo.(*dualValue).d
	negCorrect := math.Exp(v) / ((1 + math.Exp(v)) * (1 + math.Exp(v)))
	corrects := []float64{negCorrect, correct}
	assert.True(floatsEqual64(corrects, extractF64s(xdvd)))
}

