func TestSoftplus(t *testing.T) {
	assert := assert.New(t)

	var x, y, a, b *Node
	var v Value
	var xV, yV, xG, bV, aG Value
	var err error

	/* FLOAT64 SCALAR */

	if x, y, a, b, v, err = unaryOpTest(t, Float64, tensor.Shape{}, Softplus); err != nil {
		t.Fatal(err)
	}

	xV = x.Value()
	yV = y.Value()
	if xG, err = x.Grad(); err != nil {
		t.Errorf("x has no grad: %v", err)
		return
	}

	bV = b.Value()
	if aG, err = a.Grad(); err != nil {
		t.Errorf("a has no grad: %v", err)
	}

	correctVF64 := softplusf64(v.Data().(float64))
	correctDF64 := sigmoidf64(xV.Data().(float64))
	assert.True(ValueClose(NewF64(correctVF64), yV))
	assert.True(ValueClose(NewF64(correctVF64), bV))
	assert.True(ValueClose(NewF64(correctDF64), xG))
	assert.True(ValueClose(NewF64(correctDF64), aG))

	/* FLOAT32 SCALAR */

	if x, y, a, b, v, err = unaryOpTest(t, Float32, tensor.Shape{}, Softplus); err != nil {
		t.Fatal(err)
	}

	xV = x.Value()
	yV = y.Value()
	if xG, err = x.Grad(); err != nil {
		t.Errorf("x has no grad: %v", err)
		return
	}

	bV = b.Value()
	if aG, err = a.Grad(); err != nil {
		t.Errorf("a has no grad: %v", err)
	}

	correctVF32 := softplusf32(v.Data().(float32))
	correctDF32 := sigmoidf32(xV.Data().(float32))
	assert.True(ValueClose(NewF32(correctVF32), yV))
	assert.True(ValueClose(NewF32(correctVF32), bV))
	assert.True(ValueClose(NewF32(correctDF32), xG))
	assert.True(ValueClose(NewF32(correctDF32), aG))

	/* FLOAT64 Vector */

	if x, y, a, b, v, err = unaryOpTest(t, Float64, tensor.Shape{10}, Softplus); err != nil {
		t.Fatal(err)
	}

	xV = x.Value()
	yV = y.Value()
	if xG, err = x.Grad(); err != nil {
		t.Errorf("x has no grad: %v", err)
		return
	}

	bV = b.Value()
	if aG, err = a.Grad(); err != nil {
		t.Errorf("a has no grad: %v", err)
	}

	correctVF64s := v.Data().([]float64)
	correctDF64s := xV.Data().([]float64)

	for i, v := range correctVF64s {
		correctVF64s[i] = softplusf64(v)
		correctDF64s[i] = sigmoidf64(correctDF64s[i])
	}
	assert.True(floatsEqual64(correctVF64s, yV.Data().([]float64)))
	assert.True(floatsEqual64(correctVF64s, bV.Data().([]float64)))
	assert.True(floatsEqual64(correctDF64s, xG.Data().([]float64)))
	assert.True(floatsEqual64(correctDF64s, aG.Data().([]float64)))

	/* FLOAT32 Vector */

	if x, y, a, b, v, err = unaryOpTest(t, Float32, tensor.Shape{10}, Softplus); err != nil {
		t.Fatal(err)
	}

	xV = x.Value()
	yV = y.Value()
	if xG, err = x.Grad(); err != nil {
		t.Errorf("x has no grad: %v", err)
		return
	}

	bV = b.Value()
	if aG, err = a.Grad(); err != nil {
		t.Errorf("a has no grad: %v", err)
	}

	correctVF32s := v.Data().([]float32)
	correctDF32s := xV.Data().([]float32)

	for i, v := range correctVF32s {
		correctVF32s[i] = softplusf32(v)
		correctDF32s[i] = sigmoidf32(correctDF32s[i])
	}
	assert.True(floatsEqual32(correctVF32s, yV.Data().([]float32)))
	assert.True(floatsEqual32(correctVF32s, bV.Data().([]float32)))
	assert.True(floatsEqual32(correctDF32s, xG.Data().([]float32)))
	assert.True(floatsEqual32(correctDF32s, aG.Data().([]float32)))
}

package gorgonia

// global options

var debugDerives = true
var stabilization = true
var optimizationLevel = 0

// UseStabilization sets the global option to invoke stabilization functions when building the graph.
// Numerical stabilization is on by default
func UseStabilization() {
	stabilization = true
}

// UseNonStable turns off the stabilization functions when building graphs.
func UseNonStable() {
	stabilization = false
}

// DebugDerives turns on the derivation debug option when printing a graph
func DebugDerives() {
	debugDerives = true
}

// DontDebugDerives turns off derivation debug option when printing a graph.
// It is off by default
func DontDebugDerives() {
	debugDerives = false
}

package gorgonia

import (
	"sync"

	"github.com/chewxy/hm"
	"gorgonia.org/tensor"
)

var nodePool = &sync.Pool{
	New: func() interface{} { return new(Node) },
}

func borrowNode() *Node { return nodePool.Get().(*Node) }

func returnNode(n *Node) {
	// if the node is being returned to the pool then it should be removed from the graph that it is linked too as well
	if n.g != nil {
		n.g.RemoveNode(n)
	}

	// zero out any data in the node
	ReturnType(n.t)
	tensor.ReturnInts(n.shape)

	n.t = nil
	n.shape = nil
	n.op = nil
	n.children = nil
	n.name = ""
	n.group = ""
	n.groups = nil
	n.g = nil
	n.boundTo = nil
	n.derivOf = nil
	n.deriv = nil
	n.hash = 0
	n.hashed = false
	n.inferredShape = false
	n.unchanged = false
	n.isStmt = false
	n.ofInterest = false

	nodePool.Put(n)
}

// ReturnNode returns a node to the pool. It does not check that the *Node has been removed from the graph. USE WITH CAUTION.
func ReturnNode(n *Node) {
	n.g = nil
	returnNode(n)
}

// handles Returning of Values

var dvpool = &sync.Pool{
	New: func() interface{} { return new(dualValue) },
}

func borrowDV() *dualValue { return dvpool.Get().(*dualValue) }

func returnDV(dv *dualValue) {
	returnValue(dv.d)
	returnValue(dv.Value)
	// if dvdT, ok := dv.d.(tensor.Tensor); ok {
	// 	returnTensor(dvdT)
	// }
	// if dvvT, ok := dv.Value.(tensor.Tensor); ok {
	// 	returnTensor(dvvT)
	// }

	dv.d = nil
	dv.Value = nil
	dvpool.Put(dv)
}

func returnTensor(t tensor.Tensor) {
	tensor.ReturnTensor(t)
}

func returnValue(v Value) {
	if t, ok := v.(tensor.Tensor); ok {
		returnTensor(t)
	}
}

var dimSizerPool = new(sync.Map)

func borrowDimSizers(size int) []DimSizer {
	var pool *sync.Pool
	p, ok := dimSizerPool.Load(size)

	if !ok {
		s := size
		pool = &sync.Pool{
			New: func() interface{} { return make([]DimSizer, s, s) },
		}
		dimSizerPool.Store(size, pool)
	} else {
		pool = p.(*sync.Pool)
	}
	return pool.Get().([]DimSizer)
}

func returnDimSizers(ds []DimSizer) {
	p, ok := dimSizerPool.Load(cap(ds))
	if !ok {
		return
	}
	pool := p.(*sync.Pool)
	for i := range ds {
		ds[i] = nil
	}
	pool.Put(ds)
}

var tensorTypePool = &sync.Pool{
	New: func() interface{} { return new(TensorType) },
}

func borrowTensorType() *TensorType {
	return tensorTypePool.Get().(*TensorType)
}

func returnTensorType(t *TensorType) {
	switch t {
	case vecF64, vecF32:
		return
	case matF64, matF32:
		return
	case ten3F64, ten3F32:
		return
	}
	t.Of = nil
	t.Dims = 0
	tensorTypePool.Put(t)
}

// ReturnType ...
func ReturnType(t hm.Type) {
	switch tt := t.(type) {
	case *TensorType:
		returnTensorType(tt)
	case TensorType:
		// do nothing
	case tensor.Dtype:
		// do nothing
	case *hm.FunctionType:
		hm.ReturnFnType(tt)
	}
}

