package gorgonia

import (
	"github.com/pkg/errors"
	"gorgonia.org/tensor"
)

var scalarShape = tensor.ScalarShape()

type axes []int
type coordinates []int

// only works for 2D
func transpose2D(shape tensor.Shape) tensor.Shape {
	if len(shape) != 2 {
		return shape
	}
	retVal := tensor.BorrowInts(2)
	retVal[0] = shape[1]
	retVal[1] = shape[0]
	return retVal
}

// for batched matmul
func transposeBatch2D(shape tensor.Shape) tensor.Shape {
	if len(shape) != 3 {
		return shape
	}
	retVal := tensor.BorrowInts(3)
	retVal[0] = shape[0]
	retVal[1] = shape[2]
	retVal[2] = shape[1]
	return retVal
}

// calcBroadcastShape calculates the new shape of a given Node and broadcast axes.
// Note that `a` will be the *Node reshaped to the newShape.
func calcBroadcastShape(a *Node, expectedDims int, broadcastAlong []int) (newShape tensor.Shape) {
	shp := a.Shape()
	if shp.Dims() == expectedDims {
		newShape = shp.Clone()
	} else {
		newShape = make(tensor.Shape, expectedDims)
		for _, i := range broadcastAlong {
			newShape[i] = 1
		}
	}

	switch {
	case a.Shape().Eq(tensor.ScalarShape()):
		for i := range newShape {
			newShape[i] = 1
		}
	case shp.Dims() == expectedDims:
	default:
		for _, s := range a.Shape() {
			// search for first non 0
			for j := range newShape {
				if newShape[j] == 0 {
					newShape[j] = s
					break
				}
			}
		}
	}

	return
}

// KeepDims is a function that ensures that input and output dimensions are the same though the shape may change.
//
// The expandLeft flag in the function indicates if any shape expansion should be done leftwards or rightwards.
// For example, if fn() returns a tensor with a shape (3) and the desired dimension is 2,
// then if `expandLeft` is true the result will be `(1, 3)`. Otherwise the result will be `(3, 1)`.
//
// At the moment, results that turn into scalars cannot have their dimensions kept - the semantics isn't well established yet and is a work in progress.
func KeepDims(a *Node, expandLeft bool, fn func(a *Node) (*Node, error)) (*Node, error) {
	oshape := a.Shape()
	adims := oshape.Dims()
	b, err := fn(a)
	if err != nil {
		return nil, err
	}

	// happy path = quick exit
	newShape := b.Shape()
	if newShape.Eq(oshape) {
		return b, nil
	}

	bdims := newShape.Dims()
	diff := adims - bdims
	if diff < 0 {
		return b, errors.Errorf("Unable to KeepDims for a result with shape %v. It has more dimensions than input %v", newShape, oshape)
	}
	var retShape tensor.Shape
	if expandLeft {
		retShape = tensor.BorrowInts(diff + newShape.Dims())
		for i := 0; i < diff; i++ {
			retShape[i] = 1
		}
		copy(retShape[diff:], newShape)
	} else {
		retShape = newShape.Clone()
		for i := 0; i < diff; i++ {
			retShape = append(retShape, 1)
		}

	}
	return Reshape(b, retShape)
}

package gorgonia_test

import (
	"fmt"

	. "gorgonia.org/gorgonia"
	"gorgonia.org/tensor"
)

func Example_keepDims() {
	g := NewGraph()
	a := NodeFromAny(g, tensor.New(tensor.WithShape(2, 3), tensor.WithBacking([]float64{1, 2, 3, 4, 5, 6})))
	m1, _ := Mean(a, 1)
	m2, _ := KeepDims(a, false, func(a *Node) (*Node, error) { return Mean(a, 1) })
	m3, _ := Mean(a, 0)
	m4, _ := KeepDims(a, true, func(a *Node) (*Node, error) { return Mean(a, 0) })
	m5, _ := KeepDims(a, true, func(a *Node) (*Node, error) { return Mean(a) })

	// these reads are necessary as the VM may feel free to clobber the underlying data.
	// e.g. if m1.Value() is used in the print statement below, the answer will be wrong.
	// This is because before the VM executes the operations, a check is done to see if unsafe
	// operations may be done. Unsafe operations are useful in saving memory.
	// In this example, Reshape can be unsafely done if no other node is "using" m1,
	// so m1.Value() will have its shape clobbered. Thus if m1.Value() is read after the VM has run,
	// there is no guarantee that the data is correct. The only way around this is to "use" m1, by the Read() function.
	var m1v, m2v, m3v, m4v Value
	Read(m1, &m1v)
	Read(m2, &m2v)
	Read(m3, &m3v)
	Read(m4, &m4v)

	vm := NewTapeMachine(g)
	if err := vm.RunAll(); err != nil {
		panic(err)
	}

	fmt.Printf("a:\n%v\n", a.Value())
	fmt.Printf("m1 (shape: %v):\n%v\n", m1.Value().Shape(), m1v)
	fmt.Printf("m2 (shape: %v):\n%v\n", m2.Value().Shape(), m2v)
	fmt.Printf("m3 (shape: %v):\n%v\n", m3.Value().Shape(), m3v)
	fmt.Printf("m4 (shape: %v):\n%v\n", m4.Value().Shape(), m4v)
	fmt.Printf("m5 (shape: %v):\n%v\n", m5.Value().Shape(), m5.Value())

	// Output:
	// a:
	// âŽ¡1  2  3âŽ¤
	// âŽ£4  5  6âŽ¦
	//
	// m1 (shape: (2)):
	// [2  5]
	// m2 (shape: (2, 1)):
	// C[2  5]
	// m3 (shape: (3)):
	// [2.5  3.5  4.5]
	// m4 (shape: (1, 3)):
	// R[2.5  3.5  4.5]
	// m5 (shape: (1, 1)):
	// [[3.5]]

}

package gorgonia

import "gorgonia.org/tensor"

// sli is slice. It's named sli to prevent confusion over naming
type sli struct {
	start, end, step int
}

// S creates a tensor.Slice.
// end is optional. It should be passed in as the first param of the optionals.
// step is optional. It should be passed in as the second param of the optionals.
//
// Default end is start+1. Default step is 1, unless end == step+1, then it defaults to 0
func S(start int, opt ...int) tensor.Slice {
	var end, step int
	if len(opt) > 0 {
		end = opt[0]
	} else {
		end = start + 1
	}

	step = 1
	if len(opt) > 1 {
		step = opt[1]
	} else if end == start+1 {
		step = 0
	}

	return &sli{
		start: start,
		end:   end,
		step:  step,
	}
}

func (s *sli) Start() int { return s.start }
func (s *sli) End() int   { return s.end }
func (s *sli) Step() int  { return s.step }

package gorgonia

import (
	"math"

	"github.com/chewxy/math32"
	"github.com/pkg/errors"
	"gorgonia.org/tensor"
)

// Solver is anything that does gradient updates.
// The name solvers is stolen from Caffe. A much shorter name than GradientUpdaters
type Solver interface {
	Step([]ValueGrad) error
}

// ValueGrad is any type that has a value and a grad. This is used for Solvers
type ValueGrad interface {
	Valuer
	Grad() (Value, error)
}

// Namer is anything that has a name
type Namer interface {
	Name() string
}

func newCachedDV(n ValueGrad, weights, grad Value, zero bool) (cached *dualValue, err error) {
	cached = new(dualValue)
	if cached.Value, err = CloneValue(weights); err != nil {
		if nm, ok := n.(Namer); ok {
			return nil, errors.Wrapf(err, "Failed to clone weights of %v", nm.Name())
		}
		return nil, errors.Wrap(err, "Failed to clone weights")
	}
	if cached.d, err = CloneValue(grad); err != nil {
		if nm, ok := n.(Namer); ok {
			return nil, errors.Wrapf(err, "Failed to clone grad of %v", nm.Name())
		}
		return nil, errors.Wrap(err, "Failed to clone grad")
	}
	if zero {
		cached.Value = ZeroValue(cached.Value)
		cached.d = ZeroValue(cached.d)
	}
	return
}

func extractWeightGrad(n ValueGrad) (weights, grad Value, err error) {
	weights = n.Value()
	if grad, err = n.Grad(); err != nil {
		if nm, ok := n.(Namer); ok {
			return weights, nil, errors.Wrapf(err, "No Grad found for %v", nm.Name())
		}
		return weights, nil, errors.Wrap(err, "No Grad found")
	}
	return
}

// SolverOpt is a function that provides construction options for a Solver
type SolverOpt func(s Solver)

// WithL2Reg adds a L2 regularization parameter to the solver. By default, the solvers do not use any regularization param
func WithL2Reg(l2reg float64) SolverOpt {
	f := func(s Solver) {
		switch st := s.(type) {
		case *RMSPropSolver:
			st.l2reg = l2reg
			st.useL2Reg = true
		case *AdamSolver:
			st.l2reg = l2reg
			st.useL2Reg = true
		case *VanillaSolver:
			st.l2reg = l2reg
			st.useL2Reg = true
		case *Momentum:
			st.l2reg = l2reg
			st.useL2Reg = true
		case *AdamW:
			st.l2reg = l2reg
			st.useL2Reg = true
		}
	}
	return f
}

// WithL1Reg adds a L1 regularization parameter to the solver. By default, the solvers do not use any regularization param
func WithL1Reg(l1reg float64) SolverOpt {
	f := func(s Solver) {
		switch st := s.(type) {
		case *AdamSolver:
			st.l1reg = l1reg
			st.useL1Reg = true
		case *VanillaSolver:
			st.l1reg = l1reg
			st.useL1Reg = true
		case *Momentum:
			st.l1reg = l1reg
			st.useL1Reg = true
		case *AdamW:
			st.l1reg = l1reg
			st.useL1Reg = true
		}
	}
	return f
}

// WithBatchSize sets the batch size for the solver. Currently only Adam and Vanilla (basic SGD) has batch size support
func WithBatchSize(batch float64) SolverOpt {
	f := func(s Solver) {
		switch st := s.(type) {
		case *AdamSolver:
			st.batch = batch
		case *VanillaSolver:
			st.batch = batch
		case *Momentum:
			st.batch = batch
		case *AdamW:
			st.batch = batch
		}
	}
	return f
}

