func TestAdamSolverPrecision(t *testing.T) {
	testCases := []struct {
		desc           string
		learnRate      float64
		inputStart     float32
		inputEnd       float32
		inputIncrement float32
		size           int
		dtype          tensor.Dtype
		expectedOutput interface{}
	}{
		{
			desc:           "Example-float32-1",
			learnRate:      0.1,
			inputStart:     0.0,
			inputEnd:       1.0,
			inputIncrement: 0.1,
			size:           4,
			dtype:          tensor.Float32,
			expectedOutput: []float32{0.18293014, 0.18293014, 0.18293014, 0.18293014},
		},
		{
			desc:           "Example-float64-1",
			learnRate:      0.1,
			inputStart:     0.0,
			inputEnd:       1.0,
			inputIncrement: 0.1,
			size:           8,
			dtype:          tensor.Float64,
			expectedOutput: []float64{0.18293561851374684, 0.18293561851374684, 0.18293561851374684, 0.18293561851374684, 0.18293561851374684, 0.18293561851374684, 0.18293561851374684, 0.18293561851374684},
		},
	}
	for _, tC := range testCases {
		t.Run(tC.desc, func(t *testing.T) {
			c := require.New(t)
			g := NewGraph()

			weights := NewTensor(g, tC.dtype, 2, WithShape(tC.size, 1), WithInit(Ones()), WithName("weights"))
			input := NewTensor(g, tC.dtype, 2, WithShape(1, tC.size), WithName("x"))

			fc := Must(Mul(input, weights))
			loss := Must(Mean(fc))

			_, err := Grad(loss, weights)
			c.NoError(err)

			solver := NewAdamSolver(WithLearnRate(tC.learnRate))
			vm := NewTapeMachine(g, BindDualValues(weights))

			for d := tC.inputStart; d < tC.inputEnd; d += tC.inputIncrement {
				var backing interface{}

				if tC.dtype == tensor.Float32 {
					arr := make([]float32, tC.size)
					for i := range arr {
						arr[i] = float32(d)
					}

					backing = arr
				} else {
					arr := make([]float64, tC.size)
					for i := range arr {
						arr[i] = float64(d)
					}

					backing = arr
				}

				Let(input, tensor.New(
					tensor.WithShape(1, tC.size),
					tensor.WithBacking(backing),
				))
				c.NoError(vm.RunAll())

				c.NoError(solver.Step([]ValueGrad{weights}))

				vm.Reset()
			}

			c.True(dawson.AllClose(tC.expectedOutput, weights.Value().Data()))
		})
	}
}

func TestBarzilaiBorweinSolver(t *testing.T) {
	assert := assert.New(t)

	z, cost, m, err := model2dRosenbrock(1, 100, -0.5, 0.5)
	defer m.Close()
	const costThreshold = 0.00002
	if nil != err {
		t.Fatal(err)
	}

	solver := NewBarzilaiBorweinSolver(WithLearnRate(0.0001))
	iterations := 0
	costFloat := 42.0

	// NOTE: due to precision issues with floating-point arithmetic,
	// amd64 reaches the minimum expected cost at iteration #198
	// arm64 reaches the minimum expected cost at iteration #210
	// In some other cases arm converges faster than amd
	// See https://github.com/golang/go/issues/18354#issuecomment-267705645

	for iterations < 250 {
		m.Reset()
		err = m.RunAll()
		if nil != err {
			t.Fatal(err)
		}

		costFloat = cost.Value().Data().(float64)
		if costThreshold > math.Abs(costFloat) {
			break
		}

		err = solver.Step([]ValueGrad{z})
		if nil != err {
			t.Fatal(err)
		}

		iterations++
	}

	t.Logf("Found minimum cost at iteration %d. arch=%s", iterations, runtime.GOARCH)

	assert.InDelta(0, costFloat, costThreshold)
}

// The Rosenbrock function is a non-convex function,
// which is used as a performance test problem for optimization algorithms.
// https://en.wikipedia.org/wiki/Rosenbrock_function
//
// f(x,y) = (a-x)Â² + b(y-xÂ²)Â²
// It has a global minimum at (x, y) = (a, aÂ²), where f(x,y) = 0.
// Usually a = 1, b = 100, then the minimum is at x = y = 1
// TODO: There is also an n-dimensional version...see wiki
func model2dRosenbrock(a, b, xInit, yInit float64) (z, cost *Node, machine *tapeMachine, err error) {
	g := NewGraph()

	z = NewTensor(g, Float64, 1, WithShape(2), WithName("z"))

	aN := NewConstant(a, WithName("a"))
	bN := NewConstant(b, WithName("b"))

	xProjFloat := []float64{1, 0}
	xProj := NewConstant(tensor.New(tensor.WithBacking(xProjFloat), tensor.WithShape(2)))

	yProjFloat := []float64{0, 1}
	yProj := NewConstant(tensor.New(tensor.WithBacking(yProjFloat), tensor.WithShape(2)))

	x := Must(Mul(z, xProj))
	y := Must(Mul(z, yProj))

	// First term

	sqrt1stTerm := Must(Sub(aN, x))

	firstTerm := Must(Square(sqrt1stTerm))

	// Second term

	xSquared := Must(Square(x))

	yMinusxSquared := Must(Sub(y, xSquared))

	yMinusxSquaredSqu := Must(Square(yMinusxSquared))

	secondTerm := Must(Mul(bN, yMinusxSquaredSqu))

	// cost
	cost = Must(Add(firstTerm, secondTerm))

	dcost, err := Grad(cost, z)
	if nil != err {
		return nil, nil, nil, err
	}

	prog, locMap, err := CompileFunction(g, Nodes{z}, Nodes{cost, dcost[0]})
	if nil != err {
		return nil, nil, nil, err
	}

	machine = NewTapeMachine(g, WithPrecompiled(prog, locMap), BindDualValues(z))

	err = machine.Let(z, tensor.New(tensor.WithBacking([]float64{xInit, yInit}), tensor.WithShape(2)))
	if nil != err {
		return nil, nil, nil, err
	}

	return
}

func model2dSquare(xInit, yInit float64) (z, cost *Node, machine *tapeMachine, err error) {
	g := NewGraph()

	z = NewTensor(g, Float64, 1, WithShape(2), WithName("z"))

	// cost
	cost = Must(Mul(z, z))

	dcost, err := Grad(cost, z)
	if nil != err {
		return nil, nil, nil, err
	}

	prog, locMap, err := CompileFunction(g, Nodes{z}, Nodes{cost, dcost[0]})
	if nil != err {
		return nil, nil, nil, err
	}

	machine = NewTapeMachine(g, WithPrecompiled(prog, locMap), BindDualValues(z))

	err = machine.Let(z, tensor.New(tensor.WithBacking([]float64{xInit, yInit}), tensor.WithShape(2)))
	if nil != err {
		return nil, nil, nil, err
	}

	return
}

package gorgonia

import (
	"github.com/pkg/errors"
	"gorgonia.org/tensor"
)

// this file provides utility functions for solvers

func doL1Reg(w, g tensor.Tensor, l1reg interface{}) (err error) {
	var l1regs tensor.Tensor
	if l1regs, err = tensor.Sign(w); err != nil {
		errors.Wrap(err, signFail)
	}
	if _, err = tensor.Mul(l1reg, l1regs, tensor.WithIncr(g)); err != nil {
		return errors.Wrap(err, pointWiseMulFail)
	}
	defer returnTensor(l1regs)
	return nil
}

func doL2Reg(w, g tensor.Tensor, l2reg interface{}) (err error) {
	if _, err = tensor.Mul(w, l2reg, tensor.WithIncr(g)); err != nil {
		return errors.Wrap(err, pointWiseMulFail)
	}
	return nil
}

func computeRecip(x float64, as tensor.Dtype) (retVal interface{}, err error) {
	switch as {
	case tensor.Float64:
		return 1.0 / x, nil
	case tensor.Float32:
		return float32(1.0) / float32(x), nil
	default:
		return 0.0, errors.Errorf("Unhandled Dtype %v for computeRecip", as)
	}
}

func divBatch(g tensor.Tensor, batch float64) (err error) {
	recip, err := computeRecip(batch, g.Dtype())
	if err != nil {
		return errors.Wrap(err, "In divBatch()")
	}

	_, err = tensor.Mul(g, recip, tensor.UseUnsafe())
	if err != nil {
		return errors.Wrap(err, "Cannot multiply by reciprocal of batch count")
	}
	return nil
}

func clipGrad(g tensor.Tensor, clip, negClip interface{}) (err error) {
	if _, err = tensor.Clamp(g, negClip, clip, tensor.UseUnsafe()); err != nil {
		return errors.Wrap(err, clampFail)
	}
	return nil
}

package gorgonia

import "github.com/pkg/errors"

var unaryOpStabilizationFns = make(map[Ê˜UnaryOperatorType][]func(*Node) (*Node, error))
var binOpStabilizationFns = make(map[Ê˜BinaryOperatorType][]func(*Node, *Node) (*Node, error))

func init() {
	unaryOpStabilizationFns[lnOpType] = []func(*Node) (*Node, error){
		logSigmoidStabilization,
		logStabilization,
		logSoftmaxStabilization,
	}
	binOpStabilizationFns[subOpType] = []func(*Node, *Node) (*Node, error){
		exp1mStabilization,
		oneMinusSigmoidStabilization,
	}
	unaryOpStabilizationFns[log1pOpType] = []func(*Node) (*Node, error){
		log1pExpStabilization,
		log1pNegSigmoidStabilization,
	}
	unaryOpStabilizationFns[negOpType] = []func(*Node) (*Node, error){negNegOptimization}
}

// logStabilization converts
// 	log(1+a) or log(a+1) to log1p(a)
//	log(1-a) to log1p(-a)
// place before log; a should be positive.
func logStabilization(a *Node) (retVal *Node, err error) {
	stabLogf("Stabilizing log(1+a) of %v", a)
	enterLogScope()
	defer leaveLogScope()

	var x *Node
	var aop elemBinOp
	var ok bool

	if aop, ok = a.op.(elemBinOp); !ok {
		return a, noStabilizationErr{}
	}
	input0 := a.children[0]
	input1 := a.children[1]

	stabLogf("input0: %v", input0.Name())
	stabLogf("input1: %v", input1.Name())
	bot := aop.Ê˜BinaryOperator.binOpType()
	switch bot {
	case addOpType:
		if cnst, ok := input0.op.(constant); ok {
			if constEq(cnst, onef32ConstOp) || constEq(cnst, onef64ConstOp) {
				x = input1
				break
			}
		}

		if cnst, ok := input1.op.(constant); ok {
			if constEq(cnst, onef32ConstOp) || constEq(cnst, onef64ConstOp) {
				x = input0
				break
			}
		}

		return a, noStabilizationErr{}
	case subOpType:
		if cnst, ok := input0.op.(constant); !ok || (ok && !constEq(cnst, onef32ConstOp) && !constEq(cnst, onef64ConstOp)) {
			return a, noStabilizationErr{}
		}
		x = input1
	default:
		return a, noStabilizationErr{}
	}

	g := a.g
	g.removeAllEdgesFrom(a) // remove all references
	g.RemoveNode(a)
	defer returnNode(a) // send it back to the pool, since it is literally useless now

	if bot == subOpType {
		if retVal, err = Neg(x); err == nil {
			return Log1p(retVal)
		}
		return nil, errors.Wrap(err, negFail)
	}
	return Log1p(x)
}

// expStabilization converts exp(x)-1 to expm1(x)
// place before sub; i0 should be exp(x); i1 should be 1
func exp1mStabilization(a, b *Node) (retVal *Node, err error) {
	stabLogf("Stabilizing exp(x)-1 to expm1(x) of %v and %v", a, b)
	enterLogScope()
	defer leaveLogScope()

	if cnst, ok := b.op.(constant); !ok || (ok && !constEq(cnst, onef32ConstOp) && !constEq(cnst, onef64ConstOp)) {
		return nil, noStabilizationErr{}
	}

	if euo, ok := a.op.(elemUnaryOp); !ok || euo.unaryOpType() != expOpType {
		return nil, noStabilizationErr{}
	}

	op := newElemUnaryOp(expm1OpType, a.children[0])
	return ApplyOp(op, a.children[0])
}

// oneMinusSigmoidStabilization stabilizes 1-sigmoid(x) by replacing it with sigmoid(-x)
// place before sub
func oneMinusSigmoidStabilization(a, b *Node) (retVal *Node, err error) {
	stabLogf("Stabilizing 1-sigmoid(x) to sigmoid(-x) of %v and %v", a, b)
	enterLogScope()
	defer leaveLogScope()

	if cnst, ok := a.op.(constant); !ok || (ok && !constEq(cnst, onef32ConstOp) && !constEq(cnst, onef64ConstOp)) {
		return nil, noStabilizationErr{}
	}

	if euo, ok := b.op.(elemUnaryOp); !ok || euo.unaryOpType() != sigmoidOpType {
		return nil, noStabilizationErr{}
	}

	x := b.children[0]
	if retVal, err = Neg(x); err == nil {
		return Sigmoid(retVal)
	}
	return nil, errors.Wrap(err, negFail)
}

// logSigmoidStabilization stabilizes log(sigmoid(x)) by replacing it with -softplus(-x)
// place before log; a should be sigmoid(x)
func logSigmoidStabilization(a *Node) (retVal *Node, err error) {
	stabLogf("Stabilizing log sigmoid of %v", a)
	enterLogScope()
	defer leaveLogScope()

	if euo, ok := a.op.(elemUnaryOp); !ok || (ok && euo.unaryOpType() != sigmoidOpType) {
		return a, noStabilizationErr{}
	}

	x := a.children[0]
	stabLogf("x : %v", x.Name())

	if retVal, err = Neg(x); err == nil {
		if retVal, err = Softplus(retVal); err == nil {
			retVal, err = Neg(retVal)
			if err != nil {
				return nil, errors.Wrap(err, negFail)
			}
			return retVal, nil
		}
		return nil, errors.Wrap(err, softplusFail)
	}
	return nil, errors.Wrap(err, negFail)
}

// log1pExpStabilization stabilizes log1p(exp(x)) by substituting it with softplus(x)
// place before log1p; a should be exp(x)
func log1pExpStabilization(a *Node) (retVal *Node, err error) {
	stabLogf("Stabilizing log1p(exp(x)) of %v", a)
	enterLogScope()
	defer leaveLogScope()

	if euo, ok := a.op.(elemUnaryOp); !ok || (ok && euo.unaryOpType() != expOpType) {
		stabLogf("op: %v; %v", a.op, a.children)
		return a, noStabilizationErr{}
	}

	x := a.children[0]
	stabLogf("OKKKKK")
	return Softplus(x)
}

// log1pNegSigmoidStabilization stabilizes log1p(-sigmoid(x)) by substituting it with -softplus(x)
// place before log1p;  a should be -sigmoid(x)
func log1pNegSigmoidStabilization(a *Node) (retVal *Node, err error) {
	stabLogf("Stabilizing log1p(-sigmoid(x)) : %v", a)
	enterLogScope()
	defer leaveLogScope()

	if euo, ok := a.op.(elemUnaryOp); !ok || (ok && euo.unaryOpType() != negOpType) {
		return a, noStabilizationErr{}
	}

	if euo, ok := a.children[0].op.(elemUnaryOp); !ok || (ok && euo.unaryOpType() != sigmoidOpType) {
		return a, noStabilizationErr{}
	}

	x := a.children[0].children[0]

	stabLogf("x : %v", x.Name())

	if retVal, err = Softplus(x); err == nil {
		retVal, err = Neg(retVal)
		if err != nil {
			return nil, errors.Wrap(err, negFail)
		}
		return retVal, nil
	}
	return nil, errors.Wrap(err, softplusFail)
}

// logSoftmaxStabilization converts
// 	log(softmax(a)) to softmax{isLog: true}(a)
//	log(a * softmax(b)) to log(a) + softmax{isLog: true}(b)
func logSoftmaxStabilization(a *Node) (retVal *Node, err error) {
	stabLogf("Stabilizing log(softmax) of %v", a)
	enterLogScope()
	defer leaveLogScope()

	switch op := a.op.(type) {
	case *softmaxOp:
		op.isLog = true
		return a, nil
	case elemBinOp:
		if op.Ê˜BinaryOperator.binOpType() == mulOpType {
			fst := a.children[0]
			snd := a.children[1]

			var hasSm bool
			var smop1, smop2 *softmaxOp
			if smop, ok := fst.op.(*softmaxOp); ok {
				hasSm = true
				smop1 = smop
			}
			if smop, ok := snd.op.(*softmaxOp); ok {
				hasSm = true
				smop2 = smop
			}

			if hasSm {
				var newFst, newSnd *Node
				switch {
				case smop1 != nil && smop2 == nil:
					smop1.isLog = true
					newFst = fst
					if newSnd, err = Log(snd); err != nil {
						return nil, err
					}
				case smop1 == nil && smop2 != nil:
					smop2.isLog = true
					newSnd = snd
					if newFst, err = Log(fst); err != nil {
						return nil, err
					}
				case smop1 != nil && smop2 != nil:
					smop1.isLog = true
					smop2.isLog = true
					newFst = fst
					newSnd = snd
				default:
					return a, noStabilizationErr{}
				}

				// g := a.g
				// g.removeAllEdgesFrom(a) // remove all references
				// g.RemoveNode(a)
				// returnNode(a) // send it back to the pool, since it is literally useless now
				return Add(newFst, newSnd)
			}

		}
	}
	return a, noStabilizationErr{}

}

/* Graph Optimizations */

// negNegOptimization optimizes away -(-x) to just return x
// place before neg
func negNegOptimization(a *Node) (retVal *Node, err error) {
	stabLogf("Optimizing -(-x)")
	enterLogScope()
	defer leaveLogScope()

	if euo, ok := a.op.(elemUnaryOp); !ok || (ok && euo.unaryOpType() != negOpType) {
		return a, noStabilizationErr{}
	}

	x := a.children[0]
	return x, nil
}

package gorgonia

import (
	"io/ioutil"
	"testing"
)

func TestLogStabilization(t *testing.T) {
	g := NewGraph()

	// log(a+1)
	x := NewVector(g, Float64, WithName("x"), WithShape(2))
	p := Must(Add(x, onef64))
	lp := Must(Log(p))
	if lp.children[0] != x {
		t.Error("Oops.")
		ioutil.WriteFile("log(a+1).dot", []byte(lp.ToDot()), 0644)
	}

	// log(1+a)
	p = Must(Add(onef64, x))
	lp = Must(Log(p))
	if lp.children[0] != x {
		t.Error("Oops.")
		ioutil.WriteFile("log(1+a).dot", []byte(lp.ToDot()), 0644)
	}

	//log(1-a)
	m := Must(Sub(onef64, x))
	lp = Must(Log(m))
	if euo, ok := lp.children[0].op.(elemUnaryOp); !ok {
		t.Error("Oops.")
	} else {
		if euo.unaryOpType() != negOpType {
			t.Error("Expected Neg Op")
		}

		if lp.children[0].children[0] != x {
			t.Error("Oops.")
		}
	}

	if t.Failed() {
		ioutil.WriteFile("log(1-a).dot", []byte(lp.ToDot()), 0644)
	}

	//log(a-1)
	m = Must(Sub(x, onef64))
	lp = Must(Log(m))
	//TODO: surely there is a better way to test?
	if lp.children[0] == x {
		t.Error("Oops.")
	}

	// log(a+2)
	// We expect to keep the same operation tree, without stabilization
	p = Must(Add(x, twof64))
	lp = Must(Log(p))
	if lp.children[0] != p {
		t.Error("Oops.")
		ioutil.WriteFile("log(a+2).dot", []byte(lp.ToDot()), 0644)
	}
}

func TestExpStabilization(t *testing.T) {
	g := NewGraph()

	x := NewVector(g, Float64, WithName("x"), WithShape(2))
	e := Must(Exp(x))
	s := Must(Sub(e, onef64))

	if s.children[0] != x {
		t.Error("oops")
	}

	if euo, ok := s.op.(elemUnaryOp); !ok || (ok && euo.unaryOpType() != expm1OpType) {
		t.Error("oops")
	}

	if t.Failed() {
		ioutil.WriteFile("exp(a)-1.dot", []byte(s.ToDot()), 0644)
	}
}

func TestLogSigmoidStabilization(t *testing.T) {
	g := NewGraph()

	stabilization = true
	x := NewVector(g, Float64, WithName("x"), WithShape(2))
	y := Must(Sigmoid(x))
	WithName("y")(y)
	logY := Must(Log(y))
	WithName("log(sigmoid(x))")(logY)

	if euo, ok := logY.op.(elemUnaryOp); !ok || (ok && euo.unaryOpType() != negOpType) {
		t.Error("Oops")
	}

	if euo, ok := logY.children[0].op.(elemUnaryOp); !ok || (ok && euo.unaryOpType() != softplusOpType) {
		t.Error("Oops2")
	}

	if euo, ok := logY.children[0].children[0].op.(elemUnaryOp); !ok || (ok && euo.unaryOpType() != negOpType) {
		t.Error("Oops3")
	}

	if logY.children[0].children[0].children[0] != x {
		t.Errorf("Oops4: %v", logY.children[0].children[0].children[0].Name())
	}

	if t.Failed() {
		ioutil.WriteFile("fullGraph.dot", []byte(g.ToDot()), 0644)
		ioutil.WriteFile("logY.dot", []byte(logY.ToDot()), 0644)
	}
}

package gorgonia

import (
	"fmt"
	"strings"
	"text/template"
)

const exprNodeTemplText = `<
<TABLE BORDER="0" CELLBORDER="1" CELLSPACING="0" PORT="anchor" {{if isLeaf .}} COLOR="#00FF00;"{{else if isRoot . }} COLOR="#FF0000;" {{else if isMarked .}} COLOR="#0000FF;" {{end}}{{if isInput .}} BGCOLOR="lightyellow"{{else if isStmt .}} BGCOLOR="lightblue"{{end}}>

<TR><TD>{{printf "%x" .ID}}</TD><TD>{{printf "%v" .Name | html | dotEscape}} :: {{nodeType . | html | dotEscape }}</TD></TR>
{{if printOp . }}<TR><TD>Op</TD><TD>{{ opStr . | html | dotEscape }} :: {{ opType . | html | dotEscape }}</TD></TR>{{end}}
{{if hasShape .}}<TR><TD>Shape</TD><TD>{{ getShape .}}</TD></TR>{{end}}
<TR><TD>Overwrites Input {{overwritesInput . }}</TD><TD>Data On: {{.Device}}</TD></TR>
{{if hasGrad .}}<TR><TD>Value</TD><TD>Grad</TD></TR>
<TR><TD>{{printf "%+3.3s" .Value | dotEscape}}</TD><TD>{{getGrad . | dotEscape }} </TD></TR>
<TR><TD>Ptr: {{getValPtr . | dotEscape}} </TD><TD>Ptr: {{getGradPtr . | dotEscape}} </TD></TR>
{{else}}
<TR><TD>Value</TD><TD>{{printf "%+3.3s" .Value | dotEscape}}</TD></TR>
{{end}}

</TABLE>
>`

func dotEscape(s string) string {
	s = strings.Replace(s, "\n", "<BR />", -1)
	s = strings.Replace(s, "<nil>", "NIL", -1)
	return s
}

func printOp(n *Node) bool  { return n.op != nil && !n.isStmt }
func isLeaf(n *Node) bool   { return len(n.children) == 0 }
func isInput(n *Node) bool  { return n.isInput() }
func isMarked(n *Node) bool { return n.ofInterest }
func isRoot(n *Node) bool   { return n.isRoot() }
func isStmt(n *Node) bool   { return n.isStmt }
func hasShape(n *Node) bool { return n.shape != nil }
func hasGrad(n *Node) bool  { _, err := n.Grad(); return err == nil }
func opStr(n *Node) string  { return n.op.String() }
func opType(n *Node) string { return n.op.Type().String() }

func nodeType(n *Node) string {
	if n.t == nil {
		return "NIL"
	}
	return n.t.String()
}

func overwritesInput(n *Node) int {
	if n.op == nil {
		return -1
	}
	return n.op.OverwritesInput()
}

func getShape(n *Node) string {
	if !n.inferredShape {
		return fmt.Sprintf("%v", n.shape)
	}
	return fmt.Sprintf("<U>%v</U>", n.shape) // graphviz 2.38+ only supports <O>
}

func getGrad(n *Node) string {
	grad, err := n.Grad()
	if err == nil {
		return fmt.Sprintf("%+3.3s", grad)
	}
	return ""
}

func getGradPtr(n *Node) string {
	grad, err := n.Grad()
	if err == nil && grad != nil {
		return fmt.Sprintf("0x%x", grad.Uintptr())
	}
	return ""
}

func getValPtr(n *Node) string {
	if n.Value() == nil {
		return "<nil>"
	}
	return fmt.Sprintf("0x%dx", n.Value().Uintptr())
}

var funcMap = template.FuncMap{
	"dotEscape":       dotEscape,
	"printOp":         printOp,
	"isRoot":          isRoot,
	"isLeaf":          isLeaf,
	"isInput":         isInput,
	"isStmt":          isStmt,
	"isMarked":        isMarked,
	"hasShape":        hasShape,
	"hasGrad":         hasGrad,
	"getShape":        getShape,
	"getValPtr":       getValPtr,
	"getGrad":         getGrad,
	"getGradPtr":      getGradPtr,
	"overwritesInput": overwritesInput,
	"opStr":           opStr,
	"opType":          opType,
	"nodeType":        nodeType,
}

var (
	exprNodeTempl     *template.Template
	exprNodeJSONTempl *template.Template
)

func init() {
	exprNodeTempl = template.Must(template.New("node").Funcs(funcMap).Parse(exprNodeTemplText))
}

