type assertState struct {
	*assert.Assertions
	cont bool
}

func newAssertState(a *assert.Assertions) *assertState { return &assertState{a, true} }

func (a *assertState) Equal(expected interface{}, actual interface{}, msgAndArgs ...interface{}) {
	if !a.cont {
		return
	}
	a.cont = a.Assertions.Equal(expected, actual, msgAndArgs...)
}

func (a *assertState) True(value bool, msgAndArgs ...interface{}) {
	if !a.cont {
		return
	}
	a.cont = a.Assertions.True(value, msgAndArgs...)
}

func checkErr(t *testing.T, expected bool, err error, name string, id interface{}) (cont bool) {
	switch {
	case expected:
		if err == nil {
			t.Errorf("Expected error in test %v (%v)", name, id)
		}
		return true
	case !expected && err != nil:
		t.Errorf("Test %v (%v) errored: %+v", name, id, err)
		return true
	}
	return false
}

func deepNodeEq(a, b *Node) bool {
	if a == b {
		return true
	}

	if a.isInput() {
		if !b.isInput() {
			return false
		}

		if a.name != b.name {
			return false
		}
		if !ValueEq(a.boundTo, b.boundTo) {
			return false
		}
		return true
	}

	if b.isInput() {
		return false
	}

	if a.name != b.name {
		return false
	}

	if a.group != b.group {
		return false
	}

	if a.id != b.id {
		return false
	}

	if a.hash != b.hash {
		return false
	}

	if a.hashed != b.hashed {
		return false
	}

	if a.inferredShape != b.inferredShape {
		return false
	}

	if a.unchanged != b.unchanged {
		return false
	}

	if a.isStmt != b.isStmt {
		return false
	}

	if a.ofInterest != b.ofInterest {
		return false
	}

	if a.dataOn != b.dataOn {
		return false
	}

	if !a.t.Eq(b.t) {
		return false
	}
	if !a.shape.Eq(b.shape) {
		return false
	}

	if a.op.Hashcode() != b.op.Hashcode() {
		return false
	}

	if !ValueEq(a.boundTo, b.boundTo) {
		return false
	}

	if len(a.children) != len(b.children) {
		return false
	}

	if len(a.derivOf) != len(b.derivOf) {
		return false
	}

	if a.deriv != nil {
		if b.deriv == nil {
			return false
		}
		if a.deriv.Hashcode() != b.deriv.Hashcode() {
			return false
		}
	}

	for i, c := range a.children {
		if c.Hashcode() != b.children[i].Hashcode() {
			return false
		}
	}

	for i, c := range a.derivOf {
		if c.Hashcode() != b.derivOf[i].Hashcode() {
			return false
		}
	}
	return true
}

