func Test_tapeMachine_Reset(t *testing.T) {
	g := NewGraph()

	var x, y, z *Node
	var err error

	// define the expression
	x = NewScalar(g, Float64, WithName("x"))
	y = NewScalar(g, Float64, WithName("y"))
	if z, err = Add(x, y); err != nil {
		t.Fatal(err)
	}

	// create a VM to run the program on
	m1 := NewTapeMachine(g)
	m2 := NewTapeMachine(g)
	defer m1.Close()
	defer m2.Close()

	// set initial values then run
	Let(x, 2.0)
	Let(y, 2.5)
	if err = m1.RunAll(); err != nil {
		t.Fatal(err)
	}
	if z.Value().Data().(float64) != 4.5 {
		t.Fatalf("Expected %v, got %v", 4.5, z.Value())
	}
	m1.Reset()
	if !reflect.DeepEqual(m1.locMap, m2.locMap) {
		t.Fatalf("expected locmap\n\n%#v, got\n\n%#v", m1, m2)
	}
	if !reflect.DeepEqual(m1.p, m2.p) {
		t.Fatalf("expected program\n\n%#v, got\n\n%#v", m1, m2)
	}
	if !reflect.DeepEqual(m1.cpumem, m2.cpumem) {
		t.Fatalf("expected cpumem\n\n%#v, got\n\n%#v", m1, m2)
	}
	if !reflect.DeepEqual(m1.gpumem, m2.gpumem) {
		t.Fatalf("expected gpumem\n\n%#v, got\n\n%#v", m1, m2)
	}
	if !reflect.DeepEqual(m1.pc, m2.pc) {
		t.Fatalf("expected pc\n\n%#v, got\n\n%#v", m1, m2)
	}
}

func Test_tapeMachineEvalMode(t *testing.T) {
	c := require.New(t)

	g := NewGraph()

	x := NewTensor(g, Float32, 2, WithShape(3, 2), WithInit(GlorotN(1)), WithName("x"))
	scale := NewTensor(g, Float32, 2, WithShape(1, 2), WithInit(GlorotN(1)), WithName("scale"))
	bias := NewTensor(g, Float32, 2, WithShape(1, 2), WithInit(GlorotN(1)), WithName("bias"))

	y, _, _, op, err := BatchNorm(x, scale, bias, 0.9, 1e-5)
	c.NoError(err)

	op.SetTraining(true)

	var yVal, scaleVal Value
	Read(y, &yVal)
	Read(scale, &scaleVal)

	cost, _ := Mean(y)

	if _, err := Grad(cost, x, scale, bias); err != nil {
		t.Fatal(err)
	}

	trainVM := NewTapeMachine(g, BindDualValues(x, scale, bias), TraceExec())

	err = trainVM.RunAll()
	c.NoError(err)

	evalVM := NewTapeMachine(g, EvalMode())
	err = evalVM.RunAll()
	c.NoError(err)
}

type faultyOp struct{}

// Arity returns 1
func (i faultyOp) Arity() int { return 2 }

func (i faultyOp) Type() hm.Type {
	a := hm.TypeVariable('a')

	return hm.NewFnType(a, a, a)
}

func (i faultyOp) InferShape(ds ...DimSizer) (tensor.Shape, error) { return ds[0].(tensor.Shape), nil }

func (i faultyOp) Do(vs ...Value) (Value, error) {
	return vs[0], nil
}

func (i faultyOp) ReturnsPtr() bool      { return true }
func (i faultyOp) CallsExtern() bool     { return false }
func (i faultyOp) OverwritesInput() int  { return -1 }
func (i faultyOp) WriteHash(h hash.Hash) { fmt.Fprintf(h, "I") }

func (i faultyOp) Hashcode() uint32 {
	h := fnv.New32a()
	i.WriteHash(h)
	return h.Sum32()
}

func (i faultyOp) String() string { return "FaultyOp" }

func Test_tapeMachinePointerWatchOk(t *testing.T) {
	var err error

	c := require.New(t)

	g := NewGraph()

	a := NewTensor(g, Float32, 2, WithShape(2, 2), WithInit(GlorotN(1)), WithName("a"))

	out := Must(Mul(a, a))
	out = Must(Mul(a, out))
	out = Must(Mul(a, out))

	trainVM := NewTapeMachine(g, WithPointerWatch())

	for i := 0; i < 3; i++ {
		err = trainVM.RunAll()
		c.NoError(err)
	}
}

func Test_tapeMachinePointerWatchFail(t *testing.T) {
	var err error

	c := require.New(t)

	g := NewGraph()

	ts := tensor.New(tensor.WithShape(2, 2), tensor.WithBacking([]float32{1, 2, 3, 4}))

	a := NewTensor(g, Float32, 2, WithShape(2, 2), WithValue(ts), WithName("a"))
	b := NewTensor(g, Float32, 2, WithShape(2, 2), WithValue(ts), WithName("b"))

	prod := Must(HadamardProd(a, b))
	out := Must(ApplyOp(&faultyOp{}, prod, b))

	trainVM := NewTapeMachine(g, WithPointerWatch())
	err = trainVM.RunAll()
	c.Error(err)
	t.Logf("error: %v", err)

	// in this test, prod and out both have the same value which is not right
	t.Logf("prod: %v", prod.Value())
	t.Logf("out: %v", out.Value())

	c.True(strings.Contains(err.Error(), "Pointer clash found in value."))
}

package gorgonia

import (
	"sort"

	"github.com/pkg/errors"
	"gonum.org/v1/gonum/graph"
	"gonum.org/v1/gonum/graph/iterator"
	"gonum.org/v1/gonum/graph/topo"
)

// WalkGraph walks a graph. It returns a channel of *Nodes, so be sure to consume the channel or there may be a deadlock
func WalkGraph(start *Node) <-chan *Node {
	ch := make(chan *Node)
	walked := NewNodeSet()

	go func() {
		walkGraph(start, ch, walked)
		close(ch)
	}()
	return ch
}

func walkGraph(start *Node, ch chan *Node, walked NodeSet) {
	defer func() {
		walked.Add(start)
	}()
	if _, ok := walked[start]; ok {
		return // walked before
	}

	ch <- start

	for _, child := range start.children {
		walkGraph(child, ch, walked)
	}

}

// Sort topologically sorts a ExprGraph: root of graph will be first
// nodes are sorted using gonum's SortStabilized function.
//
// see https://godoc.org/gonum.org/v1/gonum/graph/topo#SortStabilized for more info
func Sort(g *ExprGraph) (sorted Nodes, err error) {
	var sortedNodes []graph.Node
	// if sortedNodes, err = topo.Sort(g); err != nil {
	if sortedNodes, err = topo.SortStabilized(g, reverseLexical); err != nil {
		return nil, errors.Wrap(err, sortFail)
	}

	sorted = graphNodeToNode(iterator.NewOrderedNodes(sortedNodes))
	return
}

// UnstableSort performs a topological sort of the directed graph g returning the 'from' to 'to'
// sort order. If a topological ordering is not possible, an Unorderable error is returned
// listing cyclic components in g with each cyclic component's members sorted by ID. When
// an Unorderable error is returned, each cyclic component's topological position within
// the sorted nodes is marked with a nil graph.Node.
func UnstableSort(g *ExprGraph) (sorted Nodes, err error) {
	var sortedNodes []graph.Node
	if sortedNodes, err = topo.Sort(g); err != nil {
		return nil, errors.Wrap(err, sortFail)
	}

	sorted = graphNodeToNode(iterator.NewOrderedNodes(sortedNodes))
	return
}

func reverseNodes(sorted Nodes) {
	for i := len(sorted)/2 - 1; i >= 0; i-- {
		j := len(sorted) - i - 1
		sorted[i], sorted[j] = sorted[j], sorted[i]
	}
}

type byID []graph.Node

func (ns byID) Len() int           { return len(ns) }
func (ns byID) Less(i, j int) bool { return ns[i].ID() > ns[j].ID() }
func (ns byID) Swap(i, j int)      { ns[i], ns[j] = ns[j], ns[i] }

func reverseLexical(a []graph.Node) {
	sort.Sort(byID(a))
}

package gorgonia

import (
	"fmt"
	"math"
	"reflect"
	"time"

	rng "github.com/leesper/go_rng"
	"github.com/pkg/errors"
	"gorgonia.org/tensor"
)

// This file provides several weight initialization utility functions.
// It uses the rng package by leesper

// InitWFn is a type of helper function to help initialize weights vector/matrices.
// It generates the backing required for the tensors.
//
// It's typically used in closures
type InitWFn func(dt tensor.Dtype, s ...int) interface{}

// Zeroes creates an InitWfn that populates a Value with... zeroes. I don't know what you expected.
func Zeroes() InitWFn {
	f := func(dt tensor.Dtype, s ...int) interface{} {
		size := tensor.Shape(s).TotalSize()
		switch dt {
		case tensor.Float64:
			return make([]float64, size)
		case tensor.Float32:
			return make([]float32, size)
		case tensor.Int:
			return make([]int, size)
		default:
			return reflect.MakeSlice(reflect.SliceOf(dt.Type), size, size).Interface()
		}
	}
	return f
}

// Ones creates an InitWfn that populates a Value with ones. See Zeroes() for more explanation.
func Ones() InitWFn {
	return func(dt tensor.Dtype, s ...int) interface{} { return ones(dt, s...).Data() }
}

// RangedFrom creates an InitWFn that populates a Value starting with the provided start, increamenting the number for each element in the value by 1
func RangedFrom(start int) InitWFn {
	f := func(dt tensor.Dtype, s ...int) interface{} {
		size := tensor.Shape(s).TotalSize()
		return tensor.Range(dt, start, start+size)
	}
	return f
}

// RangedFromWithStep creates an InitWFn that populates a value starting with the provided start, and incrementing the number for each element by the provided increment.
func RangedFromWithStep(start, increment interface{}) InitWFn {
	f := func(dt tensor.Dtype, s ...int) interface{} {
		totalSize := tensor.Shape(s).TotalSize()

		switch dt {
		case tensor.Float64:
			// for convenience
			var st, incr float64
			switch s := start.(type) {
			case float64:
				st = s
			case int:
				st = float64(s)
			default:
				panic(fmt.Sprintf("Cannot use `start`(%v of %T) in RangedFromWithStep in a Float64 tensor", start, start))
			}
			switch i := increment.(type) {
			case float64:
				incr = i
			case int:
				incr = float64(i)
			default:
				panic(fmt.Sprintf("Cannot use `increment`(%v of %T) in RangedFromWithStep in a Float64 tensor", increment, increment))
			}

			result := make([]float64, totalSize)
			for i := 0; i < totalSize; i++ {
				result[i] = st
				st += incr
			}
			return result
		case tensor.Float32:
			// for convenience, because when you write literals with decimal point in Go it will be converted to float64
			var st, incr float32
			switch s := start.(type) {
			case float32:
				st = s
			case float64:
				st = float32(s)
			case int:
				st = float32(s)
			default:
				panic(fmt.Sprintf("Cannot use `start`(%v of %T) in RangedFromWithStep in a Float32 tensor", start, start))
			}

			switch i := increment.(type) {
			case float32:
				incr = i
			case float64:
				incr = float32(i)
			case int:
				incr = float32(i)
			default:
				panic(fmt.Sprintf("Cannot use `increment`(%v of %T) in RangedFromWithStep in a Float32 tensor", increment, increment))
			}

			result := make([]float32, totalSize)
			for i := 0; i < totalSize; i++ {
				result[i] = st
				st += incr
			}
			return result
		case tensor.Int:
			st := start.(int)
			incr := increment.(int)
			result := make([]int, totalSize)
			for i := 0; i < totalSize; i++ {
				result[i] = st
				st += incr
			}
			return result
		default:
			panic(fmt.Sprintf("Dtype %v not yet supported for RangedFromWithStep. Please put a pull request in to support this function", dt))
		}

	}
	return f
}

// ValuesOf creates an InitWrn that populates a value with val. This function will cause a panic if val's type is incompatible with the values type.
func ValuesOf(val interface{}) InitWFn {
	f := func(dt tensor.Dtype, s ...int) interface{} {
		size := tensor.Shape(s).TotalSize()

		switch dt {
		case tensor.Float64:
			v := val.(float64)
			retVal := make([]float64, size)
			for i := range retVal {
				retVal[i] = v
			}
			return retVal
		case tensor.Float32:
			v := val.(float32)
			retVal := make([]float32, size)
			for i := range retVal {
				retVal[i] = v
			}
			return retVal
		case tensor.Int:
			v := val.(int)
			retVal := make([]int, size)
			for i := range retVal {
				retVal[i] = v
			}
			return retVal
		default:
			err := errors.Errorf(nyiTypeFail, "Zeroes", dt)
			panic(err)
		}
	}
	return f
}

