package main

import (
	"fmt"
	"os"
	"os/exec"
	"sort"
	"strings"
)

// A Dependency is a specific revision of a package.
type Dependency struct {
	ImportPath string
	Comment    string `json:",omitempty"` // Description of commit, if present.
	Rev        string // VCS-specific commit ID.

	// used by command save & update
	ws   string // workspace
	root string // import path to repo root
	dir  string // full path to package

	// used by command update
	matched bool // selected for update by command line
	pkg     *Package
	missing bool // packages is missing

	// used by command go
	vcs *VCS
}

func eqDeps(a, b []Dependency) bool {
	ok := true
	for _, da := range a {
		for _, db := range b {
			if da.ImportPath == db.ImportPath && da.Rev != db.Rev {
				ok = false
			}
		}
	}
	return ok
}

// containsPathPrefix returns whether any string in a
// is s or a directory containing s.
// For example, pattern ["a"] matches "a" and "a/b"
// (but not "ab").
func containsPathPrefix(pats []string, s string) bool {
	for _, pat := range pats {
		if pat == s || strings.HasPrefix(s, pat+"/") {
			return true
		}
	}
	return false
}

func uniq(a []string) []string {
	var s string
	var i int
	if !sort.StringsAreSorted(a) {
		sort.Strings(a)
	}
	for _, t := range a {
		if t != s {
			a[i] = t
			i++
			s = t
		}
	}
	return a[:i]
}

// trimGoVersion and return the major version
func trimGoVersion(version string) (string, error) {
	if version == "devel" {
		return "devel", nil
	}
	if strings.HasPrefix(version, "devel+") || strings.HasPrefix(version, "devel-") {
		return strings.Replace(version, "devel+", "devel-", 1), nil
	}
	p := strings.Split(version, ".")
	if len(p) < 2 {
		return "", fmt.Errorf("Error determining major go version from: %q", version)
	}
	var split string
	switch {
	case strings.Contains(p[1], "beta"):
		split = "beta"
	case strings.Contains(p[1], "rc"):
		split = "rc"
	}
	if split != "" {
		p[1] = strings.Split(p[1], split)[0]
	}
	return p[0] + "." + p[1], nil
}

var goVersionTestOutput = ""

func getGoVersion() (string, error) {
	// For testing purposes only
	if goVersionTestOutput != "" {
		return goVersionTestOutput, nil
	}

	// Godep might have been compiled with a different
	// version, so we can't just use runtime.Version here.
	cmd := exec.Command("go", "version")
	cmd.Stderr = os.Stderr
	out, err := cmd.Output()
	return string(out), err
}

// goVersion returns the major version string of the Go compiler
// currently installed, e.g. "go1.5".
func goVersion() (string, error) {
	out, err := getGoVersion()
	if err != nil {
		return "", err
	}
	gv := strings.Split(out, " ")
	if len(gv) < 4 {
		return "", fmt.Errorf("Error splitting output of `go version`: Expected 4 or more elements, but there are < 4: %q", out)
	}
	if gv[2] == "devel" {
		return trimGoVersion(gv[2] + gv[3])
	}
	return trimGoVersion(gv[2])
}

package main

import "testing"

func TestTrimGoVersion(t *testing.T) {
	var cases = []struct {
		in, out string
		err     bool
	}{
		{in: "go1.5", out: "go1.5", err: false},
		{in: "go1.5beta1", out: "go1.5", err: false},
		{in: "go1.6rc1", out: "go1.6", err: false},
		{in: "go1.5.1", out: "go1.5", err: false},
		{in: "devel", out: "devel", err: false},
		{in: "devel+15f7a66", out: "devel-15f7a66", err: false},
		{in: "devel-15f7a66", out: "devel-15f7a66", err: false},
		{in: "boom", out: "", err: true},
	}

	for _, c := range cases {
		mv, err := trimGoVersion(c.in)
		if err != nil && !c.err {
			t.Errorf("Unexpected error: %s", err)
		}
		if mv != c.out {
			t.Errorf("Expected trimGoVersion(%s) == '%s', but got '%s'", c.in, c.out, mv)
		}
	}
}

func TestGoVersion(t *testing.T) {
	var cases = []struct {
		o, r string
		err  bool
	}{
		{o: "go version go1.6.2 darwin/amd64", r: "go1.6", err: false},
		{o: "go version go1.6 darwin/amd64", r: "go1.6", err: false},
		{o: "go version go1.6.2 linux/amd64", r: "go1.6", err: false},
		{o: "go version devel +da6205b Wed Apr 13 17:22:38 2016 +0000 darwin/amd64", r: "devel-da6205b", err: false},
	}

	for _, c := range cases {
		goVersionTestOutput = c.o
		v, err := goVersion()
		if err != nil && !c.err {
			t.Errorf("Unexpected error: %s", err)
		}
		if v != c.r {
			t.Errorf("Expected goVersion() == '%s', but got '%s'", c.r, v)
		}
		goVersionTestOutput = ""
	}
}

package main

import (
	"bytes"
	"fmt"
	"log"

	"github.com/pmezard/go-difflib/difflib"
)

var cmdDiff = &Command{
	Name:  "diff",
	Short: "shows the diff between current and previously saved set of dependencies",
	Long: `
Shows the difference, in a unified diff format, between the
current set of dependencies and those generated on a
previous 'go save' execution.
`,
	Run:          runDiff,
	OnlyInGOPATH: true,
}

func runDiff(cmd *Command, args []string) {
	gold, err := loadDefaultGodepsFile()
	if err != nil {
		log.Fatalln(err)
	}

	pkgs := []string{"."}
	dot, err := LoadPackages(pkgs...)
	if err != nil {
		log.Fatalln(err)
	}

	gnew := &Godeps{
		ImportPath: dot[0].ImportPath,
		GoVersion:  gold.GoVersion,
	}

	err = gnew.fill(dot, dot[0].ImportPath)
	if err != nil {
		log.Fatalln(err)
	}

	diff, err := diffStr(&gold, gnew)
	if err != nil {
		log.Fatalln(err)
	}
	fmt.Println(diff)
}

// diffStr returns a unified diff string of two Godeps.
func diffStr(a, b *Godeps) (string, error) {
	var ab, bb bytes.Buffer

	_, err := a.writeTo(&ab)
	if err != nil {
		log.Fatalln(err)
	}

	_, err = b.writeTo(&bb)
	if err != nil {
		log.Fatalln(err)
	}

	diff := difflib.UnifiedDiff{
		A:        difflib.SplitLines(ab.String()),
		B:        difflib.SplitLines(bb.String()),
		FromFile: b.file(),
		ToFile:   "$GOPATH",
		Context:  10,
	}
	return difflib.GetUnifiedDiffString(diff)
}

package main

import (
	"fmt"
	"strings"
	"testing"
)

var (
	d1 = fmt.Sprintf(`--- Godeps/Godeps.json
+++ $GOPATH
@@ -1,13 +1,13 @@
 {
        "ImportPath": "C",
        "GoVersion": "go1.2",
        "GodepVersion": "v%d",
        "Deps": [
                {
                        "ImportPath": "D101",
-                       "Comment": "D202",
+                       "Comment": "D303",
                        "Rev": ""
                }
        ]
 }
`, version)

	d2 = fmt.Sprintf(`--- Godeps/Godeps.json
+++ $GOPATH
@@ -1,13 +1,18 @@
 {
        "ImportPath": "C",
        "GoVersion": "go1.2",
        "GodepVersion": "v%d",
        "Deps": [
                {
                        "ImportPath": "D101",
                        "Comment": "D202",
                        "Rev": ""
+               },
+               {
+                       "ImportPath": "D102",
+                       "Comment": "D203",
+                       "Rev": ""
                }
        ]
 }
`, version)
)

var (
	dep1 = Godeps{
		ImportPath: "C",
		GoVersion:  "go1.2",
		Deps: []Dependency{
			{ImportPath: "D101", Comment: "D202"},
		},
	}

	dep2 = Godeps{
		ImportPath: "C",
		GoVersion:  "go1.2",
		Deps: []Dependency{
			{ImportPath: "D101", Comment: "D202"},
		},
	}
)

func TestDiff(t *testing.T) {
	// Equiv Godeps, should yield an empty diff.
	diff, _ := diffStr(&dep1, &dep2)
	if diff != "" {
		t.Errorf("Diff is %v want ''", diff)
	}

	// Test modifications in packages make it to the diff.
	dep2.Deps[0].Comment = "D303"
	diff, _ = diffStr(&dep1, &dep2)
	if !diffsEqual(strings.Fields(diff), strings.Fields(d1)) {
		t.Errorf("Expecting diffs to be equal. Obs <%s>. Exp <%s>", diff, d1)
	}

	// Test additional packages in new Godeps
	dep2.Deps[0].Comment = "D202"
	dep2.Deps = append(dep2.Deps, Dependency{ImportPath: "D102", Comment: "D203"})
	diff, _ = diffStr(&dep1, &dep2)

	if !diffsEqual(strings.Fields(diff), strings.Fields(d2)) {
		t.Errorf("Expecting diffs to be equal. Obs <%v>. Exp <%v>", diff, d2)
	}
}

// diffsEqual asserts that two slices are equivalent.
func diffsEqual(a, b []string) bool {
	if len(a) != len(b) {
		return false
	}

	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

