package uuid

import (
	"encoding/json"
	"reflect"
	"testing"
)

var testUUID = MustParse("f47ac10b-58cc-0372-8567-0e02b2c3d479")

func TestJSON(t *testing.T) {
	type S struct {
		ID1 UUID
		ID2 UUID
	}
	s1 := S{ID1: testUUID}
	data, err := json.Marshal(&s1)
	if err != nil {
		t.Fatal(err)
	}
	var s2 S
	if err := json.Unmarshal(data, &s2); err != nil {
		t.Fatal(err)
	}
	if !reflect.DeepEqual(&s1, &s2) {
		t.Errorf("got %#v, want %#v", s2, s1)
	}
}

func TestJSONUnmarshal(t *testing.T) {
	type S struct {
		ID1 UUID
		ID2 UUID `json:"ID2,omitempty"`
	}

	testCases := map[string]struct {
		data           []byte
		expectedError  error
		expectedResult UUID
	}{
		"success": {
			data:           []byte(`{"ID1": "f47ac10b-58cc-0372-8567-0e02b2c3d479"}`),
			expectedError:  nil,
			expectedResult: testUUID,
		},
		"zero": {
			data:           []byte(`{"ID1": "00000000-0000-0000-0000-000000000000"}`),
			expectedError:  nil,
			expectedResult: Nil,
		},
		"null": {
			data:           []byte(`{"ID1": null}`),
			expectedError:  nil,
			expectedResult: Nil,
		},
		"empty": {
			data:           []byte(`{"ID1": ""}`),
			expectedError:  invalidLengthError{len: 0},
			expectedResult: Nil,
		},
		"omitempty": {
			data:           []byte(`{"ID2": ""}`),
			expectedError:  invalidLengthError{len: 0},
			expectedResult: Nil,
		},
	}

	for name, tc := range testCases {
		t.Run(name, func(t *testing.T) {
			var s S
			if err := json.Unmarshal(tc.data, &s); err != tc.expectedError {
				t.Errorf("unexpected error: got %v, want %v", err, tc.expectedError)
			}
			if !reflect.DeepEqual(s.ID1, tc.expectedResult) {
				t.Errorf("got %#v, want %#v", s.ID1, tc.expectedResult)
			}
		})
	}
}

func BenchmarkUUID_MarshalJSON(b *testing.B) {
	x := &struct {
		UUID UUID `json:"uuid"`
	}{}
	var err error
	x.UUID, err = Parse("f47ac10b-58cc-0372-8567-0e02b2c3d479")
	if err != nil {
		b.Fatal(err)
	}
	for i := 0; i < b.N; i++ {
		js, err := json.Marshal(x)
		if err != nil {
			b.Fatalf("marshal json: %#v (%v)", js, err)
		}
	}
}

func BenchmarkUUID_UnmarshalJSON(b *testing.B) {
	js := []byte(`{"uuid":"f47ac10b-58cc-0372-8567-0e02b2c3d479"}`)
	var x *struct {
		UUID UUID `json:"uuid"`
	}
	for i := 0; i < b.N; i++ {
		err := json.Unmarshal(js, &x)
		if err != nil {
			b.Fatalf("marshal json: %#v (%v)", js, err)
		}
	}
}

