	io.WriteString(resp.ResponseWriter, "service-")
	chain.ProcessFilter(req, resp)
}

func routeFilter(req *Request, resp *Response, chain *FilterChain) {
	io.WriteString(resp.ResponseWriter, "route-")
	chain.ProcessFilter(req, resp)
}

