{
  "name": "gojs",
  "version": "3.0.17",
  "description": "Interactive diagrams, charts, and graphs, such as trees, flowcharts, orgcharts, UML, BPMN, or business diagrams",
  "keywords": [ "GoJS", "diagram", "chart", "tree", "flowchart", "orgchart", "uml", "bpmn", "graph", "editor", "drawing", "canvas", "svg", "hierarchy", "family-tree", "decision-tree", "mindmap", "treeview", "genogram", "ivr-tree", "parse-tree", "concept-map", "visualization", "entity-relationship", "er-diagram", "radial", "sankey", "pert", "gantt", "timeline", "swimlane", "workflow", "flow", "process", "state", "sequential-function", "grafcet", "sequence", "circuit", "dataflow", "data-flow", "planogram", "floorplan", "seating", "pipes", "layout", "tree-layout", "force-directed", "layered", "hierarchical", "circular", "fishbone", "ishikawa", "treemap", "tree-map", "subgraph", "network", "library", "typescript" ],
  "homepage": "https://gojs.net",
  "bugs": {
    "url": "https://forum.nwoods.com/c/gojs",
    "email": "gojs@nwoods.com"
  },
  "license": "SEE LICENSE IN license.html",
  "author": "Northwoods Software",
  "files": [
    "package.json",
    "README.md",
    "license.html",
    "release",
    "test"
  ],
  "main": "release/go.js",
  "module": "release/go-module.js",
  "exports": {
    ".": {
      "import": {
        "types": "./release/go-module.d.ts",
        "development": "./release/go-debug.mjs",
        "production": "./release/go.mjs",
        "default": "./release/go.mjs"
      },
      "default": {
        "types": "./release/go.d.ts",
        "development": "./release/go-debug.js",
        "production": "./release/go.js",
        "default": "./release/go.js"
      }
    },
    "./release/go": {
      "import": {
        "types": "./release/go-module.d.ts",
        "default": "./release/go.mjs"
      },
      "default": {
        "types": "./release/go.d.ts",
        "default": "./release/go.js"
      }
    },
    "./release/go-debug": {
      "import": {
        "types": "./release/go-module.d.ts",
        "default": "./release/go-debug.mjs"
      },
      "default": {
        "types": "./release/go.d.ts",
        "default": "./release/go-debug.js"
      }
    },
    "./release/go-module": {
      "types": "./release/go-module.d.ts",
      "default": "./release/go.mjs"
    },
    "./release/go-debug-module": {
      "types": "./release/go-module.d.ts",
      "default": "./release/go-debug.mjs"
    },
    "./release/go.js": {
      "import": {
        "types": "./release/go-module.d.ts",
        "default": "./release/go.mjs"
      },
      "default": {
        "types": "./release/go.d.ts",
        "default": "./release/go.js"
      }
    },
    "./release/go-debug.js": {
      "import": {
        "types": "./release/go-module.d.ts",
        "default": "./release/go-debug.mjs"
      },
      "default": {
        "types": "./release/go.d.ts",
        "default": "./release/go-debug.js"
      }
    },
    "./release/go-module.js": {
      "types": "./release/go-module.d.ts",
      "default": "./release/go.mjs"
    },
    "./release/go-debug-module.js": {
      "types": "./release/go-module.d.ts",
      "default": "./release/go-debug.mjs"
    },
    "./release/go.mjs": {
      "types": "./release/go-module.d.ts",
      "default": "./release/go.mjs"
    },
    "./release/go-debug.mjs": {
      "types": "./release/go-module.d.ts",
      "default": "./release/go-debug.mjs"
    }
  },
  "typings":  "release/go.d.ts",
  "unpkg": "release/go.js",
  "repository": {
    "type": "git",
    "url": "https://github.com/NorthwoodsSoftware/GoJS"
  },
  "scripts": {
    "test": "node test/nodescript.js"
  }
}
