#!/bin/bash
#
# Converts an image, replacing it in-place ... I know! dangerous right?

# first we take the image and convert it to our 16 color map without
# dithering

ascii() {
  tmp=`mktemp --suffix=png`
  convert $1 -remap img-ref/ega16-palette.png -resize 70x $tmp
  img2txt -W 30 -x 12.4 -y 20 -f tga $1 | convert tga:- $1
  rm $tmp
}

color16() {
  convert $1 -resize 120x - | convert - -filter box -dither FloydSteinberg -remap img-ref/ega16-palette.png -resize 360x $1
}
color16 $1

#!/usr/bin/env node
const TextToSVG = require('text-to-svg');
const textToSVG = TextToSVG.loadSync('fonts/Px437_IBM_EGA8.otf');
const fs = require('fs');

if(!process.argv[2]) { 
  console.error("You need to specify a path to generate the svg files in as your argument");
  process.exit(-1);
}

const path = (process.argv[2] || '.').replace(/\/$/, '');
var list = ["// This file is auto-generated from icon-gen in the root directory"];

const variationList = [
  ['black', '#000000'],
  ['grayLight', '#bbbbbb']
];

[
  ['[', 'left-brace'],
  [']', 'right-brace'],
  ['[', 'left-brace'],
  ['-', 'hyphen'],
  ['\u25B2', 'arrow-up'],
  ['\u25Bc', 'arrow-down'],
  ['\u2591', 'shade-25'],
  ['\u2592', 'shade-50'],
  ['\u2593', 'shade-75'],
  ['\u2588', 'shade-100'],

  ['[\u25A0]', 'checkbox-checked'],
  ['[ ]', 'checkbox-unchecked'],
  [`\u253c\u2584`, 'grid'],
  ['(\u2022)', 'radio-checked'],
  ['( )', 'radio-unchecked']
].forEach(row => {
  let [str, string] = row;

  for(const [name, hex] of variationList) {
    let props = {
      x: 0, y: 0, 
      fontSize: 16, anchor: 'top',
      attributes: {fill: hex}
    };

    if (row.length > 3) {
      props = Object.assign(props, row[3]);
    }

    if(name == 'grid') {
      props.attributes.fill += '40';
    }

    const 
      svg = textToSVG.getSVG(str, props),
      fullpath = `${path}/${string}-${name}.svg`

    fs.writeFileSync(fullpath, svg);

    console.log(fullpath);
  }

  //if(bsname) {
  //  list.push(`$${bsname}: url('data:image/svg+xml,${svg}');`);
  //}
});

