nightwatch_config = {
  src_folders : [ 'test/scripts' ],

  selenium : {
    'start_process': false,
    'host': 'hub-cloud.browserstack.com',
    'port': 80
  },

  common_capabilities: {
    'browserstack.user': '${BS_USERNAME}',
    'browserstack.key': '${BS_ACCESS_KEY}'
  },

  test_settings: {
    default: {
      desiredCapabilities: {
        'os': 'Windows',
        'os_version': '7',
        'browser': 'Chrome',
        'browser_version': '14.0',
        'resolution': '1024x768'
      }
    },
    chrome: {
      desiredCapabilities: {
        'os': 'Windows',
        'os_version': '7',
        'browser': 'Chrome',
        'browser_version': '14.0',
        'resolution': '1024x768'
      }
    },
    firefox: {
      desiredCapabilities: {
        'os': 'Windows',
        'os_version': '7',
        'browser': 'Firefox',
        'browser_version': '12.0',
        'resolution': '1024x768'
      }
    },
    ie: {
      desiredCapabilities: {
        'os': 'Windows',
        'os_version': '7',
        'browser': 'IE',
        'browser_version': '9.0',
        'resolution': '1024x768'
      }
    },
    edge: {
      desiredCapabilities: {
        'os': 'Windows',
        'os_version': '10',
        'browser': 'Edge',
        'browser_version': '15.0',
        'resolution': '1024x768'
      }
    },
    safari: {
      desiredCapabilities: {
        'os': 'OS X',
        'os_version': 'Snow Leopard',
        'browser': 'Safari',
        'browser_version': '5.1',
        'resolution': '1024x768'
      }
    },
    opera: {
      desiredCapabilities: {
        'os': 'Windows',
        'os_version': '7',
        'browser': 'Opera',
        'browser_version': '12.15',
        'resolution': '1024x768'
      }
    },
  }
};

// Code to support common capabilites
for(var i in nightwatch_config.test_settings){
  var config = nightwatch_config.test_settings[i],
      browser_name = i;
  config['selenium_host'] = nightwatch_config.selenium.host;
  config['selenium_port'] = nightwatch_config.selenium.port;
  config['desiredCapabilities'] = config['desiredCapabilities'] || {};
  config['globals'] = {
    'browser_name': browser_name,
    'abortOnAssertionFailure': false
  };
  for(var j in nightwatch_config.common_capabilities){
    config['desiredCapabilities'][j] = config['desiredCapabilities'][j] || nightwatch_config.common_capabilities[j];
    // config['desiredCapabilities']['browserstack.local'] = true;
  }
}

module.exports = nightwatch_config;

const rollup = require('rollup').rollup;
const resolve = require('rollup-plugin-node-resolve');
const babel = require('rollup-plugin-babel');

const gulp = require('gulp');
const packages = require('/www/package.json');
const $ = require('gulp-load-plugins')({
  config: packages
});
const browserSync = require('browser-sync').create();
const nunjucks = require('nunjucks');
const path = require('path');
const fs = require('fs');

let sourcemapsDest = 'sourcemaps';
let libName = 'tiny-slider',
    testName = 'tests',
    modulePostfix = '.module',
    helperIEPostfix = '.helper.ie8',
    script = libName + '.js',
    moduleScript = libName + modulePostfix + '.js',
    helperIEScript = libName + helperIEPostfix + '.js',
    testScript = testName + '.js',
    sassFile = libName + '.scss',
    pathSrc = 'src/',
    pathDest = 'dist/',
    pathTest = 'tests/js/',
    scriptSources = [pathSrc + '**/*.js', '!' + pathSrc + moduleScript, '!' + pathSrc + helperIEScript];

function errorlog (error) {  
  console.error.bind(error);  
  this.emit('end');  
}  

function readfiles (dir, arr) {
  fs.readdirSync(dir).forEach( file => {
    if (path.extname(file) === '.njk') {
      arr.push(dir+file);
    }
  });
}

gulp.task('njk', function() {
  let files = [];
  readfiles('./template/demo/', files);
  readfiles('./template/test/', files);

  files.forEach(function(file) {
    let dest = path.dirname(file).replace('/template', '');

    return gulp.src(file)
      .pipe($.plumber())
      .pipe($.nunjucks.compile({}, {
        watch: true,
        noCache: true
      }))
      .pipe($.rename(function (path) {
        path.extname = '.html';
      }))
      .pipe($.htmltidy({
        doctype: 'html5',
        wrap: 0,
        hideComments: false,
        indent: true,
        'indent-attributes': false,
        'drop-empty-elements': false,
        'force-output': true
      }))
      .pipe(gulp.dest(dest));
  });
});

function sassTask(src, dest) {
  return gulp.src(src)
    .pipe($.sourcemaps.init())
    .pipe($.sass({
      outputStyle: 'compressed', 
      precision: 7
    }).on('error', $.sass.logError))  
    .pipe($.sourcemaps.write(sourcemapsDest))
    .pipe(gulp.dest(dest))
    .pipe(browserSync.stream());
}

// SASS Task
gulp.task('sass', function () {  
  sassTask(pathSrc + sassFile, pathDest);
});  

// Script Task
gulp.task('script', function () {
  return rollup({
    input: pathSrc + script,
    context: 'window',
    treeshake: false,
    plugins: [
      resolve({
        jsnext: true,
        main: true,
        browser: true,
      }),
    ],
  }).then(function (bundle) {
    return bundle.write({
      file: pathDest + libName + '.js',
      format: 'es',
      // moduleName: 'tns',
    });
  });
});

gulp.task('helper-ie8', function () {
  return rollup({
    input: pathSrc + helperIEScript,
  }).then(function (bundle) {
    return bundle.write({
      file: pathDest + helperIEScript,
      format: 'es',
    });
  });
});

gulp.task('editPro', ['script'], function() {
  return gulp.src(pathDest + libName + '.js')
    .pipe($.change(function (content) {
      return 'var tns = (function (){\n' + content.replace('export { tns }', 'return tns') + '})();';
    }))
    .pipe(gulp.dest(pathDest))
});

gulp.task('makeDevCopy', function() {
  return gulp.src(pathSrc + script)
    // .pipe($.change(function (content) {
    //   return content
    //     .replace('IIFE', 'ES MODULE')
    //     .replace(/bower_components/g, '..');
    // }))
    .pipe($.rename({ basename: libName + modulePostfix }))
    .pipe(gulp.dest(pathSrc))
});

gulp.task('min', ['editPro'], function () {
  return gulp.src(pathDest + '*.js')
    .pipe($.sourcemaps.init())
    .pipe($.uglify())
    .pipe($.sourcemaps.write('../' + sourcemapsDest))
    .pipe(gulp.dest(pathDest + 'min'))
})

// browser-sync
gulp.task('server', function() {
  browserSync.init({
    server: {
      baseDir: './'
    },
    ghostMode: {
      clicks: false,
      forms: false,
      scroll: false
    },
    port: '3000',
    open: false,
    notify: false
  });

  gulp.watch('template/**/*.njk', function (e) {
    var dir = path.parse(e.path).dir;
    if (e.type !== 'deleted' && dir.indexOf('parts') < 0) {
      return gulp.src(e.path)
        .pipe($.plumber())
        .pipe($.nunjucks.compile({}, {
          watch: true,
          noCache: true
        }))
        .pipe($.rename(function (path) {
          path.extname = '.html';
        }))
        .pipe($.htmltidy({
          doctype: 'html5',
          wrap: 0,
          hideComments: false,
          indent: true,
          'indent-attributes': false,
          'drop-empty-elements': false,
          'force-output': true
        }))
        .pipe(gulp.dest(dir.replace('/template', '')));
    }
  });
  gulp.watch(pathSrc + sassFile, function (e) {
    sassTask(pathSrc + sassFile, pathDest);
  });
  gulp.watch(pathSrc + script, ['makeDevCopy']);
  gulp.watch(scriptSources, ['min']);
  gulp.watch(pathSrc + helperIEScript, ['helper-ie8']);
  // gulp.watch([pathTest + testScript], ['test']);
  gulp.watch(['**/*.html', pathTest + '*.js', '!' + pathTest + 'tests-async.js', pathDest + '*.css', pathDest + 'min/*.js']).on('change', browserSync.reload);
});

// Default Task
gulp.task('default', [
  // 'sass',
  // 'min',
  // 'helper-ie8',
  // 'makeDevCopy',
  'server', 
]);

