apply plugin: 'com.github.dcendents.android-maven'

// æŒ‡å®šgroupï¼Œcom.github.<ç”¨æˆ·å>ï¼Œè¿™é‡Œæˆ‘é»˜è®¤å¡«å†™çš„æ˜¯æˆ‘çš„githubè´¦å·ï¼Œè¯·æ¢æˆä½ è‡ªå·±çš„ã€‚
group='com.github.xuexiangjys'

//---------------------------------------------


// æŒ‡å®šç¼–ç 
tasks.withType(JavaCompile) {
    options.encoding = "UTF-8"
}

tasks.withType(Javadoc) {
    options.encoding = 'UTF-8'
}

if (project.hasProperty("android")) { // Android libraries
    task sourcesJar(type: Jar) {
        classifier = 'sources'
        from android.sourceSets.main.java.srcDirs
    }

    task javadoc(type: Javadoc) {
        failOnError  false
        source = android.sourceSets.main.java.srcDirs
        classpath += project.files(android.getBootClasspath().join(File.pathSeparator))
    }
} else { // Java libraries
    task sourcesJar(type: Jar, dependsOn: classes) {
        classifier = 'sources'
        from sourceSets.main.allSource
    }
}

javadoc {
    options {
        encoding "UTF-8"
        charSet 'UTF-8'
        author true
        version true
        links "http://docs.oracle.com/javase/7/docs/api"
    }
}

// åˆ¶ä½œæ–‡æ¡£(Javadoc)
task javadocJar(type: Jar, dependsOn: javadoc) {
    classifier = 'javadoc'
    from javadoc.destinationDir
}

artifacts {
    archives javadocJar
    archives sourcesJar
}

// Top-level build file where you can add configuration options common to all sub-projects/modules.

buildscript {
    apply from: './versions.gradle'
    addRepos(repositories) //å¢åŠ ä»£ç ä»“åº“
    dependencies {
        classpath deps.android_gradle_plugin
        classpath deps.android_maven_gradle_plugin

        classpath 'com.github.xuexiangjys.XAOP:xaop-plugin:1.1.0'

        classpath 'com.chenenyu:img-optimizer:1.2.0' // å›¾ç‰‡å‹ç¼©
        classpath 'com.github.xuexiangjys.XRouter:xrouter-plugin:1.0.1'
        //æ»´æ»´çš„è´¨é‡ä¼˜åŒ–æ¡†æ¶
        if (isNeedPackage.toBoolean() && isUseBooster.toBoolean()) {
            classpath deps.booster.gradle_plugin
            classpath deps.booster.task_processed_res
            classpath deps.booster.task_resource_deredundancy
        }
    }
}

allprojects {
    addRepos(repositories)
}

task clean(type: Delete) {
    delete rootProject.buildDir
}
