buildscript {
    ext {
        androidLibraryVersion ="30443186505dbb16c19ac69a34122010446207fe"
        androidPluginVersion = '8.8.0'
        androidxMediaVersion = '1.5.1'
        androidxTestVersion = "1.6.1"
        appCompatVersion = '1.7.0'
        checkerVersion = "3.21.2"
        daggerVersion = "2.54"
        documentScannerVersion = "1.1.1"
        espressoVersion = "3.6.1"
        jacoco_version = '0.8.12'
        kotlin_version = '2.1.0'
        markwonVersion = "4.6.2"
        mockitoVersion = "4.11.0"
        mockitoKotlinVersion = "4.1.0"
        mockkVersion = "1.13.14"
        prismVersion = "2.0.0"
        roomVersion = "2.6.1"
        workRuntime = "2.10.0"

        ciBuild = System.getenv("CI") == "true"
        shotTest = System.getenv("SHOT_TEST") == "true"
    }
}

subprojects {
    buildscript {
        repositories {
            google()
            mavenCentral()
            gradlePluginPortal()
        }
    }
    repositories {
        google()
        mavenCentral()
        maven { url "https://jitpack.io" }
    }
}

tasks.register('clean', Delete) {
    delete rootProject.buildDir
}

tasks.register('installGitHooks', Copy) {
    def sourceFolder = "${rootProject.projectDir}/scripts/hooks"
    def destFolder = "${rootProject.projectDir}/.git/hooks"

    description = "Install git hooks"

    from(sourceFolder) {
        include '*'
    }
    into destFolder

    eachFile { file ->
        println "${sourceFolder}/${file.relativeSourcePath} -> ${destFolder}/${file.path}"
    }
}

