buildscript {
    repositories {
        gradlePluginPortal()
        google()
    }
    dependencies {
        classpath 'com.android.tools.build:gradle:7.4.1'
        classpath 'gradle.plugin.com.github.sherter.google-java-format:google-java-format-gradle-plugin:0.9'
        classpath 'net.ltgt.errorprone:net.ltgt.errorprone.gradle.plugin:2.0.2'
    }
}

allprojects {
    repositories {
        mavenCentral()
        google()
    }
}

subprojects {
    apply plugin: 'com.github.sherter.google-java-format'
    apply plugin: 'net.ltgt.errorprone'

    googleJavaFormat {
        toolVersion = '1.5'
        options style: 'AOSP'
    }

    apply plugin: 'com.android.library'

    dependencies {
        // NOTE: Updating ErrorProne introduces new checks that may cause the build to fail. Pin to a
        // specific version to control these updates.
        errorprone("com.google.errorprone:error_prone_core:2.3.2")
        // ErrorProne requires a JDK 9 compiler, so pull one in as a dependency since we use Java 8:
        // https://github.com/tbroyer/gradle-errorprone-plugin#jdk-8-support
        errorproneJavac("com.google.errorprone:javac:9+181-r4173-1")
    }

    group = 'com.android.volley'
    version = '1.2.2-SNAPSHOT'

    android {
        useLibrary 'org.apache.http.legacy'

        compileSdkVersion 28
        buildToolsVersion = '30.0.3'

        defaultConfig {
            minSdkVersion 8
        }

        compileOptions {
            sourceCompatibility JavaVersion.VERSION_1_7
            targetCompatibility JavaVersion.VERSION_1_7
        }
    }

    tasks.withType(JavaCompile) {
        options.errorprone {
            check("ParameterComment", net.ltgt.gradle.errorprone.CheckSeverity.ERROR)
        }
        options.compilerArgs << "-Xlint:unchecked" << "-Werror"
    }

    if (it.name != 'testing') {
        apply from: '../publish.gradle'
    }
}

apply plugin: 'maven-publish'

task sourcesJar(type: Jar) {
    classifier = 'sources'
    from android.sourceSets.main.java.srcDirs
}

task javadoc(type: Javadoc) {
    source = android.sourceSets.main.java.srcDirs
    classpath += project.files(android.getBootClasspath().join(File.pathSeparator))
}

afterEvaluate {
    javadoc.classpath += files(android.libraryVariants.collect { variant ->
        variant.getJavaCompileProvider().get().classpath.files
    })
}

task javadocJar(type: Jar, dependsOn: javadoc) {
    classifier = 'javadoc'
    from javadoc.destinationDir
}

artifacts {
    archives javadocJar
    archives sourcesJar
}

afterEvaluate {
    publishing {
        publications {
            release(MavenPublication) {
                // Depend on the release AAR
                from project.components.release

                groupId 'com.android.volley'
                artifactId project.artifactId
                version project.version
                pom {
                    name = project.pomName
                    description = project.pomDescription
                    url = 'https://github.com/google/volley'
                    packaging 'aar'
                    licenses {
                        license {
                            name = "The Apache License, Version 2.0"
                            url = "http://www.apache.org/licenses/LICENSE-2.0.txt"
                        }
                    }
                    scm {
                        connection = 'scm:git:git://github.com/google/volley.git'
                        developerConnection = 'scm:git:ssh://git@github.com/google/volley.git'
                        url = 'https://github.com/google/volley'
                    }
                    developers {
                        developer {
                            name = 'The Volley Team'
                            email = 'noreply+volley@google.com'
                        }
                    }
                }

                // Also include sources and JavaDoc
                artifact sourcesJar
                artifact javadocJar
            }
        }

        repositories {
            maven {
                url = "https://oss.sonatype.org/content/repositories/snapshots/"
                credentials {
                    username = System.env.OSSRH_DEPLOY_USERNAME
                    password = System.env.OSSRH_DEPLOY_PASSWORD
                }
            }
        }
    }
}
