buildscript {
    repositories {
        google()
        jcenter()
    }
    dependencies {
        classpath 'com.android.tools.build:gradle:3.2.1'
    }
}

allprojects {
    repositories {
        google()
        jcenter()
    }
}

ext {
	compileSdkVersion = 28
	buildToolsVersion = "28.0.3"
	minSdkVersion = 15
	targetSdkVersion = 28
	sourceCompatibility = JavaVersion.VERSION_1_8
	targetCompatibility = JavaVersion.VERSION_1_8
	lintAbortOnError = false
}
