task binZip(type: Zip) {
    destinationDirectory = new File(globalDist)
    archiveFileName = 'freeplane_bin-' + distVersion + '.zip'

    from(globalBin) {
        include('**')
        exclude('freeplane.sh')
    }

    from(globalBin) {
        include('freeplane.sh')
        fileMode = 0755
    }

    from(rootDir) {
        include('license.txt')
    }

    into('freeplane-' + distVersion)

    for (p in subprojects) {
        dependsOn p.tasks.getByPath("build")
    }
}

import org.gradle.util.GradleVersion

buildscript {
  repositories {
    mavenCentral()
  }
  dependencies {
    classpath 'biz.aQute.bnd:biz.aQute.bnd.gradle:7.0.0'
  }
}

plugins {
  id 'com.netflix.nebula.ospackage' version '11.10.0'
  id 'org.owasp.dependencycheck' version '9.2.0' apply false
  id 'se.patrikerdes.use-latest-versions' version '0.2.18'  apply false
  id 'com.github.ben-manes.versions' version '0.51.0'  apply false
}

Properties props = new Properties()
props.load(new FileInputStream(rootDir.path + '/freeplane/src/viewer/resources/version.properties'))

allprojects {

	apply plugin: 'org.owasp.dependencycheck'
	apply plugin: 'com.github.ben-manes.versions'
    apply plugin: 'se.patrikerdes.use-latest-versions'
    // in a multi-project setup, all projects must have the same version number!
    // the real (specific) version number is in freeplane/viewer-resources/version.properties!
	ext.majorVersion = props['freeplane_version']
	ext.versionStatus = props['freeplane_version_status']
	ext.minorVersion = System.getProperty('minor', '')
	ext.distVersion = """$majorVersion${versionStatus.empty?'':'-'+versionStatus}${
        (minorVersion.empty || minorVersion.startsWith('u'))
        ? minorVersion 
        : (minorVersion.find(/^[a-z]/) ? '-' + minorVersion : '-pre' + minorVersion)
        }"""
    version = ext.majorVersion

    // WARNING: this is removed with "gradle clean"!!
    ext.globalBin = rootDir.path + '/BIN'

    repositories {
		mavenCentral()
		maven {url 'https://s01.oss.sonatype.org/content/groups/public/' }
		maven { url 'https://resources.knopflerfish.org/repo/maven2/release/' }
		maven { url "${project.gradle.gradleUserHomeDir}/local-artifacts" }

        // this is necessary to grab both binary and source jars
        // for local (git) dependencies
        flatDir name: 'localGitDepsRepository',
                dirs: [rootDir.path + "/freeplane/lib",
                       rootDir.path + "/freeplane_plugin_jsyntaxpane/lib"]
    }

}

/* this is used for (linux) packages, where the dependencies
  live on the file system (/usr/share/java/*.jar on Debian): */
def externalizeJar(File jar) {
    // do not externalize, jars are copied over to /BUILD
    // in development workflow!
    return "lib/${jar.name}"
}

// this is overridden in linux packages where this might be named
// knopflerfish-framework.jar
def isKnopflerfishJar(fileName) {
    return fileName.startsWith('framework-') &&
           fileName.endsWith('.jar');
}

def filterAndExternalizePluginDependencies(runtime_files) {
    return runtime_files.findAll {
        File file -> !isKnopflerfishJar(file.name)
    }.findAll {
        File file ->
        file.name != "freeplane-${project.version}.jar" &&
        !(file.name.startsWith('freeplane_plugin_') &&
          file.name.endsWith('-' + project.version + '.jar'))
    }.collect {
        File file -> externalizeJar(file)
    }
}

subprojects {
    apply plugin: 'java-library'
    apply plugin: 'eclipse'
    apply plugin: 'idea'

    java {
        targetCompatibility=JavaVersion.VERSION_1_8
        sourceCompatibility=JavaVersion.VERSION_1_8
    }

    tasks.withType(JavaCompile) {
      options.encoding = "UTF-8"
    }
    tasks.withType(AbstractArchiveTask).configureEach {
      preserveFileTimestamps = false
      reproducibleFileOrder = true
    }
    javadoc {
      options.encoding = "UTF-8"
      options.docEncoding = "UTF-8"
      options.charSet = "UTF-8"
      options.locale="en_US"
      options.jFlags('-Duser.language=en', '-Duser.country=US')
      enabled = false
      failOnError = false
    }

    repositories {
       maven  {
        url "https://repo1.maven.org/maven2"
       }
    }

    configurations {
        lib
        maclib
        api.extendsFrom(lib)
        api.extendsFrom(maclib)
    }

    dependencies {
		testImplementation 'junit:junit:4.13.2',
				'org.hamcrest:hamcrest-all:1.3',
				'org.mockito:mockito-core:5.14.2',
			 	'org.assertj:assertj-core:3.26.3'
    }

    if(GradleVersion.current() >= GradleVersion.version('6.4')) {
        java {
            modularity.inferModulePath = false
        }

        tasks.named('compileJava') {
            modularity.inferModulePath = false
        }
    }

    task copyEclipseSettings(type: Copy) {
        from(rootDir.path + "/freeplane_framework/eclipse/settings-templates/") {
            include("*.prefs")
        }
        into(project.file(".settings/"))
    }
    eclipseJdt.dependsOn copyEclipseSettings

    task cleanEclipseSettings(type: Delete) {
        delete(project.file(".settings"))
    }
    cleanEclipseJdt.dependsOn cleanEclipseSettings

	if (project.hasProperty('TestLoggingFull')) {
		test {
			testLogging {
				events "failed"
				exceptionFormat "full"
			}
		}
	}
}

// OSGi projects (/plugin/, freeplane) need this for compilation (e.g. resolve Bundle),
// and freeplane_framework needs to copy it to BUILD/framework.jar
configure(subprojects.findAll {it.name =~ /plugin/ || it.name =~ /^freeplane$/ || it.name =~ /^freeplane_framework$/}){
    dependencies {
        implementation group: 'org.knopflerfish.kf6', name:'framework', version: '8.0.11'
    }
   task cleanBUILD(type: Delete) {
       delete "$buildDir/libs"
   }

   jar.dependsOn cleanBUILD
}

// common configuration for all OSGi projects...
configure(subprojects.findAll {it.name =~ /plugin/ || it.name =~ /^freeplane$/ }) {

    apply plugin: 'biz.aQute.bnd.builder'

    ext.bundleImports = 'nothing.*'
    ext.bundleExports = ''

    if (project.name.equals("freeplane"))
       ext.pluginid = 'org.freeplane.core'

   afterEvaluate {

     if (it.name =~ /plugin/) {
       ext.bundleDeps = filterAndExternalizePluginDependencies(configurations.lib.files)
       bundleDeps.add(0, "lib/plugin-" + project.version + '.jar')
     }

     jar {
        def bndConfiguration = [
        '-savemanifest': 'build/manifest/MANIFEST.MF',
        'Bundle-SymbolicName': pluginid,
        'Export-Package': bundleExports.replaceAll("\\s+", ""),
        'Bundle-Vendor': 'Freeplane Project',
        'Import-Package': bundleImports.replaceAll("\\s+", ""),
        'Bundle-Activator': bundleActivator,
        'Bundle-ClassPath': '., ' + bundleDeps.join(', ')
        ]
        if (project.name =~ /plugin/) {
            bndConfiguration << ['Require-Bundle':'org.freeplane.core']
        }
        bundle {
            bnd(bndConfiguration)
        }

       }
   }

}

// common configuration for all plugins...
configure(subprojects.findAll {it.name =~ /plugin/}) {

   ext.pluginid = project.name.replace("freeplane_plugin_", "org.freeplane.plugin.")
   ext.bundleActivator = pluginid + ".Activator"

    task copyOSGiJars(type: Copy) {
        from ("$buildDir/libs") {
            include("*.jar")
            exclude('freeplane-' + project.version + '.jar')
            exclude('freeplane_plugin_*-' + project.version + '.jar')
        }
        from (configurations.lib) {
            include("*.jar")
        }
        from ('lib') {
            include("*.jar")
            exclude('*-sources.jar')
        }
        into(globalBin + '/plugins/' + pluginid + '/lib/')
    }

    task copyOSGiManifest(type: Copy) {
        from ("$buildDir/manifest/") {
            include("MANIFEST.MF")
        }
        into(globalBin + '/plugins/' + pluginid + '/META-INF/')
    }

    task copyOSGiConfig(type: Copy) {
        from ("OSGI-INF")
        into(globalBin + '/plugins/' + pluginid + '/OSGI-INF/')
    }

    build.dependsOn copyOSGiJars
    build.dependsOn copyOSGiManifest
    build.dependsOn copyOSGiConfig

    // create a separate non-OSGi(!) jar for each plugin!
    task pluginJar(type: Jar) {
         archiveBaseName = 'plugin'
         dependsOn classes

         from (sourceSets.main.output) {
             include("**")
         }

         includeEmptyDirs = false

         // use a non-OSGi manifest (this jar is referenced from an OSGi bundle)!
         manifest { }
    }

    // jar must execute after pluginJar so that the OSGi manifest can still be
    // picked up from build/manifest
    jar.dependsOn pluginJar
    copyOSGiJars.dependsOn pluginJar
}

apply plugin: 'base' // necessary to get the global 'clean' task

task cleanBUILD(type: Delete) {
    delete globalBin
}
clean.dependsOn(cleanBUILD)
build.dependsOn(cleanBUILD)

task check_translation {
    dependsOn ':JOrtho_0.4_freeplane:check_translation'
    dependsOn ':freeplane:check_translation'
}
task format_translation {
    dependsOn ':JOrtho_0.4_freeplane:format_translation'
    dependsOn ':freeplane:format_translation'
}

apply from: './dist.gradle'
apply from: './jdt-config.gradle'

ext.globalDist = rootDir.path + '/DIST'

task gitinfoDist(type: Copy) {
    from(globalBin) {
        include('gitinfo.txt')
    }
    from(globalBin + '/resources') {
        include('gitinfo.properties')
    }
    into(globalDist)

    for (p in subprojects) {
        dependsOn p.tasks.getByPath("build")
    }
}

task historyDist(type:Copy) {
	from(rootDir.path + '/freeplane/doc/'){
		include 'history_en.txt'
	}
	into(globalDist)
}

task dist {}

apply from: './bin.dist.gradle'
apply from: './win.dist.gradle'
apply from: './mac.dist.gradle'
apply from: './src.dist.gradle'
apply from: './linux-packages.gradle'

// meta task
dist {
    dependsOn binZip
    dependsOn srcTarGz
    dependsOn srcpureTarGz
    dependsOn windowsPortableInstaller
    dependsOn gitinfoDist
    dependsOn historyDist
    dependsOn freeplaneDeb
}

// clean
task cleanDist(type: Delete) {
    delete globalDist
}
clean.dependsOn cleanDist

task createGitTag(type: Exec) {
	def tag = "${versionStatus.empty ? 'release':versionStatus}-$distVersion"
    ignoreExitValue = true
	doFirst {
		println "> Press enter to create tag $tag"
		System.in.newReader().readLine()
	}
    commandLine 'git', 'tag', tag
}

task release {
	dependsOn (clean, dist, createGitTag)
}
dist.mustRunAfter clean
createGitTag.mustRunAfter dist

subprojects {

    eclipse {

        jdt {
            // if you want to alter the java versions (by default they are configured with gradle java plugin settings):
            sourceCompatibility = JavaVersion.VERSION_1_8
            targetCompatibility = JavaVersion.VERSION_1_8

            file {
                withProperties { properties ->
                    properties.setProperty('org.eclipse.jdt.core.compiler.problem.invalidJavadocTags', 'disabled')
                }
            }
        }
    }
}

task freeplaneDeb(type: Deb) {
    destinationDirectory = new File(globalDist)

    packageName = 'freeplane'
    version = distVersion + "~upstream"
    release = 1

    requires('default-jre | java7-runtime')

    from(rootDir.path + "/freeplane_framework/script/") {
      include("freeplane.svg")
      into("/usr/share/icons/hicolor/scalable/apps")
    }

    from(rootDir.path + "/freeplane_framework/script/") {
      include("freeplane.png")
      into("/usr/share/icons/hicolor/32x32/apps")
    }

    from(rootDir.path + "/debian-meta-data") {
      include('freeplane.desktop')
      into('/usr/share/applications')
    }

    from(rootDir.path + "/debian-meta-data/") {
      include("freeplane.sharedmimeinfo")
      into('/usr/share/mime/packages')
      rename('freeplane.sharedmimeinfo', 'freeplane.xml')
    }

    from(rootDir.path + "/debian-meta-data/") {
      include("freeplane.mime")
      into('/usr/lib/mime/packages')
      rename("freeplane.mime", "freeplane")
    }

    from(globalBin) {
        include('**')
        exclude('freeplane.sh')
        into('/usr/share/freeplane')
    }

    from(globalBin) {
        include('freeplane.sh')
        fileMode = 0755
     	into('/usr/share/freeplane')
    }

    link('/usr/bin/freeplane', '/usr/share/freeplane/freeplane.sh')

    for (p in subprojects) {
        dependsOn p.tasks.getByPath("build")
    }
}

import org.apache.tools.ant.taskdefs.condition.Os
import org.apache.tools.ant.filters.*

def macAppPropertiesFile = file('macapp.properties')

Properties macAppProperties = new Properties()
if (macAppPropertiesFile.exists())
{
    macAppProperties.load(new FileInputStream(macAppPropertiesFile))
}

String macosapp_input_dir

task macosapp_extractzip {
    mustRunAfter binZip

    def zipExtracted = 'build/zip-extracted'

    doFirst {
      if(! binZip.archivePath.exists())
        ant.fail("File ${binZip.archivePath.path} not found")

      delete zipExtracted
      mkdir zipExtracted

      copy {
        from(zipTree(binZip.archivePath))
        into(zipExtracted)
      }
      macosapp_input_dir = zipExtracted + '/' + file(zipExtracted).list()[0] 
    }
}

task macosapp_input {
    dependsOn macosapp_extractzip

    doFirst {
        copy {
        from(macosapp_input_dir) {
            include '*.*'
        }
        into 'build/macosapp/input'
      }
    }
}

  def macosapp_copyvolumeicon= tasks.create ("macosapp_copyvolumeicon", Copy) {
    from file ('freeplane_framework/mac-appbundler/freeplane.icns')
    into file('build/macosapp/dmg-resource-dir')
    rename { String fileName ->
      'Freeplane-volume.icns'
    }
  }

def defineMacBuildTasks =  { architecture -> 

   def jpackage = macAppProperties["macapp.${architecture}.jpackage.jdk"] + '/bin/jpackage'

   def macosapp_deleteAppImage = tasks.create("macosapp_deleteAppImage_${architecture}", Delete) { 
     delete "build/macosapp/app-image-${architecture}" 
   }

   def jpackage_macosxapp = tasks.create ("jpackage_macosxapp_${architecture}", Exec) {

   	commandLine jpackage,
        	 '--name' , 'Freeplane',
   	 '--type', 'app-image',
     '--icon', 'freeplane_framework/mac-appbundler/freeplane.icns',
     '--app-version', majorVersion,
     '--java-options', "-XX:-IgnoreUnrecognizedVMOptions",
     '--java-options', "-XX:MaxRAM=20g",
     '--java-options', "-XX:MaxRAMPercentage=15.0",
     '--java-options', '-Djava.security.manager=allow',
     '--java-options', '-Dapple.laf.useScreenMenuBar=false',
     '--java-options', '-Xdock:name=Freeplane',
     '--java-options', '--add-exports', 
     '--java-options', 'java.desktop/com.apple.eawt=ALL-UNNAMED',
     '--java-options', '--add-exports', 
     '--java-options', 'java.desktop/sun.awt=ALL-UNNAMED',
     '--java-options', '--add-exports', 
     '--java-options', 'java.desktop/sun.swing=ALL-UNNAMED',
     '--java-options', '--add-exports', 
     '--java-options', 'java.desktop/com.apple.laf=ALL-UNNAMED',
     '--java-options', '--add-opens', 
     '--java-options', 'java.desktop/javax.swing.text.html=ALL-UNNAMED',
   	 '--input', 'build/macosapp/input',
     '--main-class', 'org.freeplane.launcher.Launcher',
  	 '--main-jar', 'freeplanelauncher.jar',
     '--runtime-image', macAppProperties["macapp.${architecture}.jdk.runtime.image"],
     '--copyright', "Freeplane ${distVersion}",
   	 '--dest', "build/macosapp/app-image-${architecture}"

     dependsOn macosapp_input, macosapp_deleteAppImage
   }

   def macosapp_makeapp= tasks.create ("macosapp_makeapp_${architecture}") {
	   dependsOn jpackage_macosxapp
     doFirst {
        copy {
          from(macosapp_input_dir) {
                include '*/**'
          }
          into "build/macosapp/app-image-${architecture}/Freeplane.app/Contents/app"
        }
        if(architecture=='intel') {
          copy {
              from project('freeplane').configurations.maclib
              into("build/macosapp/app-image-${architecture}/Freeplane.app/Contents/app/core/org.freeplane.core/lib")
          }
        }

        copy {
          from file ('freeplane_framework/mac-appbundler/freeplanedoc.icns')
          into "build/macosapp/app-image-${architecture}/Freeplane.app/Contents/Resources"
        }

        def dist_macos_info = "build/macosapp/app-image-${architecture}/Freeplane.app/Contents/Info.plist"
        ant.move(file:dist_macos_info, tofile:dist_macos_info + '2')

        ant.xslt(in: new File(dist_macos_info + '2'),
          style: new File('freeplane_framework/mac-appbundler/mac_info_plist.xslt'),
          out: new File(dist_macos_info)
        )

        delete dist_macos_info + '2'

      }
    }
  def signFlatlaf= tasks.create ("signFlatlaf_${architecture}", Exec) {
        onlyIf { Os.isFamily(Os.FAMILY_MAC) && macAppProperties['macapp.codesign.identity'] != null}
    commandLine 'bash', 'codesign/sign-jars-on-mac.sh',
                "build/macosapp/app-image-${architecture}/Freeplane.app/Contents/app/core/org.freeplane.core/lib/flatlaf-*.jar",
                macAppProperties['macapp.codesign.identity']
    dependsOn macosapp_makeapp
  }
  def signJline= tasks.create ("signJline_${architecture}", Exec) {
        onlyIf { Os.isFamily(Os.FAMILY_MAC) && macAppProperties['macapp.codesign.identity'] != null}
    commandLine 'bash', 'codesign/sign-jars-on-mac.sh',
                "build/macosapp/app-image-${architecture}/Freeplane.app/Contents/app/plugins/org.freeplane.plugin.script/lib/jline-*.jar",
                macAppProperties['macapp.codesign.identity']
    dependsOn macosapp_makeapp
}

  def signJnr= tasks.create ("signJnr_${architecture}", Exec) {
        onlyIf { Os.isFamily(Os.FAMILY_MAC) && macAppProperties['macapp.codesign.identity'] != null && architecture=='intel'}
    commandLine 'bash', 'codesign/sign-jars-on-mac.sh',
                "build/macosapp/app-image-${architecture}/Freeplane.app/Contents/app/core/org.freeplane.core/lib/jnr-*.jar",
                macAppProperties['macapp.codesign.identity']
    dependsOn macosapp_makeapp
}

  def signVappearances= tasks.create ("signVappearances_${architecture}", Exec) {
        onlyIf { Os.isFamily(Os.FAMILY_MAC) && macAppProperties['macapp.codesign.identity'] != null && architecture=='intel'}
    commandLine 'bash', 'codesign/sign-jars-on-mac.sh',
                "build/macosapp/app-image-${architecture}/Freeplane.app/Contents/app/core/org.freeplane.core/lib/vappearances-*.jar",
                macAppProperties['macapp.codesign.identity']
    dependsOn macosapp_makeapp
}

  def signVaqua= tasks.create ("signVaqua_${architecture}", Exec) {
        onlyIf { Os.isFamily(Os.FAMILY_MAC) && macAppProperties['macapp.codesign.identity'] != null && architecture=='intel'}
    commandLine 'bash', 'codesign/sign-jars-on-mac.sh',
                "build/macosapp/app-image-${architecture}/Freeplane.app/Contents/app/core/org.freeplane.core/lib/vaqua-*.jar",
                macAppProperties['macapp.codesign.identity']
    dependsOn macosapp_makeapp
}

  def signMacApp= tasks.create ("signMacApp_${architecture}", Exec) {
        onlyIf { Os.isFamily(Os.FAMILY_MAC) && macAppProperties['macapp.codesign.identity'] != null}

   	commandLine jpackage,
     '--type', 'app-image',
     '--app-image', "build/macosapp/app-image-${architecture}/Freeplane.app",
     '--mac-sign',
     '--mac-signing-key-user-name', macAppProperties['macapp.codesign.identity']
        dependsOn macosapp_makeapp, signFlatlaf, signJline, signJnr, signVappearances, signVaqua
    }

  def dmg4mac = tasks.create ("dmg4mac_${architecture}", Exec) {
    doFirst { 
        mkdir globalDist 
        file("$globalDist/freeplane-${majorVersion}.dmg").delete()
        file("$globalDist/freeplane-$distVersion-${architecture}.dmg").delete()
    } 

   	commandLine jpackage,
        '--name' , 'Freeplane',
        '--type', 'dmg',
        '--app-version', majorVersion,
        '--app-image', "build/macosapp/app-image-${architecture}/Freeplane.app",
        '--mac-package-identifier', 'org.freeplane.core',
        '--mac-package-name', 'Freeplane',
        '--resource-dir', 'build/macosapp/dmg-resource-dir',
        '--dest', globalDist

        dependsOn signMacApp, macosapp_copyvolumeicon

    doLast {
      ant.move (file:"$globalDist/Freeplane-${majorVersion}.dmg", tofile:"$globalDist/Freeplane-${distVersion}-${architecture}.dmg")
    }
  }
  def signDmg = tasks.create ("signDmg_${architecture}", Exec) {
    commandLine 'bash', '-e', '-c', """
    set -x; codesign --sign "Developer ID Application: ${macAppProperties['macapp.codesign.identity']}" $globalDist/Freeplane-${distVersion}-${architecture}.dmg
    """
    dependsOn dmg4mac
  }

  def notarytool = tasks.create ("notarytool_${architecture}", Exec) {
    commandLine 'bash', '-e', '-c', """
    set -x; xcrun notarytool submit $globalDist/Freeplane-${distVersion}-${architecture}.dmg --keychain-profile ${macAppProperties['macapp.notarytool.keychainprofile']} --no-s3-acceleration
    """
    dependsOn signDmg
  }
}

if ( Os.isFamily(Os.FAMILY_MAC) && macAppProperties['macapp.codesign.identity'] != null) {
  if (macAppProperties['macapp.intel.jdk.runtime.image'] != null
      && macAppProperties['macapp.intel.jpackage.jdk'] != null) {
    defineMacBuildTasks('intel')
  }
  if (macAppProperties['macapp.apple.jdk.runtime.image'] != null
      && macAppProperties['macapp.apple.jpackage.jdk'] != null) {
    defineMacBuildTasks('apple')
  }
}

import org.apache.tools.ant.filters.FixCrLfFilter

// some shorthands for often-used include(...)/exclude(...) specs:
def textFilesIncludeSpec(dir) {
    if (dir == null)
        return ['**/*.java',
                '**/*.xml',
                '**/*.xsl',
                '**/*.mm',
                '**/*.properties',
                '**/*.gradle']
    else
        return [dir + '/**/*.java',
                dir + '/**/*.xml',
                dir + '/**/*.xsl',
                dir + '/**/*.mm',
                dir + '/**/*.properties',
                dir + '/**/*.gradle']
}

def buildFiles() {
    return ['*/build/**', '**/bin/**', '*/apt_generated/**', '*/out/**']
}

def miscExcludedFilesSpec() {
    return [
			/* dot files */                  '**/.*/**',
            /* might include credentials! */ '**/signjar*.properties'
            ]
}

// NOTE: if you change excludes here, also adapt task 'srcpureTarGz' below
// (unfortunately there is some redundancy)!
task srcTarGz(type: Tar) {
    dependsOn 'binZip'
    destinationDirectory = new File(globalDist)
    archiveFileName = 'freeplane_src-' + distVersion + '.tar.gz'
    compression = 'GZIP'
    includeEmptyDirs = false

    // text files (convert to LF)
    from(rootDir) {
        include('license.txt')
        include('*.gradle')
        include(textFilesIncludeSpec('freeplane*'))
        include(textFilesIncludeSpec('JOrtho_0.4_freeplane'))

        // exclude gradle/eclipse build files for all projects:
        exclude(buildFiles())
        // exclude misc files:
        exclude(miscExcludedFilesSpec())

        filter(FixCrLfFilter.class,
            eol:FixCrLfFilter.CrLf.newInstance("lf"), fixlast:false)
    }

    // all other (source) files
    from(rootDir) {
        include('freeplane*/**')
        include('JOrtho_0.4_freeplane/**')
        exclude(textFilesIncludeSpec(null))

        // exclude gradle/eclipse build files for all projects:
        exclude(buildFiles())
        // exclude misc files:
        exclude(miscExcludedFilesSpec())
    }

    // these two files must be included because they are generated from git info
    // and git info is not available int src/srcpure distribs!
    from(globalBin) {
        include('gitinfo.txt')
    }
    from(globalBin + '/resources') {
        include('gitinfo.properties')
    }

    into('freeplane-' + distVersion)
}

def srcpureAdditionalExcludeSpec() {
    // built from latex.flex using jflex!
    return ['freeplane_plugin_jsyntaxpane/src/main/java/jsyntaxpane/lexers/LaTeXLexer.java',

    // the flash files don't have a source in the freeplane-distrib,
    // which is a problem for Debian,
    // see http://bugs.debian.org/cgi-bin/bugreport.cgi?bug=736106 
    // (the source is here: https://github.com/freeplane/misc/tree/master/flash-browser)
    // => there is an (inactive) Debian package here:
    //    https://github.com/fnatter/freeplane-flash-browser-debian/
          'freeplane/resources/flash/visorFreeplane.swf', 'freeplane/resources/flash/flashobject.js',

    // windows stuff
            '**/*.exe', '**/*.res', '**/*.jar', '**/*.dll',
    // MAC stuff
            '**/*JavaApplicationStub*']

}

task srcpureTarGz(type: Tar) {
    dependsOn 'binZip'
    destinationDirectory = new File(globalDist)
    archiveFileName = 'freeplane_srcpure-' + distVersion + '.tar.gz'
    compression = 'GZIP'
    includeEmptyDirs = false

    // text files (convert to LF)
    from(rootDir) {
        include('license.txt')
        include('*.gradle')
        include(textFilesIncludeSpec('freeplane*'))
        include(textFilesIncludeSpec('JOrtho_0.4_freeplane'))

        // exclude gradle/eclipse build files for all projects:
        exclude(buildFiles())
        // exclude misc files:
        exclude(miscExcludedFilesSpec())
        // exclude additional srcpure stuff
        exclude(srcpureAdditionalExcludeSpec())

        filter(FixCrLfFilter.class,
            eol:FixCrLfFilter.CrLf.newInstance("lf"), fixlast:false)
    }

    // all other (source) files
    from(rootDir) {
        include('freeplane*/**')
        include('JOrtho_0.4_freeplane/**')
        exclude(textFilesIncludeSpec(null))

        // exclude gradle/eclipse build files for all projects:
        exclude(buildFiles())
        // exclude misc files:
        exclude(miscExcludedFilesSpec())
        // exclude additional srcpure stuff
        exclude(srcpureAdditionalExcludeSpec())
    }

    // these two files must be included because they are generated from git info
    // and git info is not available int src/srcpure distribs!
    from(globalBin) {
        include('gitinfo.txt')
    }
    from(globalBin + '/resources') {
        include('gitinfo.properties')
    }

    into('freeplane-' + distVersion)
}

import groovy.io.FileType
import org.apache.tools.ant.taskdefs.condition.Os

String winapp_input_dir = 'BIN'

def defineWinDistTasks =  { javaRuntime, distName -> 

    def appImageDir = file("build/winapp/${javaRuntime}")
    def winapp_deleteAppImage = tasks.create("winapp_deleteAppImage_${javaRuntime}", Delete) { 
    onlyIf { Os.isFamily(Os.FAMILY_WINDOWS) }
        delete "${appImageDir}" 
    }

    def winapp_input = tasks.create("winapp_input_${javaRuntime}", Copy) {
        onlyIf { Os.isFamily(Os.FAMILY_WINDOWS) }

        from(winapp_input_dir) {
            include '*.jar'
        }

        into "${appImageDir}"

        doLast {
            mkdir "${appImageDir}/input"
        }

        dependsOn winapp_deleteAppImage

        for (p in subprojects) {
            dependsOn p.tasks.getByPath("build")
        }

    }


    def jpackage = 'jpackage'

    def jpackage_winapp = tasks.create ("jpackage_winapp_${javaRuntime}", Exec) {
        onlyIf { Os.isFamily(Os.FAMILY_WINDOWS) }

        commandLine jpackage,
                '--name' , 'freeplane',
        '--type', 'app-image',
        '--icon', 'freeplane_framework/launch4j/Freeplane_app.ico',
        '--app-version', majorVersion,
        '--java-options', "-XX:-IgnoreUnrecognizedVMOptions",
        '--java-options', "-XX:MaxRAM=20g",
        '--java-options', "-XX:MaxRAMPercentage=15.0",
        '--java-options', '-Djava.security.manager=allow',
        '--java-options', '-Dorg.freeplane.userfpdir=%APPDATA%\\Freeplane',
        '--java-options', '--add-exports', 
        '--java-options', 'java.desktop/sun.awt=ALL-UNNAMED',
        '--java-options', '--add-exports', 
        '--java-options', 'java.desktop/sun.awt.shell=ALL-UNNAMED',
        '--java-options', '--add-exports', 
        '--java-options', 'java.desktop/com.sun.java.swing.plaf.windows=ALL-UNNAMED',
        '--java-options', '--add-exports', 
        '--java-options', 'java.desktop/sun.swing=ALL-UNNAMED',
        '--java-options', '--add-opens', 
        '--java-options', 'java.desktop/javax.swing.text.html=ALL-UNNAMED',
        '--input', "build/winapp/${javaRuntime}/input",
        '--main-class', 'org.freeplane.launcher.Launcher',
        '--main-jar', '../freeplanelauncher.jar',
        '--add-launcher', 'freeplaneConsole=freeplane_framework/launch4j/freeplaneConsole.properties',
        '--runtime-image', "java-runtime-${javaRuntime}",
        '--copyright', "Freeplane ${distVersion}",
        '--dest', "${appImageDir}"
        dependsOn winapp_deleteAppImage, winapp_input

        for (p in subprojects) {
            dependsOn p.tasks.getByPath("build")
        }
    }

    def winapp_makeapp = tasks.create("winapp_makeapp_${javaRuntime}", Copy) {
        onlyIf { Os.isFamily(Os.FAMILY_WINDOWS) }
        dependsOn jpackage_winapp

        from(winapp_input_dir) {
            include '*/**'
            exclude '/*.exe'
            exclude '/*.l4j.ini'
        }

        into "${appImageDir}/Freeplane/"
    }


    def windowsInstaller = tasks.create("windowsInstaller_${javaRuntime}", Exec) {
        onlyIf { Os.isFamily(Os.FAMILY_WINDOWS) }

        ignoreExitValue = true
        workingDir = rootDir.path + '/freeplane_framework/windows-installer'

        commandLine 'cmd.exe', '/c', "ISCC.exe /DAppImage=${appImageDir} /Q Freeplane.iss "
        doLast {
            new File(workingDir, 'Freeplane-Setup.exe').renameTo(new File(globalDist, "Freeplane-Setup${distName}-${distVersion}.exe"))
        }

        dependsOn winapp_makeapp
    }

    dist {
        dependsOn windowsInstaller
    }

}

if ( Os.isFamily(Os.FAMILY_WINDOWS)) {
    defineWinDistTasks('jbr', '')
    defineWinDistTasks('zulu', '-touchscreen')
}


def portableInstallerDir = new File(globalDist, 'FreeplanePortable').path

task copyWindowsPortableInstallerBasicFiles(type: Copy) {
    onlyIf { Os.isFamily(Os.FAMILY_WINDOWS) }

    // copy windows-portable files
    from(rootDir.path + '/freeplane_framework/windows-portable') {
         include('**')
    }
    into(portableInstallerDir)
}
task copyWindowsPortableInstallerLicense(type: Copy) {
    onlyIf { Os.isFamily(Os.FAMILY_WINDOWS) }

    // copy license
    from(rootDir.path + '/freeplane/resources/') {
         include('license.txt')
         rename('license.txt', 'EULA.txt')
    }
    into(portableInstallerDir + '/Other/Source/')
}
task copyWindowsPortableInstallerIcon(type: Copy) {
    onlyIf { Os.isFamily(Os.FAMILY_WINDOWS) }

    // copy icon
    from(rootDir.path + '/freeplane_framework/launch4j') {
        include('Freeplane_app.ico')
        rename('Freeplane_app.ico', 'appicon.ico')
    }
    into(portableInstallerDir + '/App/AppInfo/')
}


task copyWindowsPortableInstallerFiles(type: Copy) {
    onlyIf { Os.isFamily(Os.FAMILY_WINDOWS) }

    // copy INI I
    from(rootDir.path + '/freeplane_framework/portableApps') {
        include('portable.ini')
        rename('portable.ini', 'freeplane.l4j.ini')
    }

    // copy INI II
    from(rootDir.path + '/freeplane_framework/portableApps') {
        include('portable.ini')
        rename('portable.ini', 'freeplaneConsole.l4j.ini')
    }

    // copy all other files
    from (globalBin) {
        exclude('*.bat', '*.ini', '*.sh')
    }
    into(portableInstallerDir + '/App/Freeplane/')

    for (p in subprojects) {
        dependsOn p.tasks.getByPath("build")
    }
    dependsOn copyWindowsPortableInstallerBasicFiles
    dependsOn copyWindowsPortableInstallerLicense
    dependsOn copyWindowsPortableInstallerIcon
}


task windowsPortableInstaller(type: Exec) {
    onlyIf { Os.isFamily(Os.FAMILY_WINDOWS) }

    // continue the build when running windows but PortableApps.comInstaller.exe is not installed?
    ignoreExitValue = true
    workingDir = rootDir.path + '/freeplane_framework'

    commandLine 'cmd.exe', '/c', 'PortableApps.comInstaller.exe', portableInstallerDir

    dependsOn copyWindowsPortableInstallerFiles

    doLast {
         new File(portableInstallerDir).deleteDir() 
         new File(globalDist).eachFileMatch(FileType.FILES, ~/FreeplanePortable_.*\.paf\.exe/) {
			 f -> 
			 f.renameTo(new File(globalDist, "FreeplanePortable-${distVersion}.paf.exe"))
		 }
    }
}
