// Top-level build file where you can add configuration options common to all sub-projects/modules.

buildscript {

    repositories {
        google()
        jcenter()
    }

    dependencies {
        classpath Plugins.android

        // Publish to Jcenter/Bintray
        classpath Plugins.bintray
        classpath Plugins.androidMaven

        // Kotlin Plugin
        classpath Plugins.kotlin

        // NOTE: Do not place your application dependencies here; they belong
        // in the individual module build.gradle files
    }
}

allprojects {
    repositories {
        google()
        jcenter()
    }
}

ext {
    // Library Info
    libVersionCode = Versions.libraryCode
    libVersionName = Versions.libraryName

    libPomUrl = LibraryConfig.libPomUrl
    libGithubRepo = LibraryConfig.libGithubRepo

    libModuleName = LibraryConfig.libModuleName
    libModuleDesc = LibraryConfig.libModuleDesc
    libBintrayName = LibraryConfig.libBintrayName
}

