// Top-level build file where you can add configuration options common to all sub-projects/modules.
buildscript {
  repositories {
    google()
    jcenter()
  }
  dependencies {
    classpath 'com.android.tools.build:gradle:4.1.2'
    classpath 'com.jfrog.bintray.gradle:gradle-bintray-plugin:1.8.4'
    classpath 'com.ofg:uptodate-gradle-plugin:1.6.2'
  }
}

allprojects {
  repositories {
    google()
    jcenter()
  }
  tasks.withType(JavaCompile) {
    sourceCompatibility = rootProject.ext.javaVersion
    targetCompatibility = rootProject.ext.javaVersion
  }

  tasks.withType(GroovyCompile) {
    sourceCompatibility = rootProject.ext.javaVersion
    targetCompatibility = rootProject.ext.javaVersion
  }
}

ext {
  javaVersion = JavaVersion.VERSION_1_8

  GROUP = 'com.tencent.mm'
  VERSION_NAME = "${ANDRESGUARD_VESSION}"

  POM_PACKAGING = "pom"
  POM_DESCRIPTION = "Android Resource Proguard Core Lib"

  POM_URL = "https://github.com/shwenzhang/AndResGuard"
  POM_SCM_URL = "https://github.com/shwenzhang/AndResGuard.git"
  POM_ISSUE_URL = 'https://github.com/shwenzhang/AndResGuard/issues'

  POM_LICENCE_NAME = "Apache-2.0"
  POM_LICENCE_URL = " http://www.apache.org/licenses/"
  POM_LICENCE_DIST = "repo"

  POM_DEVELOPER_ID = "Tencent Wechat"
  POM_DEVELOPER_NAME = "Tencent Wechat, Inc."

  BINTRAY_LICENCE = ["Apache-2.0"]
  BINTRAY_ORGANIZATION = "wemobiledev"
}

{
  "name": "AndResGuard",
  "version": "1.0.0",
  "description": "[![Join the chat at https://gitter.im/shwenzhang/AndResGuard](https://badges.gitter.im/shwenzhang/AndResGuard.svg)](https://gitter.im/shwenzhang/AndResGuard?utm_source=badge&utm_medium=badge&utm_campaign=pr-badge&utm_content=badge) [![Build Status](https://travis-ci.org/shwenzhang/AndResGuard.svg?branch=master)](https://travis-ci.org/shwenzhang/AndResGuard) [![Jcenter Status](https://api.bintray.com/packages/simsun/maven/AndResGuard-gradle-plugin/images/download.svg)](https://bintray.com/simsun/maven/AndResGuard-gradle-plugin) [![license](http://img.shields.io/badge/license-apache_2.0-red.svg?style=flat)](https://raw.githubusercontent.com/shwenzhang/AndResGuard/master/LICENSE)",
  "main": "index.js",
  "scripts": {
    "test": "echo \"Error: no test specified\" && exit 1"
