'use strict';

function qunitVersion() {
  var prepareStackTrace = Error.prepareStackTrace;
  Error.prepareStackTrace = function() {
    return '';
  };
  try {
    return require('qunit').version;
  }
  finally {
    Error.prepareStackTrace = prepareStackTrace;
  }
}

module.exports = function(grunt) {
  // Force use of Unix newlines
  grunt.util.linefeed = '\n';

  grunt.initConfig({
    pkg: grunt.file.readJSON('package.json'),
    qunit_ver: qunitVersion(),
    banner: '/*!\n' +
            ' * HTMLMinifier v<%= pkg.version %> (<%= pkg.homepage %>)\n' +
            ' * Copyright 2010-<%= grunt.template.today("yyyy") %> <%= pkg.author %>\n' +
            ' * Licensed under the <%= pkg.license %> license\n' +
            ' */\n',

    browserify: {
      src: {
        options: {
          banner: '<%= banner %>',
          preBundleCB: function() {
            var fs = require('fs');
            var UglifyJS = require('uglify-js');
            var files = {};
            UglifyJS.FILES.forEach(function(file) {
              files[file] = fs.readFileSync(file, 'utf8');
            });
            fs.writeFileSync('./dist/uglify.js', UglifyJS.minify(files, {
              compress: false,
              mangle: false,
              wrap: 'exports'
            }).code);
          },
          postBundleCB: function(err, src, next) {
            require('fs').unlinkSync('./dist/uglify.js');
            next(err, src);
          },
          require: [
            './dist/uglify.js:uglify-js',
            './src/htmlminifier.js:html-minifier'
          ]
        },
        src: 'src/htmlminifier.js',
        dest: 'dist/htmlminifier.js'
      }
    },

    eslint: {
      grunt: {
        src: 'Gruntfile.js'
      },
      src: {
        src: ['cli.js', 'src/**/*.js']
      },
      tests: {
        src: ['tests/*.js', 'test.js']
      },
      web: {
        src: ['assets/master.js', 'assets/worker.js']
      },
      other: {
        src: ['backtest.js', 'benchmark.js']
      }
    },

    qunit: {
      htmlminifier: ['./tests/minifier', 'tests/index.html']
    },

    replace: {
      './index.html': [
        /(<h1>.*?<span>).*?(<\/span><\/h1>)/,
        '$1(v<%= pkg.version %>)$2'
      ],
      './tests/index.html': [
        /("[^"]+\/qunit-)[0-9.]+?(\.(?:css|js)")/g,
        '$1<%= qunit_ver %>$2'
      ]
    },

    uglify: {
      options: {
        banner: '<%= banner %>',
        compress: true,
        mangle: true,
        preserveComments: false,
        report: 'min'
      },
      minify: {
        files: {
          'dist/htmlminifier.min.js': '<%= browserify.src.dest %>'
        }
      }
    }
  });

  grunt.loadNpmTasks('grunt-browserify');
  grunt.loadNpmTasks('grunt-contrib-uglify');
  grunt.loadNpmTasks('grunt-eslint');

  function report(type, details) {
    grunt.log.writeln(type + ' completed in ' + details.runtime + 'ms');
    details.failures.forEach(function(details) {
      grunt.log.error();
      grunt.log.error(details.name + (details.message ? ' [' + details.message + ']' : ''));
      grunt.log.error(details.source);
      grunt.log.error('Actual:');
      grunt.log.error(details.actual);
      grunt.log.error('Expected:');
      grunt.log.error(details.expected);
    });
    grunt.log[details.failed ? 'error' : 'ok'](details.passed + ' of ' + details.total + ' passed, ' + details.failed + ' failed');
    return details.failed;
  }

  var phantomjs = require('phantomjs-prebuilt').path;
  grunt.registerMultiTask('qunit', function() {
    var done = this.async();
    var errors = [];

    function run(testType, binPath, testPath) {
      grunt.util.spawn({
        cmd: binPath,
        args: ['test.js', testPath]
      }, function(error, result) {
        if (error) {
          grunt.log.error(result.stderr);
          grunt.log.error(testType + ' test failed to load');
          errors.push(-1);
        }
        else {
          var output = result.stdout;
          var index = output.lastIndexOf('\n');
          if (index !== -1) {
            // There's something before the report JSON
            // Log it to the console -- it's probably some debug output:
            console.log(output.slice(0, index));
            output = output.slice(index);
          }
          errors.push(report(testType, JSON.parse(output)));
        }
        if (errors.length === 2) {
          done(!errors[0] && !errors[1]);
        }
      });
    }

    run('node', process.argv[0], this.data[0]);
    run('web', phantomjs, this.data[1]);
  });

  grunt.registerMultiTask('replace', function() {
    var pattern = this.data[0];
    var path = this.target;
    var html = grunt.file.read(path);
    html = html.replace(pattern, this.data[1]);
    grunt.file.write(path, html);
  });

  grunt.registerTask('dist', [
    'replace',
    'browserify',
    'uglify'
  ]);

  grunt.registerTask('test', [
    'eslint',
    'dist',
    'qunit'
  ]);

  grunt.registerTask('default', 'test');
};

#!/usr/bin/env node

'use strict';

var child_process = require('child_process'),
    fs = require('fs'),
    os = require('os'),
    path = require('path'),
    Progress = require('progress');

var urls = require('./benchmarks');
var fileNames = Object.keys(urls);

function git() {
  var args = [].concat.apply([], [].slice.call(arguments, 0, -1));
  var callback = arguments[arguments.length - 1];
  var task = child_process.spawn('git', args, { stdio: ['ignore', 'pipe', 'ignore'] });
  var output = '';
  task.stdout.setEncoding('utf8');
  task.stdout.on('data', function(data) {
    output += data;
  });
  task.on('exit', function(code) {
    callback(code, output);
  });
}

function readText(filePath, callback) {
  fs.readFile(filePath, { encoding: 'utf8' }, callback);
}

function writeText(filePath, data) {
  fs.writeFile(filePath, data, { encoding: 'utf8' }, function(err) {
    if (err) {
      throw err;
    }
  });
}

function loadModule() {
  require('./src/htmlparser');
  return require('./src/htmlminifier').minify || global.minify;
}

function getOptions(fileName, options) {
  var result = {
    minifyURLs: {
      site: urls[fileName]
    }
  };
  for (var key in options) {
    result[key] = options[key];
  }
  return result;
}

function minify(hash, options) {
  var minify = loadModule();
  process.send('ready');
  var count = fileNames.length;
  fileNames.forEach(function(fileName) {
    readText(path.join('benchmarks/', fileName + '.html'), function(err, data) {
      if (err) {
        throw err;
      }
      else {
        try {
          var minified = minify(data, getOptions(fileName, options));
          if (minified) {
            process.send({ name: fileName, size: minified.length });
          }
          else {
            throw new Error('unexpected result: ' + minified);
          }
        }
        catch (e) {
          console.error('[' + fileName + ']', e.stack || e);
        }
        finally {
          if (!--count) {
            process.disconnect();
          }
        }
      }
    });
  });
}

function print(table) {
  var output = [];
  var errors = [];
  var row = fileNames.slice(0);
  row.unshift('hash', 'date');
  output.push(row.join(','));
  for (var hash in table) {
    var data = table[hash];
    row = [hash, '"' + data.date + '"'];
    fileNames.forEach(function(fileName) {
      row.push(data[fileName]);
    });
    output.push(row.join(','));
    if (data.error) {
      errors.push(hash + ' - ' + data.error);
    }
  }
  writeText('backtest.csv', output.join('\n'));
  writeText('backtest.log', errors.join('\n'));
}

if (process.argv.length > 2) {
  var count = +process.argv[2];
  if (count) {
    git('log', '--date=iso', '--pretty=format:%h %cd', '-' + count, function(code, data) {
      var table = {};
      var commits = data.split(/\s*?\n/).map(function(line) {
        var index = line.indexOf(' ');
        var hash = line.substr(0, index);
        table[hash] = {
          date: line.substr(index + 1).replace('+', '').replace(/ 0000$/, '')
        };
        return hash;
      });
      var nThreads = os.cpus().length;
      var running = 0;
      var progress = new Progress('[:bar] :etas', {
        width: 50,
        total: commits.length * 2
      });

      function fork() {
        if (commits.length && running < nThreads) {
          var hash = commits.shift();
          var task = child_process.fork('./backtest', { silent: true });
          var error = '';
          var id = setTimeout(function() {
            if (task.connected) {
              error += 'task timed out\n';
              task.kill();
            }
          }, 60000);
          task.on('message', function(data) {
            if (data === 'ready') {
              progress.tick(1);
              fork();
            }
            else {
              table[hash][data.name] = data.size;
            }
          }).on('exit', function() {
            progress.tick(1);
            clearTimeout(id);
            if (error) {
              table[hash].error = error;
            }
            if (!--running && !commits.length) {
              print(table);
            }
            else {
              fork();
            }
          });
          task.stderr.setEncoding('utf8');
          task.stderr.on('data', function(data) {
            error += data;
          });
          task.stdout.resume();
          task.send(hash);
          running++;
        }
      }

      fork();
    });
  }
  else {
    console.error('Invalid input:', process.argv[2]);
  }
}
else {
  process.on('message', function(hash) {
    var paths = ['src', 'benchmark.conf', 'sample-cli-config-file.conf'];
    git('reset', 'HEAD', '--', paths, function() {
      var conf = 'sample-cli-config-file.conf';

      function checkout() {
        var path = paths.shift();
        git('checkout', hash, '--', path, function(code) {
          if (code === 0 && path === 'benchmark.conf') {
            conf = path;
          }
          if (paths.length) {
            checkout();
          }
          else {
            readText(conf, function(err, data) {
              if (err) {
                throw err;
              }
              else {
                minify(hash, JSON.parse(data));
              }
            });
          }
        });
      }

      checkout();
    });
  });
}

#!/usr/bin/env node

'use strict';

var packages = require('./package.json').benchmarkDependencies;
packages = Object.keys(packages).map(function(name) {
  return name + '@' + packages[name];
});
packages.unshift('install', '--no-save', '--no-optional');
var installed = require('child_process').spawnSync('npm', packages, {
  encoding: 'utf-8',
  shell: true
});
if (installed.error) {
  throw installed.error;
}
else if (installed.status) {
  console.log(installed.stdout);
  console.error(installed.stderr);
  process.exit(installed.status);
}

var brotli = require('brotli'),
    chalk = require('chalk'),
    fork = require('child_process').fork,
    fs = require('fs'),
    https = require('https'),
    lzma = require('lzma'),
    Minimize = require('minimize'),
    path = require('path'),
    Progress = require('progress'),
    querystring = require('querystring'),
    Table = require('cli-table'),
    url = require('url'),
    zlib = require('zlib');

var urls = require('./benchmarks');
var fileNames = Object.keys(urls);

var minimize = new Minimize();

var progress = new Progress('[:bar] :etas :fileName', {
  width: 50,
  total: fileNames.length
});

var table = new Table({
  head: ['File', 'Before', 'After', 'Minimize', 'Will Peavy', 'htmlcompressor.com', 'Savings', 'Time'],
  colWidths: [fileNames.reduce(function(length, fileName) {
    return Math.max(length, fileName.length);
  }, 0) + 2, 25, 25, 25, 25, 25, 20, 10]
});

function toKb(size, precision) {
  return (size / 1024).toFixed(precision || 0);
}

function redSize(size) {
  return chalk.red.bold(size) + chalk.white(' (' + toKb(size, 2) + ' KB)');
}

function greenSize(size) {
  return chalk.green.bold(size) + chalk.white(' (' + toKb(size, 2) + ' KB)');
}

function blueSavings(oldSize, newSize) {
  var savingsPercent = (1 - newSize / oldSize) * 100;
  var savings = oldSize - newSize;
  return chalk.cyan.bold(savingsPercent.toFixed(2)) + chalk.white('% (' + toKb(savings, 2) + ' KB)');
}

function blueTime(time) {
  return chalk.cyan.bold(time) + chalk.white(' ms');
}

function readBuffer(filePath, callback) {
  fs.readFile(filePath, function(err, data) {
    if (err) {
      throw new Error('There was an error reading ' + filePath);
    }
    callback(data);
  });
}

function readText(filePath, callback) {
  fs.readFile(filePath, { encoding: 'utf8' }, function(err, data) {
    if (err) {
      throw new Error('There was an error reading ' + filePath);
    }
    callback(data);
  });
}

function writeBuffer(filePath, data, callback) {
  fs.writeFile(filePath, data, function(err) {
    if (err) {
      throw new Error('There was an error writing ' + filePath);
    }
    callback();
  });
}

function writeText(filePath, data, callback) {
  fs.writeFile(filePath, data, { encoding: 'utf8' }, function(err) {
    if (err) {
      throw new Error('There was an error writing ' + filePath);
    }
    if (callback) {
      callback();
    }
  });
}

function readSize(filePath, callback) {
  fs.stat(filePath, function(err, stats) {
    if (err) {
      throw new Error('There was an error reading ' + filePath);
    }
    callback(stats.size);
  });
}

function gzip(inPath, outPath, callback) {
  fs.createReadStream(inPath).pipe(zlib.createGzip({
    level: zlib.Z_BEST_COMPRESSION
  })).pipe(fs.createWriteStream(outPath)).on('finish', callback);
}

function run(tasks, done) {
  var i = 0;

  function callback() {
    if (i < tasks.length) {
      tasks[i++](callback);
    }
    else {
      done();
    }
  }

  callback();
}

var rows = {};

function generateMarkdownTable() {
  var headers = [
    'Site',
    'Original size *(KB)*',
    'HTMLMinifier',
    'minimize',
    'Will Peavy',
    'htmlcompressor.com'
  ];
  fileNames.forEach(function(fileName) {
    var row = rows[fileName].report;
    row[2] = '**' + row[2] + '**';
  });
  var widths = headers.map(function(header, index) {
    var width = header.length;
    fileNames.forEach(function(fileName) {
      width = Math.max(width, rows[fileName].report[index].length);
    });
    return width;
  });
  var content = '';

  function output(row) {
    widths.forEach(function(width, index) {
      var text = row[index];
      content += '| ' + text + new Array(width - text.length + 2).join(' ');
    });
    content += '|\n';
  }

  output(headers);
  widths.forEach(function(width, index) {
    content += '|';
    content += index === 1 ? ':' : ' ';
    content += new Array(width + 1).join('-');
    content += index === 0 ? ' ' : ':';
  });
  content += '|\n';
  fileNames.sort(function(a, b) {
    var r = +rows[a].report[1];
    var s = +rows[b].report[1];
    return r < s ? -1 : r > s ? 1 : a < b ? -1 : a > b ? 1 : 0;
  }).forEach(function(fileName) {
    output(rows[fileName].report);
  });
  return content;
}

function displayTable() {
  fileNames.forEach(function(fileName) {
    table.push(rows[fileName].display);
  });
  console.log();
  console.log(table.toString());
}

run(fileNames.map(function(fileName) {
  var filePath = path.join('benchmarks/', fileName + '.html');

  function processFile(site, done) {
    var original = {
      filePath: filePath,
      gzFilePath: path.join('benchmarks/generated/', fileName + '.html.gz'),
      lzFilePath: path.join('benchmarks/generated/', fileName + '.html.lz'),
      brFilePath: path.join('benchmarks/generated/', fileName + '.html.br')
    };
    var infos = {};
    ['minifier', 'minimize', 'willpeavy', 'compressor'].forEach(function(name) {
      infos[name] = {
        filePath: path.join('benchmarks/generated/', fileName + '.' + name + '.html'),
        gzFilePath: path.join('benchmarks/generated/', fileName + '.' + name + '.html.gz'),
        lzFilePath: path.join('benchmarks/generated/', fileName + '.' + name + '.html.lz'),
        brFilePath: path.join('benchmarks/generated/', fileName + '.' + name + '.html.br')
      };
    });

    function readSizes(info, done) {
      info.endTime = Date.now();
      run([
        // Apply Gzip on minified output
        function(done) {
          gzip(info.filePath, info.gzFilePath, function() {
            info.gzTime = Date.now();
            // Open and read the size of the minified+gzip output
            readSize(info.gzFilePath, function(size) {
              info.gzSize = size;
              done();
            });
          });
        },
        // Apply LZMA on minified output
        function(done) {
          readBuffer(info.filePath, function(data) {
            lzma.compress(data, 1, function(result, error) {
              if (error) {
                throw error;
              }
              writeBuffer(info.lzFilePath, new Buffer(result), function() {
                info.lzTime = Date.now();
                // Open and read the size of the minified+lzma output
                readSize(info.lzFilePath, function(size) {
                  info.lzSize = size;
                  done();
                });
              });
            });
          });
        },
        // Apply Brotli on minified output
        function(done) {
          readBuffer(info.filePath, function(data) {
            var output = new Buffer(brotli.compress(data, true).buffer);
            writeBuffer(info.brFilePath, output, function() {
              info.brTime = Date.now();
              // Open and read the size of the minified+brotli output
              readSize(info.brFilePath, function(size) {
                info.brSize = size;
                done();
              });
            });
          });
        },
        // Open and read the size of the minified output
        function(done) {
          readSize(info.filePath, function(size) {
            info.size = size;
            done();
          });
        }
      ], done);
    }

    function testHTMLMinifier(done) {
      var info = infos.minifier;
      info.startTime = Date.now();
      var args = [filePath, '-c', 'sample-cli-config-file.conf', '--minify-urls', site, '-o', info.filePath];
      fork('./cli', args).on('exit', function() {
        readSizes(info, done);
      });
    }

    function testMinimize(done) {
      readBuffer(filePath, function(data) {
        minimize.parse(data, function(error, data) {
          var info = infos.minimize;
          writeBuffer(info.filePath, data, function() {
            readSizes(info, done);
          });
        });
      });
    }

    function testWillPeavy(done) {
      readText(filePath, function(data) {
        var options = url.parse('https://www.willpeavy.com/minifier/');
        options.method = 'POST';
        options.headers = {
          'Content-Type': 'application/x-www-form-urlencoded'
        };
        https.request(options, function(res) {
          res.setEncoding('utf8');
          var response = '';
          res.on('data', function(chunk) {
            response += chunk;
          }).on('end', function() {
            var info = infos.willpeavy;
            if (res.statusCode === 200) {
              // Extract result from <textarea/>
              var start = response.indexOf('>', response.indexOf('<textarea'));
              var end = response.lastIndexOf('</textarea>');
              var result = response.slice(start + 1, end).replace(/<\\\//g, '</');
              writeText(info.filePath, result, function() {
                readSizes(info, done);
              });
            }
            // Site refused to process content
            else {
              info.size = 0;
              info.gzSize = 0;
              info.lzSize = 0;
              info.brSize = 0;
              done();
            }
          });
        }).end(querystring.stringify({
          html: data
        }));
      });
    }

    function testHTMLCompressor(done) {
      readText(filePath, function(data) {
        var options = url.parse('https://htmlcompressor.com/compress_ajax_v2.php');
        options.method = 'POST';
        options.headers = {
          'Accept-Encoding': 'gzip',
          'Content-Type': 'application/x-www-form-urlencoded'
        };
        var info = infos.compressor;

        function failed() {
          // Site refused to process content
          if (info) {
            info.size = 0;
            info.gzSize = 0;
            info.lzSize = 0;
            info.brSize = 0;
            info = null;
            done();
          }
        }

        https.request(options, function(res) {
          if (res.headers['content-encoding'] === 'gzip') {
            res = res.pipe(zlib.createGunzip());
          }
          res.setEncoding('utf8');
          var response = '';
          res.on('data', function(chunk) {
            response += chunk;
          }).on('end', function() {
            try {
              response = JSON.parse(response);
            }
            catch (e) {
              response = {};
            }
            if (info && response.success) {
              writeText(info.filePath, response.result, function() {
                readSizes(info, done);
              });
            }
            // Site refused to process content
            else {
              failed();
            }
          });
        }).on('error', failed).end(querystring.stringify({
          code_type: 'html',
          html_level: 3,
          html_strip_quotes: 1,
          minimize_style: 1,
          minimize_events: 1,
          minimize_js_href: 1,
          minimize_css: 1,
          minimize_js: 1,
          html_optional_cdata: 1,
          js_engine: 'yui',
          js_fallback: 1,
          code: data
        }));
      });
    }

    run([
      function(done) {
        readSizes(original, done);
      },
      testHTMLMinifier,
      testMinimize,
      testWillPeavy,
      testHTMLCompressor
    ], function() {
      var display = [
        [fileName, '+ gzip', '+ lzma', '+ brotli'].join('\n'),
        [redSize(original.size), redSize(original.gzSize), redSize(original.lzSize), redSize(original.brSize)].join('\n')
      ];
      var report = [
        '[' + fileName + '](' + urls[fileName] + ')',
        toKb(original.size)
      ];
      for (var name in infos) {
        var info = infos[name];
        display.push([greenSize(info.size), greenSize(info.gzSize), greenSize(info.lzSize), greenSize(info.brSize)].join('\n'));
        report.push(info.size ? toKb(info.size) : 'n/a');
      }
      display.push(
        [
          blueSavings(original.size, infos.minifier.size),
          blueSavings(original.gzSize, infos.minifier.gzSize),
          blueSavings(original.lzSize, infos.minifier.lzSize),
          blueSavings(original.brSize, infos.minifier.brSize)
        ].join('\n'),
        [
          blueTime(infos.minifier.endTime - infos.minifier.startTime),
          blueTime(original.gzTime - original.endTime),
          blueTime(original.lzTime - original.gzTime),
          blueTime(original.brTime - original.lzTime)
        ].join('\n')
      );
      rows[fileName] = {
        display: display,
        report: report
      };
      progress.tick({ fileName: '' });
      done();
    });
  }

  function get(site, callback) {
    var options = url.parse(site);
    https.get(options, function(res) {
      var status = res.statusCode;
      if (status === 200) {
        if (res.headers['content-encoding'] === 'gzip') {
          res = res.pipe(zlib.createGunzip());
        }
        res.pipe(fs.createWriteStream(filePath)).on('finish', function() {
          callback(site);
        });
      }
      else if (status >= 300 && status < 400 && res.headers.location) {
        get(url.resolve(site, res.headers.location), callback);
      }
      else {
        throw new Error('HTTP error ' + status + '\n' + site);
      }
    });
  }

  return function(done) {
    progress.tick(0, { fileName: fileName });
    get(urls[fileName], function(site) {
      processFile(site, done);
    });
  };
}), function() {
  displayTable();
  var content = generateMarkdownTable();
  var readme = './README.md';
  readText(readme, function(data) {
    var start = data.indexOf('## Minification comparison');
    start = data.indexOf('|', start);
    var end = data.indexOf('##', start);
    end = data.lastIndexOf('|\n', end) + '|\n'.length;
    data = data.slice(0, start) + content + data.slice(end);
    writeText(readme, data);
  });
});

