{
  "name": "woocommerce/FlexSlider",
  "description": "An awesome, fully responsive jQuery slider toolkit.",
  "keywords": ["flexslider","slider","jquery","woocommerce","automattic"],
  "homepage": "https://github.com/woocommerce/FlexSlider"
}

