{
  "parser": "typescript-eslint-parser",
  "parserOptions": {
    "ecmaVersion": 2017
  },
  "env": {
    "es6": true,
    "node": true
  },
  "extends": "eslint:recommended",
  "rules": {
    "linebreak-style": [
      "error",
      "unix"
    ],
    "quotes": [
      "error",
      "single"
    ],
    "semi": [
      "error",
      "always"
    ],
    "max-len": [
      "warn",
      { 
        "code": 120
      }
    ]
  }
}

const testConfig = require('./bitcore-test.config.json');

testConfig.bitcoreNode.chains.BTC.regtest.trustedPeers[0].host = 'bitcoin';
testConfig.bitcoreNode.chains.BTC.regtest.rpc.host = 'bitcoin';

testConfig.bitcoreNode.chains.BCH.regtest.trustedPeers[0].host = 'bitcoin';
testConfig.bitcoreNode.chains.BCH.regtest.rpc.host = 'bitcoin';

testConfig.bitcoreNode.chains.ETH.regtest.trustedPeers[0].host = 'geth';
testConfig.bitcoreNode.chains.ETH.regtest.trustedPeers[0].port = 30303;
testConfig.bitcoreNode.chains.ETH.regtest.providers[0].host = 'erigon';
testConfig.bitcoreNode.chains.ETH.regtest.providers[0].port = 8545;
testConfig.bitcoreNode.chains.ETH.regtest.providers[1].host = 'geth';
testConfig.bitcoreNode.chains.ETH.regtest.providers[1].port = 8546;

testConfig.bitcoreNode.chains.MATIC.regtest.trustedPeers[0].host = 'geth';
testConfig.bitcoreNode.chains.MATIC.regtest.trustedPeers[0].port = 30303;
testConfig.bitcoreNode.chains.MATIC.regtest.providers[0].host = 'geth';
testConfig.bitcoreNode.chains.MATIC.regtest.providers[0].port = 8546;

testConfig.bitcoreNode.chains.XRP.testnet.provider.host = 'rippled';
testConfig.bitcoreNode.chains.XRP.testnet.provider.port = 6006;
testConfig.bitcoreNode.chains.XRP.testnet.provider.dataHost = 'rippled';

module.exports = testConfig;

{
  "bitcoreNode": {
    "services": {
      "api": {
        "wallets": {
          "allowCreationBeforeCompleteSync": true
        }
      }
    },
    "chains": {
      "BTC": {
        "regtest": {
          "chainSource": "p2p",
          "trustedPeers": [
            {
              "host": "localhost",
              "port": 8332
            }
          ],
          "rpc": {
            "host": "localhost",
            "port": 8333,
            "username": "bitcorenodetest",
            "password": "local321"
          }
        }
      },
      "BCH": {
        "regtest": {
          "chainSource": "p2p",
          "trustedPeers": [
            {
              "host": "localhost",
              "port": 8332
            }
          ],
          "rpc": {
            "host": "localhost",
            "port": 8333,
            "username": "bitcorenodetest",
            "password": "local321"
          }
        }
      },
      "ETH": {
        "regtest": {
          "chainSource": "p2p",
          "trustedPeers": [
            {
              "host": "localhost",
              "port": 40404
            }
          ],
          "providers": [
            {
              "host": "localhost",
              "protocol": "ws",
              "port": 9545
            },
            {
              "host": "localhost",
              "protocol": "ws",
              "port": 9556
            }
          ],
          "threads": 0
        }
      },
      "MATIC": {
        "regtest": {
          "chainSource": "p2p",
          "trustedPeers": [
            {
              "host": "localhost",
              "port": 40404
            }
          ],
          "providers": [
            {
              "host": "localhost",
              "protocol": "ws",
              "port": 9556
            }
          ],
          "threads": 0
        }
      },
      "BASE": {
        "testnet": {
          "chainSource": "external",
          "module": "./moralis",
          "trustedPeers": [
            {
              "host": "localhost",
              "port": "30012/ws"
            }
          ],
          "provider": {
            "host": "docs-demo.base-mainnet.quiknode.pro/",
            "protocol": "https",
            "port": "",
            "dataType": "historical"
          },
          "providers": [
            {
              "host": "docs-demo.base-mainnet.quiknode.pro/",
              "protocol": "https",
              "port": "",
              "dataType": "combined"
            }
          ]
        }
      },
      "XRP": {
        "testnet": {
          "chainSource": "rpc",
          "provider": {
            "host": "localhost",
            "protocol": "ws",
            "port": 6996,
            "dataHost": "localhost"
          }
        }
      }
    },
    "aliasMapping": {
      "chains": {
        "BTC": "BTC"
      },
      "networks": {
        "BTC": {
          "testnet3": "testnet",
          "testnet4": "testnet"
        }
      }
    }
  }
}

