#!/usr/bin/env node
import process from 'node:process';
import path from 'node:path';
import fs from 'node:fs';
import {fileURLToPath} from 'node:url';
import meow from 'meow';
import appdmg from 'appdmg';
import plist from 'plist';
import Ora from 'ora';
import {execa} from 'execa';
import addLicenseAgreementIfNeeded from './sla.js';
import composeIcon from './compose-icon.js';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

if (process.platform !== 'darwin') {
	console.error('macOS only');
	process.exit(1);
}

const cli = meow(`
	Usage
	  $ create-dmg <app> [destination]

	Options
	  --overwrite          Overwrite existing DMG with the same name
	  --identity=<value>   Manually set code signing identity (automatic by default)
	  --dmg-title=<value>  Manually set DMG title (must be <=27 characters) [default: App name]

	Examples
	  $ create-dmg 'Lungo.app'
	  $ create-dmg 'Lungo.app' Build/Releases
`, {
	importMeta: import.meta,
	flags: {
		overwrite: {
			type: 'boolean',
		},
		identity: {
			type: 'string',
		},
		dmgTitle: {
			type: 'string',
		},
	},
});

let [appPath, destinationPath] = cli.input;

if (!appPath) {
	console.error('Specify an app');
	process.exit(1);
}

if (!destinationPath) {
	destinationPath = process.cwd();
}

const infoPlistPath = path.join(appPath, 'Contents/Info.plist');

let infoPlist;
try {
	infoPlist = fs.readFileSync(infoPlistPath, 'utf8');
} catch (error) {
	if (error.code === 'ENOENT') {
		console.error(`Could not find \`${path.relative(process.cwd(), appPath)}\``);
		process.exit(1);
	}

	throw error;
}

const ora = new Ora('Creating DMG');
ora.start();

async function init() {
	let appInfo;
	try {
		appInfo = plist.parse(infoPlist);
	} catch {
		const {stdout} = await execa('/usr/bin/plutil', ['-convert', 'xml1', '-o', '-', infoPlistPath]);
		appInfo = plist.parse(stdout);
	}

	const appName = appInfo.CFBundleDisplayName ?? appInfo.CFBundleName;
	if (!appName) {
		throw new Error('The app must have `CFBundleDisplayName` or `CFBundleName` defined in its `Info.plist`.');
	}

	const dmgTitle = cli.flags.dmgTitle ?? appName;
	const dmgFilename = `${appName} ${appInfo.CFBundleShortVersionString}.dmg`;
	const dmgPath = path.join(destinationPath, dmgFilename);

	if (dmgTitle.length > 27) {
		ora.fail('The disk image title cannot exceed 27 characters. This is a limitation in a dependency: https://github.com/LinusU/node-alias/issues/7');
		process.exit(1);
	}

	if (cli.flags.overwrite) {
		try {
			fs.unlinkSync(dmgPath);
		} catch {}
	}

	const hasAppIcon = appInfo.CFBundleIconFile;
	let composedIconPath;
	if (hasAppIcon) {
		ora.text = 'Creating icon';
		const appIconName = appInfo.CFBundleIconFile.replace(/\.icns/, '');
		composedIconPath = await composeIcon(path.join(appPath, 'Contents/Resources', `${appIconName}.icns`));
	}

	const dmgFormat = 'ULFO'; // ULFO requires macOS 10.11+
	const dmgFilesystem = 'APFS'; // APFS requires macOS 10.13+

	const ee = appdmg({
		target: dmgPath,
		basepath: process.cwd(),
		specification: {
			title: dmgTitle,
			icon: composedIconPath,
			//
			// Use transparent background and `background-color` option when this is fixed:
			// https://github.com/LinusU/node-appdmg/issues/135
			background: path.join(__dirname, 'assets/dmg-background.png'),
			'icon-size': 160,
			format: dmgFormat,
			filesystem: dmgFilesystem,
			window: {
				size: {
					width: 660,
					height: 400,
				},
			},
			contents: [
				{
					x: 180,
					y: 170,
					type: 'file',
					path: appPath,
				},
				{
					x: 480,
					y: 170,
					type: 'link',
					path: '/Applications',
				},
			],
		},
	});

	ee.on('progress', info => {
		if (info.type === 'step-begin') {
			ora.text = info.title;
		}
	});

	ee.on('finish', async () => {
		try {
			ora.text = 'Adding Software License Agreement if needed';
			await addLicenseAgreementIfNeeded(dmgPath, dmgFormat);

			ora.text = 'Code signing DMG';
			let identity;
			const {stdout} = await execa('/usr/bin/security', ['find-identity', '-v', '-p', 'codesigning']);
			if (cli.flags.identity && stdout.includes(`"${cli.flags.identity}"`)) {
				identity = cli.flags.identity;
			} else if (!cli.flags.identity && stdout.includes('Developer ID Application:')) {
				identity = 'Developer ID Application';
			} else if (!cli.flags.identity && stdout.includes('Mac Developer:')) {
				identity = 'Mac Developer';
			} else if (!cli.flags.identity && stdout.includes('Apple Development:')) {
				identity = 'Apple Development';
			}

			if (!identity) {
				const error = new Error(); // eslint-disable-line unicorn/error-message
				error.stderr = 'No suitable code signing identity found';
				throw error;
			}

			try {
				await execa('/usr/bin/codesign', ['--sign', identity, dmgPath]);
			} catch (error) {
				ora.fail(`Code signing failed. The DMG is fine, just not code signed.\n${error.stderr?.trim() ?? error}`);
				process.exit(2);
			}

			const {stderr} = await execa('/usr/bin/codesign', [dmgPath, '--display', '--verbose=2']);

			const match = /^Authority=(.*)$/m.exec(stderr);
			if (!match) {
				ora.fail('Not code signed');
				process.exit(1);
			}

			ora.info(`Code signing identity: ${match[1]}`).start();
			ora.succeed(`Created ‚Äú${dmgFilename}‚Äù`);
		} catch (error) {
			ora.fail(`${error.stderr?.trim() ?? error}`);
			process.exit(2);
		}
	});

	ee.on('error', error => {
		ora.fail(`Building the DMG failed. ${error}`);
		process.exit(1);
	});
}

try {
	await init();
} catch (error) {
	ora.fail(error?.stack || error);
	process.exit(1);
}

import {Buffer} from 'node:buffer';
import fs from 'node:fs';
import {promisify} from 'node:util';
import path from 'node:path';
import {fileURLToPath} from 'node:url';
import {execa} from 'execa';
import {temporaryFile} from 'tempy';
import baseGm from 'gm';
import icns from 'icns-lib';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

const gm = baseGm.subClass({imageMagick: true});
const readFile = promisify(fs.readFile);
const writeFile = promisify(fs.writeFile);

const filterMap = (map, filterFunction) => Object.fromEntries(Object.entries(map).filter(element => filterFunction(element)).map(([key, item]) => [key, item]));

// Drive icon from `/System/Library/Extensions/IOStorageFamily.kext/Contents/Resources/Removable.icns``
const baseDiskIconPath = `${__dirname}/disk-icon.icns`;

const biggestPossibleIconType = 'ic10';

async function baseComposeIcon(type, appIcon, mountIcon, composedIcon) {
	mountIcon = gm(mountIcon);
	appIcon = gm(appIcon);

	const [appIconSize, mountIconSize] = await Promise.all([
		promisify(appIcon.size.bind(appIcon))(),
		promisify(appIcon.size.bind(mountIcon))(),
	]);

	// Change the perspective of the app icon to match the mount drive icon
	appIcon = appIcon.out('-matte').out('-virtual-pixel', 'transparent').out('-distort', 'Perspective', `1,1  ${appIconSize.width * 0.08},1     ${appIconSize.width},1  ${appIconSize.width * 0.92},1     1,${appIconSize.height}  1,${appIconSize.height}     ${appIconSize.width},${appIconSize.height}  ${appIconSize.width},${appIconSize.height}`);

	// Resize the app icon to fit it inside the mount icon, aspect ration should not be kept to create the perspective illution
	appIcon = appIcon.resize(mountIconSize.width / 1.58, mountIconSize.height / 1.82, '!');

	const temporaryAppIconPath = temporaryFile({extension: 'png'});
	await promisify(appIcon.write.bind(appIcon))(temporaryAppIconPath);

	// Compose the two icons
	const iconGravityFactor = mountIconSize.height * 0.063;
	mountIcon = mountIcon.composite(temporaryAppIconPath).gravity('Center').geometry(`+0-${iconGravityFactor}`);

	composedIcon[type] = await promisify(mountIcon.toBuffer.bind(mountIcon))();
}

const hasGm = async () => {
	try {
		await execa('gm', ['-version']);
		return true;
	} catch (error) {
		if (error.code === 'ENOENT') {
			return false;
		}

		throw error;
	}
};

export default async function composeIcon(appIconPath) {
	if (!await hasGm()) {
		return baseDiskIconPath;
	}

	const baseDiskIcons = filterMap(icns.parse(await readFile(baseDiskIconPath)), ([key]) => icns.isImageType(key));
	const appIcon = filterMap(icns.parse(await readFile(appIconPath)), ([key]) => icns.isImageType(key));

	const composedIcon = {};
	await Promise.all(Object.entries(appIcon).map(async ([type, icon]) => {
		if (baseDiskIcons[type]) {
			return baseComposeIcon(type, icon, baseDiskIcons[type], composedIcon);
		}

		console.warn('There is no base image for this type', type);
	}));

	if (!composedIcon[biggestPossibleIconType]) {
		// Make sure the highest-resolution variant is generated
		const largestAppIcon = Object.values(appIcon).sort((a, b) => Buffer.byteLength(b) - Buffer.byteLength(a))[0];
		await baseComposeIcon(biggestPossibleIconType, largestAppIcon, baseDiskIcons[biggestPossibleIconType], composedIcon);
	}

	const temporaryComposedIcon = temporaryFile({extension: 'icns'});

	await writeFile(temporaryComposedIcon, icns.format(composedIcon));

	return temporaryComposedIcon;
}

import {Buffer} from 'node:buffer';
import process from 'node:process';
import fs from 'node:fs';
import path from 'node:path';
import {fileURLToPath} from 'node:url';
import {execa} from 'execa';
import {temporaryFile} from 'tempy';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

function getRtfUnicodeEscapedString(text) {
	let result = '';
	for (let index = 0; index < text.length; index++) {
		if (text[index] === '\\' || text[index] === '{' || text[index] === '}' || text[index] === '\n') {
			result += `\\${text[index]}`;
		} else if (text[index] === '\r') {
			// ignore
		} else if (text.codePointAt(index) <= 0x7F) {
			result += text[index];
		} else {
			result += `\\u${text.codePointAt(index)}?`;
		}
	}

	return result;
}

function wrapInRtf(text) {
	return '\t$"7B5C 7274 6631 5C61 6E73 695C 616E 7369"\n'
		+ '\t$"6370 6731 3235 325C 636F 636F 6172 7466"\n'
		+ '\t$"3135 3034 5C63 6F63 6F61 7375 6272 7466"\n'
		+ '\t$"3833 300A 7B5C 666F 6E74 7462 6C5C 6630"\n'
		+ '\t$"5C66 7377 6973 735C 6663 6861 7273 6574"\n'
		+ '\t$"3020 4865 6C76 6574 6963 613B 7D0A 7B5C"\n'
		+ '\t$"636F 6C6F 7274 626C 3B5C 7265 6432 3535"\n'
		+ '\t$"5C67 7265 656E 3235 355C 626C 7565 3235"\n'
		+ '\t$"353B 7D0A 7B5C 2A5C 6578 7061 6E64 6564"\n'
		+ '\t$"636F 6C6F 7274 626C 3B3B 7D0A 5C70 6172"\n'
		+ '\t$"645C 7478 3536 305C 7478 3131 3230 5C74"\n'
		+ '\t$"7831 3638 305C 7478 3232 3430 5C74 7832"\n'
		+ '\t$"3830 305C 7478 3333 3630 5C74 7833 3932"\n'
		+ '\t$"305C 7478 3434 3830 5C74 7835 3034 305C"\n'
		+ '\t$"7478 3536 3030 5C74 7836 3136 305C 7478"\n'
		+ '\t$"616C 5C70 6172 7469 6768 7465 6E66 6163"\n'
		+ '\t$"746F 7230 0A0A 5C66 305C 6673 3234 205C"\n'
		+ `${serializeString('63663020' + Buffer.from(getRtfUnicodeEscapedString(text)).toString('hex').toUpperCase() + '7D')}`;
}

function serializeString(text) {
	return '\t$"' + text.match(/.{1,32}/g).map(x => x.match(/.{1,4}/g).join(' ')).join('"\n\t$"') + '"';
}

export default async function sla(dmgPath, dmgFormat) {
	// Valid SLA filenames
	const rawSlaFile = path.join(process.cwd(), 'sla.r');
	const rtfSlaFile = path.join(process.cwd(), 'license.rtf');
	const txtSlaFile = path.join(process.cwd(), 'license.txt');

	const hasRaw = fs.existsSync(rawSlaFile);
	const hasRtf = fs.existsSync(rtfSlaFile);
	const hasTxt = fs.existsSync(txtSlaFile);

	if (!hasRaw && !hasRtf && !hasTxt) {
		return;
	}

	const temporaryDmgPath = temporaryFile({extension: 'dmg'});

	// UDCO or UDRO format is required to be able to unflatten
	// Convert and unflatten DMG (original format will be restored at the end)
	await execa('/usr/bin/hdiutil', ['convert', '-format', 'UDCO', dmgPath, '-o', temporaryDmgPath]);
	await execa('/usr/bin/hdiutil', ['unflatten', temporaryDmgPath]);

	if (hasRaw) {
		// If user-defined sla.r file exists, add it to dmg with 'rez' utility
		await execa('/usr/bin/rez', ['-a', rawSlaFile, '-o', temporaryDmgPath]);
	} else {
		// Generate sla.r file from text/rtf file
		// Use base.r file as a starting point
		let data = fs.readFileSync(path.join(__dirname, 'base.r'), 'utf8');
		let plainText = '';

		// Generate RTF version and preserve plain text
		data += '\ndata \'RTF \' (5000, "English") {\n';

		if (hasRtf) {
			data += serializeString((fs.readFileSync(rtfSlaFile).toString('hex').toUpperCase()));
			({stdout: plainText} = await execa('/usr/bin/textutil', ['-convert', 'txt', '-stdout', rtfSlaFile]));
		} else {
			plainText = fs.readFileSync(txtSlaFile, 'utf8');
			data += wrapInRtf(plainText);
		}

		data += '\n};\n';

		// Generate plain text version
		// Used as an alternate for command-line deployments
		data += '\ndata \'TEXT\' (5000, "English") {\n';
		data += serializeString(Buffer.from(plainText, 'utf8').toString('hex').toUpperCase());
		data += '\n};\n';

		// Save sla.r file, add it to DMG with `rez` utility
		const temporarySlaFile = temporaryFile({extension: 'r'});
		fs.writeFileSync(temporarySlaFile, data, 'utf8');
		await execa('/usr/bin/rez', ['-a', temporarySlaFile, '-o', temporaryDmgPath]);
	}

	// Flatten and convert back to original dmgFormat
	await execa('/usr/bin/hdiutil', ['flatten', temporaryDmgPath]);
	await execa('/usr/bin/hdiutil', ['convert', '-format', dmgFormat, temporaryDmgPath, '-o', dmgPath, '-ov']);
}

import path from 'node:path';
import fs from 'node:fs';
import {fileURLToPath} from 'node:url';
import test from 'ava';
import {execa} from 'execa';
import {temporaryDirectory} from 'tempy';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

test('main', async t => {
	const cwd = temporaryDirectory();

	try {
		await execa(path.join(__dirname, 'cli.js'), ['--identity=0', path.join(__dirname, 'fixtures/Fixture.app')], {cwd});
	} catch (error) {
		// Silence code signing failure
		if (!error.message.includes('No suitable code signing')) {
			throw error;
		}
	}

	t.true(fs.existsSync(path.join(cwd, 'Fixture 0.0.1.dmg')));
});

test('binary plist', async t => {
	const cwd = temporaryDirectory();

	try {
		await execa(path.join(__dirname, 'cli.js'), ['--identity=0', path.join(__dirname, 'fixtures/Fixture-with-binary-plist.app')], {cwd});
	} catch (error) {
		// Silence code signing failure
		if (!error.message.includes('No suitable code signing')) {
			throw error;
		}
	}

	t.true(fs.existsSync(path.join(cwd, 'Fixture 0.0.1.dmg')));
});

test('app without icon', async t => {
	const cwd = temporaryDirectory();

	try {
		await execa(path.join(__dirname, 'cli.js'), ['--identity=0', path.join(__dirname, 'fixtures/Fixture-no-icon.app')], {cwd});
	} catch (error) {
		// Silence code signing failure
		if (!error.message.includes('No suitable code signing')) {
			throw error;
		}
	}

	t.true(fs.existsSync(path.join(cwd, 'Fixture 0.0.1.dmg')));
});
