:root {
  --monospace: Consolas, monospace;
  --serif: Georgia, serif;
  --sans-serif: Helvetica, Arial, sans-serif;
}

/* hide Google translate header */
body > .skiptranslate {
  display: none;
}

/* hide Google translate in-progress widget */
body > .skiptranslate + div {
  display: none;
}

/* make translated text wrapper non-blocking */
font {
  pointer-events: none;
}

input,
select,
button {
  font-size: 1em;
}

input,
select,
textarea {
  border: 0.5px solid #dbdfe6;
  border-radius: 0.5px;
  box-sizing: border-box;
}

select {
  height: 1.6em;
  border-top-color: #abadb3;
  padding: 0;
  text-indent: 0px;
}

input {
  border-top-color: #abadb3;
  padding: 2px;
  text-indent: 1px;
}

input:read-only {
  cursor: default;
}

input[type="radio"] {
  vertical-align: bottom;
  cursor: pointer;
}

textarea {
  padding: 3px;
  box-sizing: border-box;
  width: 100%;
}

iframe {
  border: 0;
  width: 100%;
}

#map {
  background-color: #000000;
  mask-mode: alpha;
  mask-clip: no-clip;
  fill-rule: evenodd;
  user-select: none;
}

#canvas {
  position: absolute;
  pointer-events: none;
}

#preview {
  position: absolute;
  bottom: 1em;
  left: 1em;
  cursor: pointer;
}

#pickerContainer {
  position: absolute;
  z-index: 100;
}

input,
button,
select,
a,
textarea {
  outline: none;
}

button,
select,
a {
  cursor: pointer;
}

.pointer {
  cursor: pointer !important;
}

#prec text {
  font-size: 32px;
  stroke: none;
  text-shadow: 1px 1px 1px #9daac9;
  user-select: none;
}

#population,
#cells,
#compass {
  fill: none;
}

#landmass {
  mask: url(#land);
  fill-rule: evenodd;
}

#lakes,
#coastline,
#armies,
#ice,
#emblems {
  cursor: pointer;
}

#temperature {
  font-family: var(--sans-serif);
  font-weight: 700;
  text-anchor: middle;
  dominant-baseline: central;
  text-shadow: 0px 0px 10px white;
  fill-rule: evenodd;
}

#oceanLayers,
#terrs {
  fill-rule: evenodd;
}

#coastline {
  fill: none;
  stroke-linejoin: round;
}

t,
#regions,
#cults,
#relig,
#biomes,
#provincesBody,
#terrs,
#tooltip,
#temperature,
#texture,
#landmass,
#vignette,
#fogging {
  pointer-events: none;
}

#armies text {
  pointer-events: none;
  user-select: none;
  stroke: none;
  fill: #fff;
  text-shadow: 0 0 4px #000;
  dominant-baseline: central;
  text-anchor: middle;
  font-family: var(--sans-serif);
  fill-opacity: 1;
}

#armies text.regimentIcon {
  font-size: 0.8em;
}

#statesHalo {
  fill: none;
  stroke-linecap: round;
  stroke-linejoin: round;
}

#statesBody,
#provincesBody,
#relig,
#biomes,
#cults {
  stroke-linejoin: round;
  fill-rule: evenodd;
}

#statesBody,
#provincesBody,
#relig,
#cults {
  mask: url(#land);
}

#borders {
  stroke-linejoin: round;
  fill: none;
}

#rivers {
  stroke: none;
  mask: url(#land);
  cursor: pointer;
  fill-rule: nonzero;
}

#anchors {
  pointer-events: none;
}

#terrain,
#burgIcons {
  cursor: pointer;
}

.strokes {
  stroke-width: 0.08px;
  width: 2px;
  stroke: #5c5c70;
  stroke-dasharray: 0.5, 0.7;
  stroke-linecap: round;
}

#routes {
  fill: none;
  cursor: pointer;
}

i.icon-lock {
  cursor: pointer;
}

#labelEditor div {
  display: inline-block;
}

#labels {
  text-anchor: middle;
  dominant-baseline: central;
  cursor: pointer;
}

.chartInfo {
  text-align: center;
  font-family: var(--sans-serif);
  font-style: italic;
  font-size: 12px;
}

#statesTree text,
#provincesTree text {
  pointer-events: none;
  user-select: none;
  stroke: none;
  font-size: 11px;
}

#statesTree circle {
  filter: url(#dropShadow05);
  stroke: #666666;
  stroke-width: 1;
}

#statesTree circle.selected,
#provincesTree .selected {
  stroke: #c13119;
  stroke-width: 2;
}

.regimentDragLine {
  marker-end: url(#end-arrow);
  stroke: #333333;
  stroke-dasharray: 5;
  stroke-dashoffset: 1000;
  animation: dash 80s linear backwards;
}

.arrow {
  marker-end: url(#end-arrow-small);
  stroke: #555;
  stroke-width: 0.5;
}

@keyframes dash {
  to {
    stroke-dashoffset: 0;
  }
}

#provinceLabels,
#burgLabels {
  dominant-baseline: alphabetic;
  text-anchor: middle;
}

#routeLength,
#coastlineArea {
  background-color: #eeeeee;
  border: 1px solid #a5a5a5;
  line-height: 1.3em;
  cursor: default;
}

#brushCircle {
  stroke: #373737;
  stroke-width: 1.5px;
  stroke-dasharray: 7;
  stroke-linecap: butt;
  fill: none;
}

text.drag {
  text-shadow: 0 0 1px red;
}

#dialogs {
  background-color: var(--bg-dialogs);
}

.draggable {
  cursor: move;
}

.ui-widget-header {
  border-bottom: 1px solid var(--dark-solid);
  background: var(--header);
  color: #ffffff;
  font-weight: bold;
}

button.ui-button:disabled {
  filter: brightness(0.95);
}

button.ui-button:disabled:hover {
  cursor: default;
}

.ui-dialog,
#optionsContainer {
  user-select: none;
}

#optionsTrigger {
  padding: 0.6em 0.45em;
}

@media (max-width: 600px) {
  #optionsTrigger {
    font-size: 2em;
    padding: 0;
    width: 1.3em;
    height: 1.6em;
    border: solid 1px #5e4fa2;
  }
}

#options {
  position: absolute;
  font-family: var(--monospace);
  border: solid 1px #5e4fa2;
  margin: 10px;
  padding-bottom: 0.3em;
  background: var(--bg-light);
}

#options input,
