// Custom require setup for ava tests
//
// Includes:
//
// * babel-register
// * .walt to strings
// * .ne to grammar functions

const pirates = require('pirates');
const nearley = require('nearley');
const compile = require('nearley/lib/compile');
const generate = require('./generator');
const nearleyGrammar = require('nearley/lib/nearley-language-bootstrapped');

require('babel-register')({
  ignore: [/\.walt/, /\.ne/, 'node_modules'],
});

// AVA has no plugins for loaders. Seems like it's recommended to use pre-compiled
// sources with Webpack. But with a require hook we can transform the walt sources
// to strings just fine.
const matcher = filename => filename.indexOf('.walt') > -1;

pirates.addHook(code => `module.exports = \`${code}\`;`, {
  exts: ['.walt'],
  matcher,
});

// Another one for nearly grammars, gotta love JS
pirates.addHook(
  (code, file) => {
    // Parse the grammar source into an AST
    const grammarParser = new nearley.Parser(nearleyGrammar);
    grammarParser.feed(code);
    const grammarAst = grammarParser.results[0];

    // Compile the AST into a set of rules
    const grammarInfoObject = compile(grammarAst, { file });
    // Generate JavaScript code from the rules
    const grammarJs = generate(grammarInfoObject, 'grammar');

    return grammarJs;
  },
  { exts: ['.ne'], matcher: filename => filename.indexOf('.ne') > -1 }
);

// Rollup plugins
import babel from 'rollup-plugin-babel';
import eslint from 'rollup-plugin-eslint';
import uglify from 'rollup-plugin-uglify';
import { minify } from 'uglify-es';
import resolve from 'rollup-plugin-node-resolve';
import commonjs from 'rollup-plugin-commonjs';
import replace from 'rollup-plugin-replace';
import string from 'rollup-plugin-string';
import flow from 'rollup-plugin-flow';
// custom nearley plugin (for functional builds)
import nearley from 'rollup-plugin-walt-grammar';

const PROD = process.env.NODE_ENV === 'production';

export default {
  plugins: [
    nearley(),
    eslint({
      exclude: ['**/*.ne']
    }),
    flow(),
    string({
      include: '**/*.walt',
    }),
    replace({
      'process.env.NODE_ENV': JSON.stringify(process.env.NODE_ENV),
    }),
    babel({
      babelrc: false,
      presets: [
        [
          'env',
          {
            modules: false,
            targets: {
              chrome: '60.0.0',
            },
          },
        ],
      ],
      plugins: [
        'external-helpers',
        'transform-object-rest-spread',
        'transform-node-env-inline',
        ['minify-dead-code-elimination', { 'keepFnArgs': true }]
      ],
    }),
    resolve({
      jail: __dirname,
      main: false,
    }),
    commonjs({
      namedExports: {
        'node_modules/walt-compiler/dist/walt.js': [
          'unstableCompileWalt',
          'fragment',
          'parser',
        ],
        'node_modules/wasm-types/index.js': [
          'i32',
          'i64',
          'f32',
          'f64',
          'anyfunc',
          'func',
          'block_type',
          'i8',
          'u8',
          'i16',
          'u16',
          'u32',
          'u64',
          'set',
          'get',
          'sizeof',
        ],
        'node_modules/walt-parser-tools/scope.js': [
          'enter',
          'exit',
          'current',
          'find',
          'add',
          'index',
          'namespace',
          'signature',
        ],
        'node_modules/walt-parser-tools/map-node.js': ['map', 'mapNode'],
        'node_modules/walt-syntax/dist/walt-syntax.js': [
          'tokens',
          'semantics',
          'builtinTypes',
          'statements',
          'i32',
          'f32',
          'i64',
          'f64',
        ],
        'node_modules/nearley/lib/nearley.js': ['Parser', 'Grammar'],
      },
    }),
    PROD && uglify({}, minify),
  ],
};

const generate = require('nearley/lib/generate');

function dedentFunc(func) {
  let lines = func.toString().split(/\n/);

  if (lines.length === 1) {
    return [lines[0].replace(/^\s+|\s+$/g, '')];
  }

  let indent = null;
  let tail = lines.slice(1);
  for (let i = 0; i < tail.length; i++) {
    let match = /^\s*/.exec(tail[i]);
    if (match && match[0].length !== tail[i].length) {
      if (indent === null || match[0].length < indent.length) {
        indent = match[0];
      }
    }
  }

  if (indent === null) {
    return lines;
  }

  return lines.map(function dedent(line) {
    if (line.slice(0, indent.length) === indent) {
      return line.slice(indent.length);
    }
    return line;
  });
}

function tabulateString(string, indent, options) {
  let lines;
  if (Array.isArray(string)) {
    lines = string;
  } else {
    lines = string.toString().split('\n');
  }

  options = options || {};
  const tabulated = lines
    .map(function addIndent(line, i) {
      let shouldIndent = true;

      if (i === 0 && !options.indentFirst) {
        shouldIndent = false;
      }

      if (shouldIndent) {
        return indent + line;
      }
      return line;
    })
    .join('\n');

  return tabulated;
}

function serializeSymbol(s) {
  if (s instanceof RegExp) {
    return s.toString();
  } else if (s.token) {
    return s.token;
  }
  return JSON.stringify(s);
}

function serializeRule(rule, builtinPostprocessors) {
  let ret = '{';
  ret += '"name": ' + JSON.stringify(rule.name);
  ret += ', "symbols": [' + rule.symbols.map(serializeSymbol).join(', ') + ']';
  if (rule.postprocess) {
    if (rule.postprocess.builtin) {
      rule.postprocess = builtinPostprocessors[rule.postprocess.builtin];
    }
    ret +=
      ', "postprocess": ' +
      tabulateString(dedentFunc(rule.postprocess), '        ', {
        indentFirst: false,
      });
  }
  ret += '}';
  return ret;
}

function serializeRules(rules, builtinPostprocessors) {
  return (
    '[\n    ' +
    rules
      .map(function(rule) {
        return serializeRule(rule, builtinPostprocessors);
      })
      .join(',\n    ') +
    '\n]'
  );
}

module.exports = function customGenerator(parser) {
  let output = `// Custom Walt Grammar Generator
function id(x) { return x[0]; }

module.exports = function() {
  ${parser.body.join('\n')}

  return {
    Lexer: ${parser.config.lexer},
    ParserRules: ${serializeRules(
      parser.rules,
      generate.javascript.builtinPostprocessors
    )},
    ParserStart: ${JSON.stringify(parser.start)}
  };
}
`;

  return output;
};

