export { default as Canvas } from './src/canvas/baseCanvas';
export { default as TreeCanvas } from './src/canvas/treeCanvas';
export { default as Edge } from './src/edge/baseEdge';
export { default as Endpoint } from './src/endpoint/baseEndpoint';
export { default as Group } from './src/group/baseGroup';
export { default as Node } from './src/node/baseNode';
export { default as TreeNode } from './src/node/treeNode';
export { default as Minimap } from './src/utils/minimap';
export { default as Tips } from './src/utils/toolTip';
export { default as Layout } from './src/utils/layout/layout';
export { default as Arrow } from './src/utils/arrow';

'use strict';

const _ = require('lodash');

import Canvas from './src/canvas/baseCanvas';
import TreeCanvas from './src/canvas/treeCanvas';
import Edge from './src/edge/baseEdge';
import Endpoint from './src/endpoint/baseEndpoint';
import Group from './src/group/baseGroup';
import Node from './src/node/baseNode';
import TreeNode from './src/node/treeNode';
import Minimap from './src/utils/minimap';
import Tips from './src/utils/toolTip';
import Layout from './src/utils/layout/layout';
import Arrow from './src/utils/arrow';
import './src/index.less';

let Butterfly = {
  Canvas,
  TreeCanvas,
  Edge,
  Endpoint,
  Group,
  Node,
  TreeNode,
  Minimap,
  Tips,
  Layout,
  Arrow
};

window.Butterfly = Butterfly;

module.exports = Butterfly;

import path from 'path';
import json from 'rollup-plugin-json';
import babel from 'rollup-plugin-babel';
import postcss from 'rollup-plugin-postcss';
import commonjs from 'rollup-plugin-commonjs';
import extensions from 'rollup-plugin-extensions';
import external from 'rollup-plugin-peer-deps-external';
import url from "rollup-plugin-url"

import pkg from './package.json';

const config = {
  presets: [
    '@babel/preset-react',
    '@babel/preset-env'
  ],
  plugins: [
    '@babel/plugin-proposal-class-properties'
  ]
};


const plugins = [
  extensions({
    extensions: ['.js'],
    resolveIndex: true,
  }),
  external(),
  babel(Object.assign({
    exclude: [
      'node_modules/**',
    ]
  }, config)),
  postcss({
    extract: true,
    modules: false,
    use: [
      [
        'less',
        {
          javascriptEnabled: true,
        }
      ]
    ]
  }),
  commonjs(),
  json(),
  url({
    limit: 100 * 1024, // inline files < 100k, copy files > 100k
    include: ["**/*.svg", "**/*.eot", "**/*.tff", "**/*.woff", "**/*.woff2"], // defaults to .svg, .png, .jpg and .gif files
    emitFiles: true // defaults to true
  })
];

const rollupCfg = [];

// all in one 构建
rollupCfg.push({
  input: path.join(__dirname, 'index.js'),
  output: [
    {
      file: pkg.pack,
      format: 'cjs',
      exports: 'named',
      sourcemap: true
    },
    {
      file: pkg.es,
      format: 'es',
      sourcemap: true
    }
  ],
  plugins
});

export default rollupCfg;

const _ = require('lodash');
const webpack = require('webpack');
const MiniCssExtractPlugin = require('mini-css-extract-plugin');

const common = {
  mode: 'production',
  devtool: 'cheap-module-source-map',
  performance: {
    hints: false
  },
  entry: './index.js',
  target: ['web', 'es5'],
  output: {
    path: __dirname,
    filename: 'dist/index.js',
    libraryTarget: 'umd',
    environment: {
      arrowFunction: false
    }
  },
  resolve: { 
    alias: {},
  },
  plugins: [
    new MiniCssExtractPlugin({
      filename: 'dist/index.css',
      chunkFilename: '[id].css'
    })
  ],
  module: {
    rules: [
      {
        test: /\.(js|jsx)$/,
        exclude: content => {
          if (/(node_modules|bower_components)/.test(content)) {
            if (/d3/.test(content) || /ml-matrix/.test(content)) {
              return false;
            }
            return true;
          }
          return false;
        },
        use: {
          loader: 'babel-loader',
          options: {
            presets: ['@babel/preset-env'], //'env'--babel7中的es7语法编译插件
            // plugins: ['transform-decorators-legacy', 'transform-class-properties', 'add-module-exports', 'transform-object-rest-spread'],
            plugins: ['transform-es2015-modules-commonjs', '@babel/plugin-proposal-object-rest-spread', '@babel/plugin-proposal-class-properties']
          }
        }
      },
      {
        test: /\.less$/,
        use: [
          MiniCssExtractPlugin.loader,
          {
            loader: "css-loader" // translates CSS into CommonJS
          },
          {
            loader: "less-loader" // compiles Less to CSS
          }
        ]
      },
      {
        test: /\.(woff|woff2|eot|ttf|otf|svg)$/,
        use: {
          loader: 'url-loader',
          options: {
            limit: 1024 * 200,
            name: '[name].[ext]',
            outputPath: '/dist/fonts/'
          }
        }
      }
    ]
  },
  optimization: {
    minimize: false
  }  
};

const main = _.cloneDeep(common);
const unpkg = _.cloneDeep(common);

/**
 * unpkg包需要满足条件：
 * 1. no sourcemap
 * 2. no mini-css-extract-plugin
 * 3. minimize: true
 */
unpkg.optimization.minimize = true;
delete unpkg.devtool;
unpkg.output.filename = 'unpkg/index.unpkg.js';
unpkg.plugins.pop();
unpkg.module.rules[1].use.shift();

module.exports = [
  main,
  unpkg
];
