import commonjs from 'rollup-plugin-commonjs';
import resolve from 'rollup-plugin-node-resolve';

import pkg from './package.json';

export default {
  input: 'src/index.js',
  output: [
    {
      file: pkg.main,
      format: 'cjs',
      sourcemap: true
    },
    {
      file: pkg.module,
      format: 'es',
      sourcemap: true
    }
  ],
  external: ['blessed', 'ws', 'react-devtools-core', 'react-reconciler'],
  plugins: [
    babel({
      exclude: 'node_modules/**'
    }),
    resolve(),
    commonjs({
      ignore: ['ws', 'react-devtools-core']
    })
  ]
};

require('@babel/register')({
  presets: [['@babel/preset-env'], ['@babel/preset-react']]
});
const argv = process.argv.slice(2);
const example = argv[0];
const examples = [
  'animation',
  'context',
  'dashboard',
  'demo',
  'dispatch',
  'effects',
  'form',
  'hooks',
  'neo-blessed',
  'progressbar',
  'remove',
  'teardown'
];

if (examples.indexOf(example) === -1) {
  console.warn(
    'Invalid example "%s" provided. Must be one of:\n  *',
    example,
    examples.join('\n  * ')
  );
  process.exit(0);
}

require('./examples/' + example);
